INSERT INTO `media` VALUES (1, 'default11', '', 'media_information', '[11]', 'admin', 'Giới thiệu về HTN', '', '', 'vn', '', '&lt;p&gt;\r\n	&lt;span style=&quot;color:#ffffff;&quot;&gt;&lt;strong&gt;Ho&amp;agrave;ng Tuấn Nguy&amp;ecirc;n (HTN)&lt;/strong&gt;&lt;/span&gt; chuy&amp;ecirc;n cung cấp giải ph&amp;aacute;p tổng thể cho hệ thống ph&amp;ograve;ng chiếu phim HD-3D chuy&amp;ecirc;n d&amp;ugrave;ng, cafe 3D, ph&amp;ograve;ng học 3D (tư vấn thiết kế x&amp;acirc;y dựng ph&amp;ograve;ng, ghế chuy&amp;ecirc;n d&amp;ugrave;ng xem phim, hệ thống ti&amp;ecirc;u &amp;acirc;m, t&amp;aacute;ch &amp;acirc;m, t&amp;aacute;n &amp;acirc;m, &amp;acirc;m thanh, m&amp;aacute;y chiếu, k&amp;iacute;nh 3D, đầu ph&amp;aacute;t HD, HDD ....), cho thu&amp;ecirc; thiết bị 3D để quảng c&amp;aacute;o,demo sản phẩm, sự kiện, thiết kế nội dung 3D.&lt;/p&gt;\r\n&lt;p&gt;\r\n	HTN được s&amp;aacute;ng lập bởi những th&amp;agrave;nh vi&amp;ecirc;n c&amp;oacute; chung đam m&amp;ecirc; phim ảnh v&amp;agrave; c&amp;ocirc;ng nghệ, đặc biệt c&amp;ocirc;ng nghệ phim 3D. Những h&amp;igrave;nh ảnh ho&amp;agrave;nh tr&amp;aacute;ng v&amp;agrave; sống động trong bộ phim 3D Avatar của đạo diễn lừng danh James Careron đ&amp;atilde; th&amp;ocirc;i th&amp;uacute;c họ tự m&amp;agrave;y m&amp;ograve; t&amp;igrave;m hiểu về c&amp;ocirc;ng nghệ l&amp;agrave;m v&amp;agrave; chiếu phim 3D. Sự đam m&amp;ecirc; đ&amp;atilde; đưa họ gặp nhau v&amp;agrave; c&amp;ugrave;ng th&amp;agrave;nh lập c&amp;ocirc;ng ty HTN để thỏa m&amp;atilde;n ước muốn lu&amp;ocirc;n được sống với 3D.&lt;/p&gt;\r\n&lt;p&gt;\r\n	HTN l&amp;agrave; nh&amp;agrave; ti&amp;ecirc;n phong trong việc đưa ứng dụng thương mại của c&amp;ocirc;ng nghệ phim 3D v&amp;agrave;o Việt Nam. Những ng&amp;agrave;y đầu th&amp;agrave;nh lập, HTN tư vấn v&amp;agrave; lắp đặt c&amp;aacute;c hệ thống chiếu phim 3D cho gia đ&amp;igrave;nh, cho mục đ&amp;iacute;ch thương mai, v&amp;agrave; cung cấp vật tư, thiết bị li&amp;ecirc;n quan đến l&amp;atilde;nh vực tr&amp;igrave;nh chiếu 3D. Sau hơn một năm hoạt động, đến nay HTN đ&amp;atilde; tư vấn lắp đặt cho gần 20 ph&amp;ograve;ng chiếu 3D ở c&amp;aacute;c tỉnh th&amp;agrave;nh cả nước.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Năm 2012 đ&amp;aacute;nh dấu bước ngoặt quan trọng khi HTN chuyển từ kinh doanh dựa tr&amp;ecirc;n đam m&amp;ecirc; c&amp;aacute; nh&amp;acirc;n sang m&amp;ocirc; h&amp;igrave;nh hoạt động chuy&amp;ecirc;n nghiệp. Ch&amp;uacute;ng t&amp;ocirc;i đ&amp;atilde; chuyển đến văn ph&amp;ograve;ng mới với ph&amp;ograve;ng chiếu phim 3D chuy&amp;ecirc;n nghiệp; tăng cường năng lực đội ngũ kỹ thuật vi&amp;ecirc;n lắp đặt v&amp;agrave; bảo dưỡng hệ thống; tăng cường năng lực tiếp thị - b&amp;aacute;n h&amp;agrave;ng; chuẩn h&amp;oacute;a quy tr&amp;igrave;nh tư vấn v&amp;agrave; dịch vụ kh&amp;aacute;ch h&amp;agrave;ng. B&amp;ecirc;n cạnh dịch vụ truyền thống l&amp;agrave; tư vấn-lắp đặt hệ thống chiếu phim 3D, HTN đang ph&amp;aacute;t triển c&amp;aacute;c nội dung 3D nhằm mang đến giải ph&amp;aacute;p trọn g&amp;oacute;i nhiều gi&amp;aacute; trị gia tăng hơn cho kh&amp;aacute;ch h&amp;agrave;ng.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Qu&amp;yacute; kh&amp;aacute;ch c&amp;oacute; thể đến xem ph&amp;ograve;ng chiếu Demo Home Theatre tại &lt;strong&gt;110 Hoa C&amp;uacute;c, Phường 07, Quận Ph&amp;uacute; Nhuận&lt;/strong&gt; (ngay c&amp;acirc;y xăng Phan X&amp;iacute;ch Long đi v&amp;agrave;o 20 m&amp;eacute;t).&lt;/p&gt;', 'gioi-thieu-htn', '', '', '', '', '', '', 0, 0, '', 0, '', '', '', 0, 0, 'active', '', '2012-10-11 15:36:45', 'admin', '2012-10-11 15:36:45');
INSERT INTO `media` VALUES (44, 'default8', '', 'media_information', '[8]', 'admin', 'Danh sách khách hàng đã lắp đặt 3D', '', '', 'vn', '', '&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;01.Cafe Champions : &lt;/strong&gt;102 Đăng Văn Ngữ , Đống Đa, H&amp;agrave; Nội. (Website)&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;02.Cafe G&amp;oacute;c Phố : &lt;/strong&gt;Cao ốc Nguyễn Ngọc Phương, Đường Huỳnh Mẫn Đạt, B&amp;igrave;nh Thạnh, HCM&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;03.Cafe Feeling Qui Nhơn :&lt;/strong&gt;34 L&amp;ecirc; Xu&amp;acirc;n Trữ, th&amp;agrave;nh phố Qui Nhơn (Website)&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;04.Ph&amp;ograve;ng chiếu 3D H&amp;agrave; Tĩnh &lt;/strong&gt;- C&amp;ocirc;ng ty cổ phần du lịch Thạch H&amp;agrave;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	C&amp;ocirc;ng vi&amp;ecirc;n L&amp;yacute; Tự Trọng - khối 10 - Thị Trấn Thạch H&amp;agrave;, huyện Thạch H&amp;agrave;, H&amp;agrave; Tĩnh&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;05.Cafe 3D Đ&amp;agrave; Nẵng&lt;/strong&gt; - 17 Đặng Tất - H&amp;ograve;a Kh&amp;aacute;nh - Li&amp;ecirc;n Chiểu - TP. Đ&amp;agrave; Nẵng (Website)&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;06.Ph&amp;ograve;ng chiếu 3D Nha Trang&lt;/strong&gt; - Tầng 4 Maximark Nha Trang - 60 Th&amp;aacute;i Nguy&amp;ecirc;n - TP.Nha Trang&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;07.Cafe 3D Rạch Gi&amp;aacute;&lt;/strong&gt; - 29 L&amp;yacute; Tự Trọng - Rạch Gi&amp;aacute; - Ki&amp;ecirc;n Giang (Website)&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;08.Cafe 3D Hương PH&amp;ugrave; Sa &lt;/strong&gt;- Đường Trưng Nữ Vương nối d&amp;agrave;i - Thị X&amp;atilde; Ch&amp;acirc;u Đốc - An Giang&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;09.Ph&amp;ograve;ng chiếu 3D Vĩnh Long&lt;/strong&gt; - 47 Đinh Ti&amp;ecirc;n Ho&amp;agrave;ng - Kh&amp;oacute;m 2 - Phường 8 - TP. Vĩnh Long&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;10.Cafe Lối Về S&amp;oacute;c Trăng&lt;/strong&gt; - 409 L&amp;ecirc; Hồng Phong Th&amp;agrave;nh Phố S&amp;oacute;c Trăng&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;11.Ph&amp;ograve;ng chiếu 3D Cam Ranh&lt;/strong&gt; - Maximark Cam Ranh - 89 H&amp;ugrave;ng Vương - TP. Cam Ranh&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;12.Cafe 3D Mộc &lt;/strong&gt;: Đường Bửu Long, Thủ Đức ( đối diện ĐH Lạc Hồng )&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;13.Cafe 3D&lt;/strong&gt; : 80 Tỉnh lộ 942 - Huyện Chợ Mới - An Giang&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;14.Cafe 3D BiCi :&lt;/strong&gt; 8/16A L&amp;yacute; Thường Kiệt - Phuờng Mỹ B&amp;igrave;nh, TP.Long Xuy&amp;ecirc;n, An GIang&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;15.Ph&amp;ograve;ng chiếu 3D Hải Dương :&lt;/strong&gt; &amp;nbsp;Km2+500 Nguyễn Lương Bằng, TP.Hải Dương (Đối diện trung cấp y Hải Dương) (Website)&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;16.Cafe 3D B&amp;igrave;nh Dương :&lt;/strong&gt; 14/8 Ấp T&amp;acirc;n Ho&amp;agrave;, X&amp;atilde; Đ&amp;ocirc;ng H&amp;ograve;a, Huyện Dĩ An, B&amp;igrave;nh Dương.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;17.Caf&amp;eacute; 3D Vũng T&amp;agrave;u &lt;/strong&gt;- 547 Trương C&amp;ocirc;ng Định, TP.Vũng T&amp;agrave;u&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;18. &amp;nbsp; &amp;nbsp;Si&amp;ecirc;u thị BMC H&amp;agrave; Tĩnh&lt;/strong&gt; - &amp;nbsp;2 Phan Đ&amp;igrave;nh Ph&amp;ugrave;ng, TP.H&amp;agrave; Tĩnh&lt;/p&gt;', 'danh-sach-khach-hang-da-lap-dat-he-thong-3d', '', '', '', '', '', '', 0, 0, '', 0, '', '', '', 0, 0, 'active', '', '2012-10-09 17:43:32', 'admin', '2012-10-09 17:43:32');
INSERT INTO `media` VALUES (45, 'default1349837924', '', 'media_news', '[8],', 'admin', 'Cafe 3D Sài Gòn', '', '', 'vn', '&lt;p&gt;\r\n	Chỉ với 20.000 đồng, bạn c&amp;oacute; thể thưởng thức một bộ phim với hiệu ứng như ở rạp tr&amp;ecirc;n m&amp;agrave;n h&amp;igrave;nh 150-300 inch. Phong tr&amp;agrave;o qu&amp;aacute;n kiểu n&amp;agrave;y đang rộ l&amp;ecirc;n ở TP HCM.&lt;/p&gt;', '&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Chỉ với 20.000 đồng, bạn c&amp;oacute; thể thưởng thức một bộ phim với hiệu ứng như ở rạp tr&amp;ecirc;n m&amp;agrave;n h&amp;igrave;nh 150-300 inch. Phong tr&amp;agrave;o qu&amp;aacute;n kiểu n&amp;agrave;y đang rộ l&amp;ecirc;n ở TP HCM.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Năm 2009, khi bộ phim 3D Avatar g&amp;acirc;y cơn sốt tr&amp;ecirc;n to&amp;agrave;n thế giới v&amp;agrave; cả Việt Nam, c&amp;aacute;c rạp chiếu phim đ&amp;atilde; bắt đầu tr&amp;igrave;nh chiếu c&amp;ocirc;ng nghệ n&amp;agrave;y. C&amp;ugrave;ng với xu hướng đ&amp;oacute;, những qu&amp;aacute;n c&amp;agrave; ph&amp;ecirc; chiếu phim tại S&amp;agrave;i G&amp;ograve;n cũng đầu tư để c&amp;oacute; thể phục vụ người xem.&amp;nbsp;&lt;/p&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Lựa chọn hầu hết của c&amp;aacute;c qu&amp;aacute;n l&amp;agrave; xem bằng c&amp;ocirc;ng nghệ k&amp;iacute;nh m&amp;agrave;u, gi&amp;aacute; th&amp;agrave;nh rẻ d&amp;ugrave; sử dụng m&amp;aacute;y chiếu l&amp;ecirc;n m&amp;agrave;n chiếu nhưng chất lượng 3D rất thấp n&amp;ecirc;n kh&amp;ocirc;ng được đ&amp;aacute;nh gi&amp;aacute; cao. V&amp;agrave;o năm 2010, tivi 3D c&amp;oacute; k&amp;iacute;nh v&amp;agrave; kh&amp;ocirc;ng k&amp;iacute;nh xuất hiện nhưng thưởng thức phim với k&amp;iacute;ch cỡ m&amp;agrave;n h&amp;igrave;nh nhỏ (tối đa chỉ khoảng 55 inch) kh&amp;ocirc;ng thu h&amp;uacute;t.&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Tuy nhi&amp;ecirc;n, thời gian gần đ&amp;acirc;y, khi một nh&amp;oacute;m của diễn đ&amp;agrave;n chơi HD, 3D tại TP HCM ứng dụng th&amp;agrave;nh c&amp;ocirc;ng việc t&amp;iacute;ch hợp để xem được c&amp;ocirc;ng nghệ 3D DLP với m&amp;agrave;n chiếu bạc &amp;quot;độ&amp;quot; gi&amp;aacute; rẻ, k&amp;iacute;nh v&amp;agrave; m&amp;aacute;y chiếu, th&amp;igrave; c&amp;agrave; ph&amp;ecirc; phim 3D lại rộ l&amp;ecirc;n.&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; height=&quot;auto&quot; src=&quot;images/autosize-800x800/upload/3d sai gon.jpg&quot; width=&quot;auto&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;em&gt;C&amp;agrave; ph&amp;ecirc; phim 3D đang thu h&amp;uacute;t kh&amp;aacute;ch ở S&amp;agrave;i G&amp;ograve;n. Ảnh: Ki&amp;ecirc;n Cường.&lt;/em&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	&amp;quot;Trước đ&amp;acirc;y, qu&amp;aacute;n từng đầu tư tivi 3D, 50 inch khoảng 40 triệu đồng với k&amp;iacute;nh 2,5 triệu đồng một c&amp;aacute;i, tất cả mất hết khoảng 60-70 triệu đồng nhưng giờ bỏ hết để d&amp;ugrave;ng m&amp;agrave;n chiếu&amp;quot;, b&amp;agrave; L&amp;ecirc; Mỹ H&amp;agrave;, chủ qu&amp;aacute;n Corner cuối đường Huỳnh Mẫn Đạt, quận B&amp;igrave;nh Thạnh, cho biết.&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Một m&amp;agrave;n chiếu &amp;quot;rởm&amp;quot; 150 inch chưa tr&amp;aacute;ng bạc gi&amp;aacute; 10 triệu, sau khi được nh&amp;oacute;m chơi HD tư vấn v&amp;agrave; d&amp;ugrave;ng sơn bạc sơn bằng robot, đ&amp;atilde; c&amp;oacute; gi&amp;aacute; hơn 1.000 USD nhưng cho chất lượng tương đương loại nhập xịn 10.000 USD. M&amp;aacute;y chiếu loại Optoma 67 th&amp;igrave; c&amp;oacute; gi&amp;aacute; khoảng 18-19 triệu, k&amp;egrave;m theo 20 k&amp;iacute;nh 3D ph&amp;acirc;n cực 130 USD mỗi c&amp;aacute;i. &amp;quot;Rạp&amp;quot; chiếu phim 3 chiều đ&amp;atilde; h&amp;igrave;nh th&amp;agrave;nh tại qu&amp;aacute;n c&amp;agrave; ph&amp;ecirc; n&amp;agrave;y như thế.&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Corner c&amp;agrave; ph&amp;ecirc; buổi tối cuối tuần ở S&amp;agrave;i G&amp;ograve;n đ&amp;ocirc;ng kh&amp;aacute;ch dần, trẻ em v&amp;agrave; người lớn đều c&amp;oacute;. Qu&amp;aacute;n chỉ mới vừa mở phục vụ phim 3D được gần 1 th&amp;aacute;ng nhưng những phản hồi về hiệu ứng rất t&amp;iacute;ch cực. &amp;quot;Kh&amp;ocirc;ng kh&amp;aacute;c g&amp;igrave; ở rạp, xem 3D ngồi c&amp;agrave;ng gần hiệu ứng c&amp;agrave;ng tốt m&amp;agrave; điều n&amp;agrave;y ở đ&amp;acirc;y l&amp;agrave; rất tốt&amp;quot;, anh Trung Nguy&amp;ecirc;n, nh&amp;agrave; ở quận 8, vừa xem vừa n&amp;oacute;i.&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Chủ qu&amp;aacute;n chia sẻ, &amp;yacute; tưởng n&amp;agrave;y đ&amp;atilde; được b&amp;agrave; ấp ủ c&amp;aacute;ch đ&amp;acirc;y rất l&amp;acirc;u khi phải mất tới hơn 100.000 đồng, c&amp;oacute; khi hơn, để v&amp;agrave;o xem phim 3D ở rạp. Trong khi đ&amp;oacute;, ở đ&amp;acirc;y, phụ thu 3D chỉ 20.000 đồng mỗi người, thậm ch&amp;iacute; kh&amp;ocirc;ng cần k&amp;ecirc;u nước (gi&amp;aacute; nước chừng 20.000 đồng một người) kh&amp;aacute;ch chỉ cần trả tiền phụ thu để xem phim.&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; height=&quot;auto&quot; src=&quot;images/autosize-800x800/upload/phim3D.jpg&quot; width=&quot;auto&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Đ&amp;aacute;nh gi&amp;aacute; về tr&amp;agrave;o lưu 3D tại Việt Nam, anh Vũ Phạm Anh Tuấn, th&amp;agrave;nh vi&amp;ecirc;n quản trị mạng thuộc một diễn đ&amp;agrave;n HD, 3D, cho biết khi 3D c&amp;oacute; mặt tr&amp;ecirc;n thế giới th&amp;igrave; chừng 1-2 th&amp;aacute;ng sau ở Việt Nam cũng đ&amp;atilde; c&amp;oacute;.&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Anh H&amp;ugrave;ng, một trong những người chơi HD c&amp;oacute; tiếng tại H&amp;agrave; Nội th&amp;igrave; khẳng định hiện tại ở đ&amp;acirc;y vẫn chưa c&amp;oacute; c&amp;agrave; ph&amp;ecirc; 3D như TP HCM. &amp;quot;H&amp;agrave; Nội thường chuộng tivi 3D 50-55 inch, nhưng theo t&amp;ocirc;i chuẩn xem bằng m&amp;agrave;n chiếu bạc l&amp;agrave; tốt nhất&amp;quot;, anh H&amp;ugrave;ng ph&amp;acirc;n t&amp;iacute;ch.&lt;/div&gt;', 'cafe-3d-sai-gon', '', '', '', '', '', '', 0, 0, 'upload/3Dlogo(1).jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 11:12:33', 'admin', '2012-10-10 11:12:33');
INSERT INTO `media` VALUES (27, 'default1349511188', '', 'media_faq', '[9]', 'admin', 'Lắp đặt phòng chiếu phim 3D', '', '', 'vn', '&lt;p&gt;\r\n	3D VietNam đ&amp;atilde; gửi anh b&amp;aacute;o gi&amp;aacute; qua mail&lt;/p&gt;', '&lt;p&gt;\r\n	M&amp;igrave;nh muốn tư vấn lắp đặt ph&amp;ograve;ng phim 3d.Diện t&amp;iacute;ch 4.5X9m2.Nhờ HTN gửi mail b&amp;aacute;o gi&amp;aacute; chọn g&amp;oacute;i gi&amp;uacute;p m&amp;igrave;nh nh&amp;eacute;.&lt;/p&gt;', 'lap-dat-phong-chieu-phim-3d', '', '', '', '', '', '', 0, 0, '', 0, '', '[9]', '', 0, 0, 'active', '', '2012-10-06 16:13:08', 'admin', '2012-10-06 16:13:08');
INSERT INTO `media` VALUES (28, 'default1349511672', '', 'media_faq', '[9]', 'admin', 'Báo giá phòng chiếu phim 3D', '', '', 'vn', '&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Trước ti&amp;ecirc;n, cảm ơn chị &amp;nbsp;đ&amp;atilde; quan t&amp;acirc;m về hệ thống 3D của C&amp;ocirc;ng Ty HTN.&lt;/p&gt;\r\n&lt;p&gt;\r\n	C&amp;ocirc;ng ty ch&amp;uacute;ng t&amp;ocirc;i đ&amp;atilde; gửi bảng b&amp;aacute;o gi&amp;aacute; c&amp;aacute;c thiết bị hệ thống 3D ph&amp;acirc;n cực cho chị &amp;nbsp;tham khảo.&amp;nbsp;&lt;/p&gt;', '&lt;p&gt;\r\n	Xin ch&amp;agrave;o! T&amp;ocirc;i đang chuẩn bị l&amp;agrave;m ph&amp;ograve;ng chiếu phim 3D diện t&amp;iacute;ch 5x8m. Xin cung cấp cho t&amp;ocirc;i gi&amp;aacute; c&amp;aacute;c khoản mục cũng như tổng gi&amp;aacute; tri đầu tư.gồm - M&amp;aacute;y chiếu - m&amp;agrave;n chiếu - loa - ghế chuy&amp;ecirc;n dụng - k&amp;iacute;nh&lt;/p&gt;', 'bao-gia-phong-chieu-phim-3d', '', '', '', '', '', '', 0, 0, '', 0, '', '[9]', '', 0, 0, 'active', '', '2012-10-06 16:21:12', 'admin', '2012-10-06 16:21:12');
INSERT INTO `media` VALUES (2, 'default12', '', 'media_information', '[12]', 'admin', 'Hệ thống 3D phân cực đạt chuẩn của HTN', '', '', 'vn', '', '&lt;table align=&quot;left&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; hspace=&quot;0&quot; style=&quot;width: 100%; &quot; vspace=&quot;0&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td align=&quot;left&quot; style=&quot;padding-top:0cm;padding-right:9.0pt;\r\n  padding-bottom:0cm;padding-left:9.0pt&quot; valign=&quot;top&quot;&gt;\r\n				&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom: 0.0001pt; text-align: justify; &quot;&gt;\r\n					&amp;nbsp;&lt;/p&gt;\r\n				&lt;div&gt;\r\n					Hệ thống 3D ph&amp;acirc;n cực ho&amp;agrave;n chỉnh ( phim 3D Bluray độ ph&amp;acirc;n giải FULL 3D 3840x1080, &amp;acirc;m thanh DTSHD Master 7.1, chức năng chuyển đổi 2D -&amp;gt; 3D cho nguồn phim HD khổng lồ )&lt;/div&gt;\r\n				&lt;div&gt;\r\n					Hiện nay, giải ph&amp;aacute;p ph&amp;ograve;ng chiếu 3D ph&amp;acirc;n cực đang l&amp;agrave; lựa chọn tối ưu cho ph&amp;ograve;ng caf&amp;ecirc; , rạp chiếu phim 3D từ 40 người trở l&amp;ecirc;n, do gi&amp;aacute; th&amp;agrave;nh k&amp;iacute;nh ph&amp;acirc;n cực rẻ hơn nhiều so với k&amp;iacute;nh shutter (trập động). Việc bảo quản k&amp;iacute;nh sẽ dễ d&amp;agrave;ng v&amp;agrave; chi ph&amp;iacute; đầu tư k&amp;iacute;nh thấp. Ngo&amp;agrave;i ra sử dụng nguồn phim 3D Bluray độ n&amp;eacute;t cao 3840x1080, cho chất lượng gấp 4 lần phim n&amp;eacute;n Side By Side đảm bảo hiệu ứng 3D cao nhất. Đặc biệt, hệ thống cho ph&amp;eacute;p chuyển phim &amp;nbsp;2D-&amp;gt;3D với c&amp;aacute;c nguồn phim HD khổng lồ trong l&amp;uacute;c phim 3D c&amp;ograve;n hạn chế.&lt;/div&gt;\r\n				&lt;p class=&quot;MsoNormal&quot; style=&quot;text-align: center; &quot;&gt;\r\n					&lt;span style=&quot;font-size:\r\n  10.0pt;font-family:&amp;quot;Verdana&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-fareast-font-family:&amp;quot;Times New Roman&amp;quot;;\r\n  mso-bidi-font-family:Arial;color:black;mso-themecolor:text1&quot;&gt;&amp;nbsp;&lt;img alt=&quot;&quot; height=&quot;278&quot; src=&quot;http://i75.photobucket.com/albums/i292/crazyboy1304/3Dphancuc01-1.jpg&quot; width=&quot;500&quot; /&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;\r\n				&lt;p class=&quot;MsoNormal&quot; style=&quot;text-align: justify; &quot;&gt;\r\n					&amp;nbsp;&lt;/p&gt;\r\n				&lt;p class=&quot;MsoNormal&quot; style=&quot;text-align: justify; &quot;&gt;\r\n					&amp;nbsp;&lt;/p&gt;\r\n				&lt;p class=&quot;MsoNormal&quot; style=&quot;text-align: justify; &quot;&gt;\r\n					&amp;nbsp;&lt;/p&gt;\r\n				&lt;div&gt;\r\n					&lt;strong&gt;Hệ thống 3D ph&amp;acirc;n cực ho&amp;agrave;n chỉnh gồm c&amp;aacute;c th&amp;agrave;nh phần sau:&lt;/strong&gt;&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&lt;u&gt;1. 2 m&amp;aacute;y chiếu DLP th&amp;ocirc;ng thường c&amp;ugrave;ng model&lt;/u&gt; ( lưu &amp;yacute; l&amp;agrave; m&amp;aacute;y chiếu LCD phải d&amp;ugrave;ng loại thấu k&amp;iacute;nh ph&amp;ugrave; hợp, những loại thấu k&amp;iacute;nh tr&amp;ecirc;n thị trường hiện nay chỉ d&amp;ugrave;ng cho m&amp;aacute;y chiếu DLP ). D&amp;ugrave;ng 2 m&amp;aacute;y Full HD 1080p cho chất lượng tốt nhất.&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&lt;u&gt;2. 2 thấu k&amp;iacute;nh ph&amp;acirc;n cực :&lt;/u&gt; gồm 2 loại l&amp;agrave; linear v&amp;agrave; circular, giải phải circular ti&amp;ecirc;n tiến hơn v&amp;agrave; được nhiều người lựa chọn. Ngo&amp;agrave;i ra lựa chọn chất liệu thấu k&amp;iacute;nh thủy tinh chịu nhiệt sẽ đảm bảo. Một số loại thấu k&amp;iacute;nh kh&amp;aacute;c xem phim l&amp;acirc;u sẽ bị n&amp;oacute;ng v&amp;agrave; bị biến dạng l&amp;agrave;m mất t&amp;iacute;nh chất, dẫn tới kh&amp;ocirc;ng c&amp;oacute; hiệu ứng 3D.&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&lt;u&gt;3. M&amp;agrave;n chiếu bạc chống khử cực đạt chuẩn :&lt;/u&gt; gồm loại m&amp;agrave;n sơn ( điện hoặc k&amp;eacute;o tay ) v&amp;agrave; m&amp;agrave;n bạc nguy&amp;ecirc;n tấm &amp;nbsp;( ch&amp;uacute; &amp;yacute; việc tự &amp;yacute; sơn bằng tay l&amp;ecirc;n thạch cao hoặc tường sẽ kh&amp;ocirc;ng đảm bảo t&amp;iacute;nh chống khử cực v&amp;agrave; g&amp;acirc;y ra hiện tượng b&amp;oacute;ng ma (ghostng) rất kh&amp;oacute; chịu khi xem phim 3D.&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&lt;u&gt;4. K&amp;iacute;nh ph&amp;acirc;n cực :&lt;/u&gt; gồm 2 loại linear v&amp;agrave; circular, t&amp;ugrave;y v&amp;agrave;o thấu k&amp;iacute;nh sẽ chọn loại ph&amp;ugrave; hợp.&lt;/div&gt;\r\n				&lt;div&gt;\r\n					5. Nguồn ph&amp;aacute;t phim 3D : gồm PC cấu h&amp;igrave;nh ph&amp;ugrave; hợp v&amp;agrave; nguồn phim 3D. Sử dụng phim 3D Bluray cho chất lượng gấp 4 lần phim n&amp;eacute;n Side By Side, &amp;acirc;m thanh DTS-HD Master v&amp;agrave; TrueHD. Chức năng chuyển phim 2D -&amp;gt; 3D khi sử dụng k&amp;egrave;m thiết bị.&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&amp;nbsp;&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&lt;strong&gt;Nguy&amp;ecirc;n l&amp;yacute; hoạt động của một hệ thống chiếu phim ph&amp;acirc;n cực:&lt;/strong&gt;&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&amp;nbsp;&lt;/div&gt;\r\n				&lt;div&gt;\r\n					Để tạo ra hiệu ứng 3D th&amp;igrave; trong c&amp;ugrave;ng một thời điểm, mắt tr&amp;aacute;i v&amp;agrave; phải của người xem phải nh&amp;igrave;n thấy 2 h&amp;igrave;nh ảnh kh&amp;aacute;c nhau.&lt;/div&gt;\r\n				&lt;div&gt;\r\n					Hỉnh ảnh từ 2 m&amp;aacute;y chiếu tr&amp;aacute;i v&amp;agrave; phải sẽ đi qua 2 thấu k&amp;iacute;nh - &amp;gt; từ đ&amp;oacute; sẽ mang t&amp;iacute;nh chất ph&amp;acirc;n cực -&amp;gt; m&amp;agrave;n bạc sẽ bảo đảm t&amp;iacute;nh ph&amp;acirc;n cực &amp;aacute;nh s&amp;aacute;ng của 2 m&amp;aacute;y chiếu kh&amp;ocirc;ng bi mất đi v&amp;agrave; phản xạ lại tới người xem. Người xem sẽ đeo k&amp;iacute;nh ph&amp;acirc;n cực, mỗi tấm lọc mỗi b&amp;ecirc;n tr&amp;ograve;ng k&amp;iacute;nh ph&amp;acirc;n cực sẽ tương ứng với thấu k&amp;iacute;nh v&amp;agrave; chỉ cho ph&amp;eacute;p h&amp;igrave;nh ảnh của m&amp;aacute;y chiếu tr&amp;aacute;i hoặc phải đi qua. Từ đ&amp;oacute; người xem sẽ thấy 2 h&amp;igrave;nh ảnh kh&amp;aacute;c nhau.&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&amp;nbsp;&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&lt;strong&gt;H&amp;igrave;nh ảnh một v&amp;agrave;i hệ thống chiếu phim ph&amp;acirc;n cực chuy&amp;ecirc;n nghiệp:&lt;/strong&gt;&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&amp;nbsp;&lt;/div&gt;\r\n				&lt;div&gt;\r\n					&amp;nbsp;&lt;/div&gt;\r\n				&lt;p class=&quot;MsoNormal&quot;&gt;\r\n					&lt;span style=&quot;font-size:10.0pt;font-family:&amp;quot;Verdana&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-fareast-font-family:\r\n  &amp;quot;Times New Roman&amp;quot;;mso-bidi-font-family:Arial;color:black;mso-themecolor:text1&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;\r\n				&lt;p class=&quot;MsoNormal&quot; style=&quot;text-align: center; &quot;&gt;\r\n					&lt;img alt=&quot;&quot; height=&quot;303&quot; src=&quot;http://i75.photobucket.com/albums/i292/crazyboy1304/3Dphancuc003.jpg&quot; width=&quot;500&quot; /&gt;&lt;/p&gt;\r\n				&lt;p align=&quot;center&quot; class=&quot;MsoNormal&quot; style=&quot;text-align: center; &quot;&gt;\r\n					&amp;nbsp;&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;', '', '', '', '', '', '', '', 0, 0, '', 0, '', '', '', 0, 0, 'active', '', '2012-10-09 16:41:25', 'admin', '2012-10-09 16:41:25');
INSERT INTO `media` VALUES (20, 'default1349505944', '', 'media_faq', '[9]', 'admin', 'Tôi Muốn mở 1 phong xem phim 3d cafe', '', '', 'vn', '', '&lt;p&gt;\r\n	Hiện tại t&amp;ocirc;i muốn mở 1 ph&amp;ograve;ng xem phim 3D, di&amp;ecirc;n t&amp;iacute;ch khoảng 50 m&amp;eacute;t vu&amp;ocirc;ng . xin cho hỏi chi ph&amp;iacute; l&amp;agrave; bao nhi&amp;ecirc;u . Xin ch&amp;acirc;n th&amp;agrave;nh cảm ơn&lt;/p&gt;', 'toi-muon-mo-1-phong-xem-phim-3d-cafe', '', '', '', '', '', '', 0, 0, '', 0, '', '[9]', '', 0, 0, 'active', '', '2012-10-06 14:45:44', 'admin', '2012-10-06 14:45:44');
INSERT INTO `media` VALUES (21, 'default1349506303', '', 'media_faq', '[9]', 'admin', 'Báo giá lắp đặt phòng xem phim 3D', '', '', 'vn', '', '&lt;p&gt;\r\n	HIện t&amp;ocirc;i đang dự định mở ph&amp;ograve;ng chiếu phim 3D với sức chứa 40-50 người, t&amp;ocirc;i kh&amp;ocirc;ng biết phải cần phải mua thiết bị như thế n&amp;agrave;o. Mong qu&amp;yacute; c&amp;ocirc;ng ty tư vấn v&amp;agrave; b&amp;aacute;o gi&amp;aacute; gi&amp;uacute;p t&amp;ocirc;i. Xin ch&amp;acirc;n th&amp;agrave;nh cảm ơn&lt;/p&gt;', 'bao-gia-lap-dat-phong-xem-phim-3d', '', '', '', '', '', '', 0, 0, '', 0, '', '[9]', '', 0, 0, 'active', '', '2012-10-06 14:51:43', 'admin', '2012-10-06 14:51:43');
