INSERT INTO `media` VALUES (24, 'default1349510752', '', 'media_faq', '[9]', 'admin', 'Hệ thống chiếu phim 3D coffee', '', '', 'vn', '&lt;p&gt;\r\n	Đ&amp;atilde; gửi b&amp;aacute;o gi&amp;aacute;&lt;/p&gt;', '&lt;p&gt;\r\n	T&amp;ocirc;i đang c&amp;oacute; nhu cầu đầu tư cho hệ thống chiếu Film Coffe với quy m&amp;ocirc; từ 70-&amp;gt; 100 ghế, t&amp;ocirc;i cần Qu&amp;yacute; Cty hỗ trợ đầy đủ về gi&amp;aacute; cả c&amp;aacute;c thiết bị cho hệ thống chiếu Film 3D. Xin c&amp;aacute;m ơn!&lt;/p&gt;', 'he-thong-chieu-phim-3d-coffee', '', '', '', '', '', '', 0, 0, '', 0, '', '[9]', '', 0, 0, 'active', '', '2012-10-06 16:05:52', 'admin', '2012-10-06 16:05:52');
INSERT INTO `media` VALUES (25, 'default1349510909', '', 'media_faq', '[9]', 'admin', 'Hỏi về tivi Plasma và ti vi Led', '', '', 'vn', '&lt;p&gt;\r\n	Đ&amp;atilde; gọi điện tư vấn trực tiếp&lt;/p&gt;', '&lt;p&gt;\r\n	qu&amp;yacute; c&amp;ocirc;ng ty cho t&amp;ocirc;i hỏi tivi platm v&amp;agrave; led c&amp;ugrave;ng 43 in ti&amp;ecirc;u thụ điện thế n&amp;agrave;o? loại n&amp;agrave;o ti&amp;ecirc;u thụ điện nhiều hơn? Mỗi loại c&amp;oacute; ưu, khuyết điểm g&amp;igrave;? để t&amp;ocirc;i mua. Xin cảm ơn.&lt;/p&gt;', 'hoi-ve-tivi-plasma-va-ti-vi-led', '', '', '', '', '', '', 0, 0, '', 0, '', '[9]', '', 0, 0, 'active', '', '2012-10-06 16:08:29', 'admin', '2012-10-06 16:08:29');
INSERT INTO `media` VALUES (26, 'default1349511078', '', 'media_faq', '[9]', 'admin', 'Hệ thống chiếu phim 3D cho quán cafe', '', '', 'vn', '&lt;p&gt;\r\n	Đ&amp;atilde; gọi điện tư vấn trực tiếp&lt;/p&gt;', '&lt;p&gt;\r\n	T&amp;ocirc;i đang ở H&amp;agrave; Nội t&amp;ocirc;i hiện chưa c&amp;oacute; rạp hiện nay qu&amp;ecirc; t&amp;ocirc;i chiếu phim hay qu&amp;aacute;n coffee 3d n&amp;agrave;o n&amp;ecirc;n T&amp;ocirc;i đang c&amp;oacute; dự định mở 1 quan coffee 3d quy m&amp;ocirc; nhỏ với khoảng 50 ghế.t&amp;ocirc;i đ&amp;atilde; được c&amp;ocirc;ng ty tư vấn kh&amp;aacute; r&amp;otilde; về m&amp;ocirc; h&amp;igrave;nh cũng như c&amp;ocirc;ng nghệ,thiết bị.Nhưng t&amp;ocirc;i đang ph&amp;acirc;n v&amp;acirc;n ko biết phương ph&amp;aacute;p kinh doanh thế n&amp;agrave;o cho hiệu quả,với quy m&amp;ocirc; như vậy th&amp;igrave; tri ph&amp;iacute; hoạt động,doanh thu h&amp;agrave;ng th&amp;aacute;ng khoảng bao nhi&amp;ecirc;u?Nếu qu&amp;yacute; c&amp;ocirc;ng ty c&amp;oacute; kinh nghiệm c&amp;oacute; thể chia sẻ cho t&amp;ocirc;i biết th&amp;igrave; t&amp;ocirc;i cảm ơn rất nhiều&lt;/p&gt;', 'he-thong-chieu-phim-3d-cho-quan-cafe', '', '', '', '', '', '', 0, 0, '', 0, '', '[9]', '', 0, 0, 'active', '', '2012-10-06 16:11:18', 'admin', '2012-10-06 16:11:18');
INSERT INTO `media` VALUES (3, 'default14', '', 'media_information', '[14]', 'admin', 'Giải pháp phòng chiếu 3D DLP', '', '', 'vn', '', '&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;Rạp phim 3D tại nh&amp;agrave;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Nay đ&amp;atilde; c&amp;oacute; thể xem phim 3D tại nh&amp;agrave;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Bạn ghiền phim.&amp;nbsp; Bạn th&amp;iacute;ch phim 3D v&amp;agrave; muốn h&amp;ograve;a m&amp;igrave;nh v&amp;agrave;o thế giới của bộ phim thay v&amp;igrave; chỉ theo d&amp;otilde;i c&amp;acirc;u chuyện tr&amp;ecirc;n phim 2D. &amp;nbsp;Nhưng kh&amp;ocirc;ng phải l&amp;uacute;c n&amp;agrave;o bạn cũng c&amp;oacute; thời gian để đến rạp xem phim 3D.&amp;nbsp; Bạn kh&amp;ocirc;ng thể xem lại nhiều bộ phim 3D y&amp;ecirc;u th&amp;iacute;ch v&amp;igrave; c&amp;aacute;c rạp đ&amp;atilde; kh&amp;ocirc;ng c&amp;ograve;n chiếu ch&amp;uacute;ng nữa.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Kh&amp;ocirc;ng c&amp;ograve;n l&amp;agrave; &amp;ldquo;mơ ước&amp;rdquo; nữa, hệ thống xem phim 3D tại nh&amp;agrave; giờ trở n&amp;ecirc;n trong tầm tay với chi ph&amp;iacute; hợp l&amp;yacute; v&amp;agrave; thời gian lắp đặt chỉ trong v&amp;ograve;ng 2-3 ng&amp;agrave;y.&lt;/p&gt;\r\n&lt;p&gt;\r\n	H&amp;atilde;y li&amp;ecirc;n hệ ngay với HTN - nh&amp;agrave; ti&amp;ecirc;n phong giải ph&amp;aacute;p chiếu phim 3D ngo&amp;agrave;i rạp ở Việt Nam - để tham gia trải nghiệm c&amp;aacute;c xuất chiếu 3D demo v&amp;agrave; được tư vấn về giải ph&amp;aacute;p cho một hệ thống tương tự tại nh&amp;agrave; bạn.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Giải ph&amp;aacute;p ph&amp;ograve;ng chiếu 3D tại nh&amp;agrave; của ch&amp;uacute;ng t&amp;ocirc;i đem đến cho bạn:&lt;/p&gt;\r\n&lt;p&gt;\r\n	-Khả năng tr&amp;igrave;nh chiếu phim 3D với m&amp;agrave;n ảnh từ 100 đến 300 inches&lt;/p&gt;\r\n&lt;p&gt;\r\n	- cảm nhận v&amp;agrave; h&amp;ograve;a m&amp;igrave;nh v&amp;agrave;o thế giới của bộ phim, chứ kh&amp;ocirc;ng chỉ l&amp;agrave; người xem thụ động&lt;/p&gt;\r\n&lt;p&gt;\r\n	-Hiệu ứng h&amp;igrave;nh ảnh v&amp;agrave; &amp;acirc;m thanh kh&amp;ocirc;ng thua k&amp;eacute;m ở rạp&lt;/p&gt;\r\n&lt;p&gt;\r\n	-Chơi game 3D khi kết nối với thiết bị game&lt;/p&gt;\r\n&lt;p&gt;\r\n	-Xem phim 2D th&amp;ocirc;ng thường&lt;/p&gt;\r\n&lt;p&gt;\r\n	- Kết nối v&amp;agrave; sử dụng đơn giản&lt;/p&gt;', 'giai-phap-phong-chieu-3d-dlp', '', '', '', '', '', '', 0, 0, '', 0, '', '', '', 0, 0, 'active', '', '2012-10-11 15:14:51', 'admin', '2012-10-11 15:14:51');
INSERT INTO `media` VALUES (59, 'default1349859148', '', 'media_news', '[6],', 'admin', '&quot;Long môn phi giáp&quot;: Những bí mật thú vị', '', '', 'vn', '&lt;p&gt;\r\n	Từ nguồn gốc t&amp;ecirc;n phim cho tới lai lịch nh&amp;acirc;n vật, xuất xứ bối cảnh&amp;hellip; đều sẽ khiến kh&amp;aacute;n giả bất ngờ.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Sau khi thực hiện &amp;ldquo;Long m&amp;ocirc;n kh&amp;aacute;ch sạn&amp;rdquo; năm 1967, đạo diễn Hồ Kim Thuy&amp;ecirc;n đ&amp;atilde; được kh&amp;aacute;n giả ngưỡng mộ đặt cho danh hiệu &amp;ldquo;chuy&amp;ecirc;n gia lịch sử&amp;rdquo;. Năm 2010, khi bắt tay sản xuất bom tấn 3D &amp;ldquo;Long m&amp;ocirc;n phi gi&amp;aacute;p&amp;rdquo;, Từ Khắc cũng kh&amp;ocirc;ng d&amp;aacute;m lơ l&amp;agrave; c&amp;ocirc;ng t&amp;aacute;c chuẩn bị...&lt;/p&gt;', '&lt;p&gt;\r\n	Từ nguồn gốc t&amp;ecirc;n phim cho tới lai lịch nh&amp;acirc;n vật, xuất xứ bối cảnh&amp;hellip; đều sẽ khiến kh&amp;aacute;n giả bất ngờ.&lt;/p&gt;\r\n&lt;div&gt;\r\n	&lt;p&gt;\r\n		Sau khi thực hiện &amp;ldquo;Long m&amp;ocirc;n kh&amp;aacute;ch sạn&amp;rdquo; năm 1967, đạo diễn Hồ Kim Thuy&amp;ecirc;n đ&amp;atilde; được kh&amp;aacute;n giả ngưỡng mộ đặt cho danh hiệu &amp;ldquo;chuy&amp;ecirc;n gia lịch sử&amp;rdquo;. Năm 2010, khi bắt tay sản xuất bom tấn 3D &amp;ldquo;Long m&amp;ocirc;n phi gi&amp;aacute;p&amp;rdquo;, Từ Khắc cũng kh&amp;ocirc;ng d&amp;aacute;m lơ l&amp;agrave; c&amp;ocirc;ng t&amp;aacute;c chuẩn bị. Ngo&amp;agrave;i bối cảnh, tạo h&amp;igrave;nh v&amp;agrave; đạo cụ được nghi&amp;ecirc;n cứu kỹ c&amp;agrave;ng, nội dung phim cũng ẩn chứa kh&amp;aacute; nhiều chi tiết c&amp;oacute; quan hệ chặt chẽ với lịch sử. Dưới đ&amp;acirc;y l&amp;agrave; 4 trong số những đặc điểm th&amp;uacute; vị c&amp;oacute; thể &amp;ldquo;điểm mặt kể t&amp;ecirc;n&amp;rdquo; bằng dẫn chứng r&amp;otilde; r&amp;agrave;ng.&lt;/p&gt;\r\n	&lt;p&gt;\r\n		B&amp;iacute; mật t&amp;ecirc;n phim: V&amp;igrave; sao l&amp;agrave; &amp;ldquo;Long m&amp;ocirc;n phi gi&amp;aacute;p&amp;rdquo;?&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		&lt;img alt=&quot;&amp;quot;Long môn phi giáp&amp;quot;: Những bí mật thú vị, Phim, Long mon phi giap,ly lien kiet,chau tan,tran khon,truong hinh du,dien vien trung quoc,tin tuc,phim vo thuat,phim 3D&quot; src=&quot;http://media.tinmoi.vn//2011/12/24/3_26_1324660282_9_1324544919-long-mon-phi-giap--7-.jpg&quot; /&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		Đo&amp;agrave;n l&amp;agrave;m phim Long m&amp;ocirc;n phi gi&amp;aacute;p nh&amp;iacute; nhảnh tr&amp;ecirc;n trường quay ngoại cảnh&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Cụm từ &amp;ldquo;Long m&amp;ocirc;n&amp;rdquo; trong &amp;ldquo;Long m&amp;ocirc;n phi gi&amp;aacute;p&amp;rdquo; kh&amp;ocirc;ng hề vay mượn từ t&amp;ecirc;n phim &amp;ldquo;Long m&amp;ocirc;n kh&amp;aacute;ch sạn&amp;rdquo; như dư luận vẫn đồn đại. Tr&amp;ecirc;n thực tế n&amp;oacute; bắt nguồn từ hai tấm bia đ&amp;aacute; cổ m&amp;agrave; đo&amp;agrave;n l&amp;agrave;m phim v&amp;ocirc; t&amp;igrave;nh ph&amp;aacute;t hiện được khi tới Hồ Bắc chọn ngoại cảnh. Theo giới thiệu, tr&amp;ecirc;n mặt tấm bia đ&amp;aacute; ni&amp;ecirc;n đại cổ xưa n&amp;agrave;y, tất cả đường n&amp;eacute;t đều rất mờ nhạt; ri&amp;ecirc;ng chỉ c&amp;oacute; một v&amp;agrave;i chữ khắc rải r&amp;aacute;c &amp;ldquo;Long&amp;rdquo;, &amp;ldquo;M&amp;ocirc;n&amp;rdquo;, &amp;ldquo;Phi&amp;rdquo;, &amp;ldquo;Gi&amp;aacute;p&amp;rdquo; l&amp;agrave; tr&amp;ocirc;ng thấy r&amp;otilde; hơn cả. Đạo diễn tỏ ra rất t&amp;acirc;m đắc v&amp;agrave; quả quyết d&amp;ugrave;ng cụm từ n&amp;agrave;y để đặt t&amp;ecirc;n ch&amp;iacute;nh thức cho phim mới.&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Sau đ&amp;oacute;, Từ Khắc c&amp;ograve;n vận dụng chi tiết tấm bia cổ để l&amp;agrave;m đầu mối quan trọng cho qu&amp;aacute; tr&amp;igrave;nh truy t&amp;igrave;m t&amp;ecirc;n th&amp;aacute;i gi&amp;aacute;m gian &amp;aacute;c. &amp;ldquo;Long m&amp;ocirc;n phi gi&amp;aacute;p&amp;rdquo; kh&amp;ocirc;ng chỉ đơn giản l&amp;agrave; t&amp;ecirc;n phim m&amp;agrave; n&amp;oacute; c&amp;ograve;n được đặt ẩn trong tứ thơ:&amp;nbsp; Long thần hiến hải sa, long m&amp;ocirc;n phi gi&amp;aacute;p tới&amp;rdquo;. Về hiệu quả cụ thể của mấu chốt n&amp;agrave;y, đơn vị sản xuất để d&amp;agrave;nh cho kh&amp;aacute;n giả kh&amp;aacute;m ph&amp;aacute; sau khi bộ phim ch&amp;iacute;nh thức c&amp;ocirc;ng chiếu.&lt;/p&gt;\r\n	&lt;p&gt;\r\n		B&amp;iacute; mật nh&amp;acirc;n vật: Vạn qu&amp;yacute; phi v&amp;agrave; Triệu Ho&amp;agrave;i An&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		&lt;img alt=&quot;&amp;quot;Long môn phi giáp&amp;quot;: Những bí mật thú vị, Phim, Long mon phi giap,ly lien kiet,chau tan,tran khon,truong hinh du,dien vien trung quoc,tin tuc,phim vo thuat,phim 3D&quot; src=&quot;http://media.tinmoi.vn//2011/12/24/3_26_1324660287_07_1324545076-long-mon-phi-giap--20-.jpg&quot; /&gt;&amp;nbsp;&lt;img alt=&quot;&amp;quot;Long môn phi giáp&amp;quot;: Những bí mật thú vị, Phim, Long mon phi giap,ly lien kiet,chau tan,tran khon,truong hinh du,dien vien trung quoc,tin tuc,phim vo thuat,phim 3D&quot; src=&quot;http://media.tinmoi.vn//2011/12/24/3_26_1324660288_14_1324545076-long-mon-phi-giap--18-.jpg&quot; /&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		Trương Hinh Dư v&amp;agrave; mi&amp;ecirc;u tả nh&amp;acirc;n vật Vạn qu&amp;yacute; phi thời nh&amp;agrave; Minh&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Mặc d&amp;ugrave; kh&amp;ocirc;ng sử dụng tối đa chi tiết lịch sử như đạo diễn Hồ Kim Thuy&amp;ecirc;n trong &amp;ldquo;Long m&amp;ocirc;n kh&amp;aacute;ch sạn&amp;rdquo; nhưng Từ Khắc vẫn rất chu đ&amp;aacute;o khi nghi&amp;ecirc;n cứu x&amp;acirc;y dựng nh&amp;acirc;n vật. V&amp;iacute; dụ điển h&amp;igrave;nh nhất l&amp;agrave; vai diễn Vạn qu&amp;yacute; phi của Trương Hinh Dư l&amp;agrave; &amp;ldquo;bản sao&amp;rdquo; của nh&amp;acirc;n vật c&amp;ugrave;ng t&amp;ecirc;n thời nh&amp;agrave; Minh. Từ lai lịch xuất th&amp;acirc;n cho tới qu&amp;aacute; tr&amp;igrave;nh nhập cung v&amp;agrave; đến khi băng h&amp;agrave; đều được khắc họa rất tỉ mỉ.&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		&lt;img alt=&quot;&amp;quot;Long môn phi giáp&amp;quot;: Những bí mật thú vị, Phim, Long mon phi giap,ly lien kiet,chau tan,tran khon,truong hinh du,dien vien trung quoc,tin tuc,phim vo thuat,phim 3D&quot; src=&quot;http://media.tinmoi.vn//2011/12/24/3_26_1324660288_99_1324545076-long-mon-phi-giap--22-.jpg&quot; /&gt;&amp;nbsp;&lt;img alt=&quot;&amp;quot;Long môn phi giáp&amp;quot;: Những bí mật thú vị, Phim, Long mon phi giap,ly lien kiet,chau tan,tran khon,truong hinh du,dien vien trung quoc,tin tuc,phim vo thuat,phim 3D&quot; src=&quot;http://media.tinmoi.vn//2011/12/24/3_26_1324660290_49_1324545076-long-mon-phi-giap--19-.jpg&quot; /&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		L&amp;yacute; Li&amp;ecirc;n Kiệt v&amp;agrave; th&amp;aacute;i gi&amp;aacute;m Ho&amp;agrave;i &amp;Acirc;n nổi tiếng trong lịch sử Trung Quốc&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Ngo&amp;agrave;i ra, nh&amp;acirc;n vật Triệu Ho&amp;agrave;i An do L&amp;yacute; Li&amp;ecirc;n Kiệt thủ vai l&amp;agrave; một v&amp;otilde; l&amp;acirc;m hảo h&amp;aacute;n được &amp;ldquo;sao ch&amp;eacute;p&amp;rdquo; từ đại th&amp;aacute;i gi&amp;aacute;m Ho&amp;agrave;i &amp;Acirc;n nổi tiếng. Cả cuộc đời của &amp;ocirc;ng thanh li&amp;ecirc;m, ngay thẳng.&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Trong khi đ&amp;oacute;, nh&amp;acirc;n vật m&amp;agrave; Phạm Hiểu Tuy&amp;ecirc;n thủ vai trong lịch sử ch&amp;iacute;nh l&amp;agrave; Kỷ c&amp;ocirc; nương &amp;ndash; con g&amp;aacute;i của sỹ quan Quảng T&amp;acirc;y. Sau khi nhập cung nhận chức quản kho, c&amp;ocirc; đ&amp;atilde; may mắn được ho&amp;agrave;ng thượng sủng &amp;aacute;i v&amp;agrave; mang thai rồng. Sau khi sinh nở, Kỷ c&amp;ocirc; nương được phong l&amp;agrave;m Kỷ phi, con trai trở th&amp;agrave;nh th&amp;aacute;i tử. Tuy nhi&amp;ecirc;n sau đ&amp;oacute; 1 th&amp;aacute;ng, c&amp;ocirc; đ&amp;atilde; qua đời b&amp;iacute; ẩn v&amp;agrave; người đời tương truyền rằng ch&amp;iacute;nh Vạn qu&amp;yacute; phi đ&amp;atilde; ra tay h&amp;atilde;m hại.&lt;/p&gt;\r\n	&lt;p&gt;\r\n		B&amp;iacute; mật địa danh: Mặc thủy th&amp;agrave;nh ở đ&amp;acirc;u?&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		&lt;img alt=&quot;&amp;quot;Long môn phi giáp&amp;quot;: Những bí mật thú vị, Phim, Long mon phi giap,ly lien kiet,chau tan,tran khon,truong hinh du,dien vien trung quoc,tin tuc,phim vo thuat,phim 3D&quot; src=&quot;http://media.tinmoi.vn//2011/12/24/3_26_1324660291_34_1324545242-long-mon-phi-giap--14-.jpg&quot; /&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		Địa danh ở Nội M&amp;ocirc;ng Cổ...&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		&lt;img alt=&quot;&amp;quot;Long môn phi giáp&amp;quot;: Những bí mật thú vị, Phim, Long mon phi giap,ly lien kiet,chau tan,tran khon,truong hinh du,dien vien trung quoc,tin tuc,phim vo thuat,phim 3D&quot; src=&quot;http://media.tinmoi.vn//2011/12/24/3_26_1324660293_7_1324545242-long-mon-phi-giap--9-.jpg&quot; /&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		&lt;img alt=&quot;&amp;quot;Long môn phi giáp&amp;quot;: Những bí mật thú vị, Phim, Long mon phi giap,ly lien kiet,chau tan,tran khon,truong hinh du,dien vien trung quoc,tin tuc,phim vo thuat,phim 3D&quot; src=&quot;http://media.tinmoi.vn//2011/12/24/3_26_1324660301_36_1324545242-long-mon-phi-giap--8-.jpg&quot; /&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		... v&amp;agrave; h&amp;igrave;nh ảnh trong Long m&amp;ocirc;n phi gi&amp;aacute;p&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Mặc thủy th&amp;agrave;nh l&amp;agrave; nơi ph&amp;aacute;t sinh c&amp;acirc;u chuyện đầu ti&amp;ecirc;n trong Long m&amp;ocirc;n phi gi&amp;aacute;p, to&amp;agrave;n bộ nh&amp;acirc;n m&amp;atilde; đều hội tụ nơi đ&amp;acirc;y. Để thuận tiện trong việc mi&amp;ecirc;u tả về địa danh t&amp;agrave;ng trữ kho b&amp;aacute;u trong truyền thuyết, Mặc thủy th&amp;agrave;nh đ&amp;atilde; hiện l&amp;ecirc;n hết sức sinh động v&amp;agrave; ho&amp;agrave;nh tr&amp;aacute;ng.&amp;nbsp; Rất nhiều kh&amp;aacute;n giả từng theo d&amp;otilde;i &amp;ldquo;Đại thoại T&amp;acirc;y du&amp;rdquo; hay &amp;ldquo;T&amp;acirc;n long m&amp;ocirc;n kh&amp;aacute;ch sạn&amp;rdquo; đều suy đo&amp;aacute;n Mặc thủy th&amp;agrave;nh tọa lạc ở Ninh Hạ. Tuy nhi&amp;ecirc;n, tr&amp;ecirc;n thực tế địa danh n&amp;agrave;y nằm ở khu vục Nội M&amp;ocirc;ng Cổ. Trong qu&amp;aacute; khứ nơi đ&amp;acirc;y từng gặp nạn b&amp;atilde;o c&amp;aacute;t khiến to&amp;agrave;n bộ d&amp;acirc;n cư v&amp;agrave; t&amp;agrave;i sản rơi v&amp;agrave;o &amp;ldquo;hố đen b&amp;iacute; ẩn&amp;rdquo;.&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		&lt;img alt=&quot;&amp;quot;Long môn phi giáp&amp;quot;: Những bí mật thú vị, Phim, Long mon phi giap,ly lien kiet,chau tan,tran khon,truong hinh du,dien vien trung quoc,tin tuc,phim vo thuat,phim 3D&quot; src=&quot;http://media.tinmoi.vn//2011/12/24/3_26_1324660307_64_1324545242-long-mon-phi-giap--3-.jpg&quot; /&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		Quế Lu&amp;acirc;n Mỹ vai Bố Lỗ Đ&amp;ocirc; - người đi t&amp;igrave;m kho b&amp;aacute;u tại Mặc thủy th&amp;agrave;nh&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Trong &amp;ldquo;Long m&amp;ocirc;n phi gi&amp;aacute;p&amp;rdquo;, đạo diễn Từ Khắc đ&amp;atilde; sử dụng địa danh n&amp;agrave;y l&amp;agrave;m t&amp;igrave;nh tiết quan trọng: nh&amp;acirc;n vật Bố Lỗ Đ&amp;ocirc; (Quế Lu&amp;acirc;n Mỹ thủ vai) kh&amp;ocirc;ng ngừng t&amp;igrave;m kiếm b&amp;aacute;u vật của gia tộc T&amp;acirc;y Hạ trong Mặc thủy th&amp;agrave;nh.&lt;/p&gt;\r\n	&lt;p&gt;\r\n		B&amp;iacute; mật về kết cấu vai diễn: T&amp;acirc;y Xưởng v&amp;agrave; Đ&amp;ocirc;ng Xưởng&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		&lt;img alt=&quot;&amp;quot;Long môn phi giáp&amp;quot;: Những bí mật thú vị, Phim, Long mon phi giap,ly lien kiet,chau tan,tran khon,truong hinh du,dien vien trung quoc,tin tuc,phim vo thuat,phim 3D&quot; src=&quot;http://media.tinmoi.vn//2011/12/24/3_26_1324660310_89_1324545354-long-mon-phi-giap--2-.jpg&quot; /&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		Trần Kh&amp;ocirc;n v&amp;agrave;o vai t&amp;ecirc;n th&amp;aacute;i gi&amp;aacute;m gian &amp;aacute;c, nham hiểm&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Trong &amp;ldquo;T&amp;acirc;n long m&amp;ocirc;n kh&amp;aacute;ch sạn&amp;rdquo;, Đ&amp;ocirc;ng Xưởng h&amp;atilde;m hại Trung Hiền v&amp;agrave; sau c&amp;ugrave;ng bị hiệp kh&amp;aacute;ch Ch&amp;acirc;u Chuẩn An s&amp;aacute;t hại. &amp;ldquo;Long m&amp;ocirc;n phi gi&amp;aacute;p&amp;rdquo; cũng tiếp nhận chi tiết n&amp;agrave;y v&amp;agrave; ph&amp;aacute;t triển th&amp;ecirc;m theo chiều hướng đi s&amp;acirc;u khắc họa. Trong đ&amp;oacute;, T&amp;acirc;y Xưởng Vũ Hoa Điền do Trần Kh&amp;ocirc;n thủ vai v&amp;igrave; tức giận trước c&amp;aacute;i chết của Đ&amp;ocirc;ng Xưởng đ&amp;atilde; mang theo binh sỹ tới bi&amp;ecirc;n giới giữa nh&amp;agrave; Minh v&amp;agrave; sa mạc ph&amp;iacute;a bắc để săn t&amp;igrave;m tội phạm của triều đ&amp;igrave;nh.&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		&lt;img alt=&quot;&amp;quot;Long môn phi giáp&amp;quot;: Những bí mật thú vị, Phim, Long mon phi giap,ly lien kiet,chau tan,tran khon,truong hinh du,dien vien trung quoc,tin tuc,phim vo thuat,phim 3D&quot; src=&quot;http://media.tinmoi.vn//2011/12/24/3_26_1324660311_78_1324545354-long-mon-phi-giap--1-.jpg&quot; /&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		&lt;img alt=&quot;&amp;quot;Long môn phi giáp&amp;quot;: Những bí mật thú vị, Phim, Long mon phi giap,ly lien kiet,chau tan,tran khon,truong hinh du,dien vien trung quoc,tin tuc,phim vo thuat,phim 3D&quot; src=&quot;http://media.tinmoi.vn//2011/12/24/3_26_1324660313_62_1324545354-long-mon-phi-giap--12-.jpg&quot; /&gt;&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center;&quot;&gt;\r\n		Một cảnh phim của Trần Kh&amp;ocirc;n v&amp;agrave; L&amp;yacute; Vũ Xu&amp;acirc;n&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Trong lịch sử Trung Quốc, hai nh&amp;acirc;n vật Đ&amp;ocirc;ng Xưởng v&amp;agrave; T&amp;acirc;y Xưởng cũng thực sự tồn tại ở thời nh&amp;agrave; Minh. Đ&amp;ocirc;ng Xưởng l&amp;agrave; th&amp;aacute;i gi&amp;aacute;m tổng cai quản hậu cung, l&amp;agrave; bề t&amp;ocirc;i th&amp;acirc;n t&amp;iacute;n của ho&amp;agrave;ng thượng. T&amp;acirc;y Xưởng l&amp;agrave; người &amp;ldquo;n&amp;acirc;ng đỡ&amp;rdquo; Vạn qu&amp;yacute; phi v&amp;agrave; sau đ&amp;oacute; &amp;ldquo;dựa hơi&amp;rdquo; để thăng quan tiến chức. Theo sử s&amp;aacute;ch ghi lại, nh&amp;acirc;n vật n&amp;agrave;y nhiều lần b&amp;agrave;y mưu t&amp;iacute;nh kế h&amp;atilde;m hại tất cả những người cản đường m&amp;igrave;n. &amp;Ocirc;ng ta nhiều lần h&amp;atilde;m hại mệnh quan triều đ&amp;igrave;nh với thủ đoạn nham hiểm độc &amp;aacute;c. Theo đ&amp;oacute;, sự xuất hiện của nh&amp;acirc;n vật phản diện Vũ Hoa Điền (Trần Kh&amp;ocirc;n) l&amp;agrave; ho&amp;agrave;n to&amp;agrave;n x&amp;aacute;c thực v&amp;agrave; tương th&amp;iacute;ch với lịch sử.&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;', 'long-mon-phi-giap-nhung-bi-mat-thu-vi', '', '', '', '', '', '24h.com.vn', 0, 0, 'upload/3_26_1324660313_62_1324545354-long-mon-phi-giap--12-.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 16:56:26', 'admin', '2012-10-10 16:56:26');
INSERT INTO `media` VALUES (60, 'default1349859805', '', 'media_news', '[6],', 'admin', '&quot;Alvin and the Chipmunks 3&quot; đã thật sự mắc cạn!', '', '', 'vn', '&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Mặc d&amp;ugrave; bộ phim &amp;quot;Giờ đen tối&amp;quot; c&amp;ograve;n chưa kịp ra mắt nhưng xem chừng ng&amp;agrave;y tận thế đ&amp;atilde; &amp;quot;&amp;aacute;m&amp;quot; lu&amp;ocirc;n c&amp;aacute;c ch&amp;uacute; s&amp;oacute;c chuột.&lt;/p&gt;\r\n&lt;div&gt;\r\n	Alvin and the Chipmunks 3&amp;nbsp;(Những Ch&amp;uacute; S&amp;oacute;c Chuột 3) l&amp;agrave; c&amp;acirc;u chuyện tiếp nối về ban nhạc s&amp;oacute;c chuột gồm 6 th&amp;agrave;nh vi&amp;ecirc;n mang t&amp;ecirc;n&amp;nbsp;Chipmunks and Chipettes. Phim được đầu tư đến 75 triệu $ (~1.577,5 tỉ đồng) v&amp;agrave; sử dụng nhiều ca kh&amp;uacute;c đang được y&amp;ecirc;u th&amp;iacute;ch trong h&amp;agrave;ng loạt bảng xếp hạng như&amp;nbsp;Bad Romance - Lady Gaga, Whip My Hair - Willow Smith, Survivor - Destiny Child ...&lt;/div&gt;', '&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Mặc d&amp;ugrave; bộ phim &amp;quot;Giờ đen tối&amp;quot; c&amp;ograve;n chưa kịp ra mắt nhưng xem chừng ng&amp;agrave;y tận thế đ&amp;atilde; &amp;quot;&amp;aacute;m&amp;quot; lu&amp;ocirc;n c&amp;aacute;c ch&amp;uacute; s&amp;oacute;c chuột.&lt;/p&gt;\r\n&lt;div&gt;\r\n	&lt;div&gt;\r\n		Alvin and the Chipmunks 3&amp;nbsp;(Những Ch&amp;uacute; S&amp;oacute;c Chuột 3) l&amp;agrave; c&amp;acirc;u chuyện tiếp nối về ban nhạc s&amp;oacute;c chuột gồm 6 th&amp;agrave;nh vi&amp;ecirc;n mang t&amp;ecirc;n&amp;nbsp;Chipmunks and Chipettes. Phim được đầu tư đến 75 triệu $ (~1.577,5 tỉ đồng) v&amp;agrave; sử dụng nhiều ca kh&amp;uacute;c đang được y&amp;ecirc;u th&amp;iacute;ch trong h&amp;agrave;ng loạt bảng xếp hạng như&amp;nbsp;Bad Romance - Lady Gaga, Whip My Hair - Willow Smith, Survivor - Destiny Child... Thế nhưng tr&amp;aacute;i với mong đợi,Alvin and the Chipmunks 3&amp;nbsp;lại mang đến nỗi thất vọng lớn cho c&amp;aacute;c fan h&amp;acirc;m mộ s&amp;oacute;c chuột.&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img src=&quot;http://k14.vcmedia.vn/Images/Uploaded/Share/2011/12/27/111227cineRA11.jpg&quot; /&gt;&lt;br /&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		C&amp;acirc;u chuyện của phần 3 xoay quanh chuyến du ngoạn tr&amp;ecirc;n biển của nh&amp;oacute;m nhạc s&amp;oacute;c chuột c&amp;ugrave;ng ch&amp;uacute; quản l&amp;yacute;Dave&amp;nbsp;trước khi tham gia show diễn quốc tế. Thế nhưng, thay v&amp;igrave; tận hưởng kỳ nghỉ 5 sao tr&amp;ecirc;n du thuyền sang trọng, cả nh&amp;oacute;m s&amp;oacute;c chuột cầm đầu l&amp;agrave;&amp;nbsp;Alvin&amp;nbsp;lại quậy ph&amp;aacute; g&amp;acirc;y bao thiệt hại. Điều n&amp;agrave;y khiến quản l&amp;yacute;Dave&amp;nbsp;v&amp;ocirc; c&amp;ugrave;ng giận dữ, cấm t&amp;uacute;c cả nh&amp;oacute;m trong ph&amp;ograve;ng v&amp;agrave; lại c&amp;agrave;ng khiến&amp;nbsp;Alvin&amp;nbsp;quyết t&amp;acirc;m đ&amp;agrave;o tho&amp;aacute;t. V&amp;ocirc; t&amp;igrave;nh, cậu nh&amp;oacute;c dẫn cả nh&amp;oacute;m đến một... hoang đảo. V&amp;agrave; tại đ&amp;acirc;y, nh&amp;oacute;m s&amp;oacute;c chuột phải đối mặt với những nguy hiểm chưa từng trải qua.&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img src=&quot;http://k14.vcmedia.vn/Images/Uploaded/Share/2011/12/27/111227cineRA04.jpg&quot; /&gt;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img src=&quot;http://k14.vcmedia.vn/Images/Uploaded/Share/2011/12/27/111227cineRA08.jpg&quot; /&gt;&lt;br /&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		C&amp;ocirc;ng bằng m&amp;agrave; đ&amp;aacute;nh gi&amp;aacute; th&amp;igrave;&amp;nbsp;Alvin and the Chipmunks 3&amp;nbsp;c&amp;oacute; phần h&amp;igrave;nh ảnh đẹp hơn 2 phần trước. Bối cảnh ch&amp;iacute;nh của phim diễn ra tr&amp;ecirc;n một h&amp;ograve;n đảo hoang sơ n&amp;ecirc;n kh&amp;aacute;n giả c&amp;oacute; l&amp;uacute;c như bị th&amp;ocirc;i mi&amp;ecirc;n bởi h&amp;igrave;nh ảnh thi&amp;ecirc;n nhi&amp;ecirc;n n&amp;uacute;i non h&amp;ugrave;ng vĩ, b&amp;atilde;i c&amp;aacute;t trắng mịn trải d&amp;agrave;i như ở Hawaii. V&amp;agrave; phần đồ họa th&amp;igrave; v&amp;ocirc; c&amp;ugrave;ng xuất sắc với những ch&amp;uacute; s&amp;oacute;c chuột sinh động v&amp;agrave; bộ l&amp;ocirc;ng mượt m&amp;agrave;. Đặc biệt nh&amp;oacute;m s&amp;oacute;c nữ&amp;nbsp;Chipettes&amp;nbsp;gồm 3 c&amp;ocirc; n&amp;agrave;ngBrittany, Jeanette, Eleanor&amp;nbsp;được chăm ch&amp;uacute;t kỹ hơn với rất nhiều bộ trang phục đẹp.&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img src=&quot;http://k14.vcmedia.vn/Images/Uploaded/Share/2011/12/27/111227cineRA09.jpg&quot; /&gt;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		Tuy nhi&amp;ecirc;n điểm cộng th&amp;igrave; &amp;iacute;t c&amp;ograve;n điểm trừ lại qu&amp;aacute; nhiều. Điều đầu ti&amp;ecirc;n khiến c&amp;aacute;c kh&amp;aacute;n giả h&amp;acirc;m mộ s&amp;oacute;c chuột thất vọng l&amp;agrave; ở phần 3 n&amp;agrave;y, nh&amp;oacute;m&amp;nbsp;Chipmunks and Chipettes&amp;nbsp;kh&amp;ocirc;ng h&amp;aacute;t nhiều như mong đợi. Bộ phim l&amp;agrave; h&amp;agrave;nh tr&amp;igrave;nh t&amp;igrave;m c&amp;aacute;ch sống s&amp;oacute;t trong rừng n&amp;ecirc;n kh&amp;ocirc;ng c&amp;oacute; show diễn, kh&amp;ocirc;ng c&amp;oacute; những trường đoạn h&amp;aacute;t h&amp;ograve; vui nhộn, nhắng nh&amp;iacute;t khiến kh&amp;aacute;n ph&amp;ograve;ng s&amp;ocirc;i động, h&amp;ograve; reo. Thay v&amp;agrave;o đ&amp;oacute; l&amp;agrave; h&amp;agrave;nh tr&amp;igrave;nh t&amp;igrave;m đồ ăn, l&amp;agrave;m nh&amp;agrave;... ch&amp;aacute;n ngắt v&amp;agrave; tẻ nhạt. C&amp;oacute; lẽ đ&amp;acirc;y ch&amp;iacute;nh l&amp;agrave; l&amp;yacute; do lớn nhất khiến kh&amp;aacute;n giả y&amp;ecirc;u th&amp;iacute;ch loạt phim s&amp;oacute;c chuột cảm thấy thất vọng.&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img src=&quot;http://k14.vcmedia.vn/Images/Uploaded/Share/2011/12/27/111227cineRA02.jpg&quot; /&gt;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img src=&quot;http://k14.vcmedia.vn/Images/Uploaded/Share/2011/12/27/111227cineRA07.jpg&quot; /&gt;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		Alvin and the Chipmunks 3&amp;nbsp;kh&amp;ocirc;ng chỉ tẻ nhạt v&amp;igrave; thiếu sự s&amp;ocirc;i động m&amp;agrave; bộ phim c&amp;ograve;n c&amp;oacute; nội dung rất gượng &amp;eacute;p khi cho nh&amp;acirc;n vật ch&amp;uacute; quản l&amp;yacute;&amp;nbsp;Dave&amp;nbsp;thay đổi t&amp;iacute;nh c&amp;aacute;ch. Từ 1 người quản l&amp;yacute; rất gần gũi, thấu hiểu v&amp;agrave; y&amp;ecirc;u thương nh&amp;oacute;m s&amp;oacute;c chuột trong 2 phần trước,&amp;nbsp;Dave&amp;nbsp;trong phần 3 trở n&amp;ecirc;n kh&amp;oacute; ưa, độc đo&amp;aacute;n lạ l&amp;ugrave;ng. C&amp;ograve;n nh&amp;acirc;n vật phản diện&amp;nbsp;Ian&amp;nbsp;lại đột ngột quay về cư xử v&amp;ocirc; c&amp;ugrave;ng... hiền h&amp;ograve;a v&amp;agrave; y&amp;ecirc;u thương nh&amp;oacute;m s&amp;oacute;c chuột. Rồi t&amp;igrave;nh cảm giữa&amp;nbsp;Alvin&amp;nbsp;v&amp;agrave;&amp;nbsp;Dave, 1 người quản l&amp;yacute; y&amp;ecirc;u thương ch&amp;uacute; s&amp;oacute;c&amp;nbsp;Alvin&amp;nbsp;đột ngột trở th&amp;agrave;nh t&amp;igrave;nh... cha con?! Điều n&amp;agrave;y khiến kh&amp;aacute;n giả 1 lần nữa bất ngờ v&amp;agrave; nhận ra sự v&amp;ocirc; l&amp;yacute;.&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img src=&quot;http://k14.vcmedia.vn/Images/Uploaded/Share/2011/12/27/111227cineRA13.jpg&quot; /&gt;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		Dường như người viết kịch bản phim kh&amp;ocirc;ng thể ph&amp;aacute;c họa ra c&amp;acirc;u chuyện kh&amp;aacute;c hấp dẫn hơn n&amp;ecirc;n đ&amp;agrave;nh phải sử dụng lại những nh&amp;acirc;n vật cũ v&amp;agrave; &amp;eacute;p họ c&amp;oacute; t&amp;iacute;nh c&amp;aacute;ch mới ho&amp;agrave;n to&amp;agrave;n tr&amp;aacute;i ngược. Điều n&amp;agrave;y g&amp;acirc;y phản cảm với những kh&amp;aacute;n giả đ&amp;atilde; xem 2 phần trước đ&amp;oacute;. Kế tiếp ch&amp;iacute;nh l&amp;agrave; sự d&amp;agrave;i d&amp;ograve;ng của c&amp;acirc;u chuyện v&amp;agrave; thừa th&amp;atilde;i của những nh&amp;acirc;n vật phụ. Kh&amp;aacute;n giả khi xem&amp;nbsp;Alvin and the Chipmunks 3&amp;nbsp;sẽ kh&amp;ocirc;ng thể nắm bắt hết nội dung bởi bộ phim c&amp;oacute; qu&amp;aacute; nhiều tuyến nh&amp;acirc;n vật, qu&amp;aacute; nhiều thứ được kể: n&amp;agrave;o l&amp;agrave; c&amp;acirc;u chuyện m&amp;acirc;u thuẫn t&amp;igrave;nh thương giữa&amp;nbsp;Dave&amp;nbsp;v&amp;agrave;&amp;nbsp;Alvin, rồi lại c&amp;oacute; m&amp;acirc;u thuẫn cũ giữa 2 người quản l&amp;yacute;&amp;nbsp;Dave&amp;nbsp;v&amp;agrave;&amp;nbsp;Ian. Cộng th&amp;ecirc;m chuyện tự kỷ của&amp;nbsp;Simon&amp;nbsp;chưa kịp định h&amp;igrave;nh th&amp;igrave; phim lại xuất hiện 1 nh&amp;acirc;n vật b&amp;iacute; ẩn mới toanh l&amp;agrave; nh&amp;agrave; nữ khảo cổ &amp;quot;h&amp;acirc;m h&amp;acirc;m&amp;quot;.&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img src=&quot;http://k14.vcmedia.vn/Images/Uploaded/Share/2011/12/27/111227cineRA12.jpg&quot; /&gt;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		D&amp;ugrave; rằng nh&amp;oacute;m s&amp;oacute;c chuột vẫn c&amp;oacute; 1 m&amp;agrave;n tr&amp;igrave;nh diễn cuối phim nhưng điều đ&amp;oacute; cũng kh&amp;ocirc;ng thể khiến bộ phim hấp dẫn, vui nhộn hơn khi thiếu đi chất giọng ngọng ngịu dễ thương của c&amp;aacute;c ca sĩ động vật. Tại Việt Nam,Alvin and the Chipmunks 3&amp;nbsp;được lồng tiếng Việt với 2 t&amp;ecirc;n tuổi: MC&amp;nbsp;Trấn Th&amp;agrave;nh&amp;nbsp;v&amp;agrave; ca sĩ&amp;nbsp;Bảo Thy. Nhưng d&amp;ugrave; khả năng lồng tiếng của họ c&amp;oacute; tốt đến mấy cũng kh&amp;ocirc;ng gi&amp;uacute;p bộ phim hay l&amp;ecirc;n được phần n&amp;agrave;o. D&amp;ugrave; vậy, trong một m&amp;ugrave;a Noel thiếu những phim giải tr&amp;iacute; cho những kh&amp;aacute;n giả s&amp;iacute;nh ngoại th&amp;igrave; bộ phim vẫn l&amp;agrave; một lựa chọn &amp;quot;kh&amp;ocirc;ng qu&amp;aacute; đến nỗi n&amp;agrave;o&amp;quot;!&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img src=&quot;http://k14.vcmedia.vn/Images/Uploaded/Share/2011/12/27/111227cineRA05.jpg&quot; /&gt;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&amp;nbsp;&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&lt;table cellpadding=&quot;5&quot; cellspacing=&quot;5&quot;&gt;\r\n			&lt;tbody&gt;\r\n				&lt;tr&gt;\r\n					&lt;td&gt;\r\n						&lt;p&gt;\r\n							Đạo diễn: Mike Mitchell&lt;br /&gt;\r\n							Diễn vi&amp;ecirc;n: Justin Long, Matthew Gray Gubler, Jesse McCartney&amp;nbsp;&lt;br /&gt;\r\n							Thể loại: H&amp;agrave;i hước&lt;br /&gt;\r\n							Ph&amp;acirc;n loại:&amp;nbsp;&lt;br /&gt;\r\n							&lt;img src=&quot;http://k14.vcmedia.vn/Images/Uploaded/Share/2011/12/27/filmRatingsG.jpg&quot; /&gt;&lt;br /&gt;\r\n							&amp;nbsp;&lt;/p&gt;\r\n					&lt;/td&gt;\r\n				&lt;/tr&gt;\r\n			&lt;/tbody&gt;\r\n		&lt;/table&gt;\r\n	&lt;/div&gt;\r\n	&lt;div&gt;\r\n		&lt;div&gt;\r\n			&amp;nbsp;&lt;/div&gt;\r\n		&lt;div style=&quot;text-align: center; &quot;&gt;\r\n			&lt;img src=&quot;http://k14.vcmedia.vn/Images/Uploaded/Share/2011/12/27/111227cineRA01.jpg&quot; /&gt;&lt;/div&gt;\r\n	&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;', 'alvin-and-the-chipmunks-3-da-that-su-mac-can', '', '', '', '', '', 'kenh14.vn', 0, 0, 'upload/dave.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 17:03:25', 'admin', '2012-10-10 17:03:25');
INSERT INTO `media` VALUES (61, 'default1349860088', '', 'media_news', '[6],', 'admin', 'IMEC phát triển công nghệ 3D Holographic dành cho tương lai', '', '', 'vn', '&lt;p&gt;\r\n	Holographic, một trong những c&amp;ocirc;ng nghệ 3D ti&amp;ecirc;n tiến v&amp;agrave; tiềm năng nhất hiện nay. Với c&amp;ocirc;ng nghệ n&amp;agrave;y, người xem kh&amp;ocirc;ng chỉ cảm nhận được chiều s&amp;acirc;u hay c&amp;aacute;c hiệu ứng nổi của vật thế như tr&amp;ecirc;n TV m&amp;agrave; c&amp;ograve;n cho ph&amp;eacute;p quan s&amp;aacute;t c&amp;aacute;c h&amp;igrave;nh ảnh trong kh&amp;ocirc;ng gian 360 độ...&lt;/p&gt;', '&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div&gt;\r\n	&lt;table&gt;\r\n		&lt;tbody&gt;\r\n			&lt;tr&gt;\r\n				&lt;td&gt;\r\n					&lt;a href=&quot;http://www.hdvietnam.com/diendan/attachment.php?attachmentid=42287&amp;amp;d=1325096295&quot; rel=&quot;nofollow&quot;&gt;&lt;img alt=&quot;&quot; border=&quot;0&quot; id=&quot;hs_imageresizer_container_2&quot; src=&quot;http://www.hdvietnam.com/diendan/attachment.php?attachmentid=42287&amp;amp;d=1325096295&quot; /&gt;&lt;/a&gt;&lt;br /&gt;\r\n					C&amp;ocirc;ng nghệ Holographic trong phim Iron Man.&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n		&lt;/tbody&gt;\r\n	&lt;/table&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n	&lt;p&gt;\r\n		&lt;br /&gt;\r\n		Holographic, một trong những c&amp;ocirc;ng nghệ 3D ti&amp;ecirc;n tiến v&amp;agrave; tiềm năng nhất hiện nay. Với c&amp;ocirc;ng nghệ n&amp;agrave;y, người xem kh&amp;ocirc;ng chỉ cảm nhận được chiều s&amp;acirc;u hay c&amp;aacute;c hiệu ứng nổi của vật thế như tr&amp;ecirc;n TV m&amp;agrave; c&amp;ograve;n cho ph&amp;eacute;p quan s&amp;aacute;t c&amp;aacute;c h&amp;igrave;nh ảnh trong kh&amp;ocirc;ng gian 360 độ. C&amp;aacute;c vật thể ảo sẽ xuất hiện tương tự như c&amp;aacute;c vật thể xuất hiện thực tế ngay trước mắt. Tuy nhi&amp;ecirc;n, hiện tại Holographic vẫn l&amp;agrave; một kh&amp;aacute;i niệm mơ hồ, một giấc mơ qu&amp;aacute; xa x&amp;ocirc;i cho tất cả ch&amp;uacute;ng ta. N&amp;oacute; chỉ tồn tại trong c&amp;aacute;c ph&amp;ograve;ng th&amp;iacute; nghiệm, trong c&amp;aacute;c lĩnh vực rất đặc biệt như quốc ph&amp;ograve;ng, vũ trụ hay những nơi m&amp;agrave; người b&amp;igrave;nh thường kh&amp;oacute; c&amp;oacute; thể tiếp cận.&amp;nbsp;&lt;br /&gt;\r\n		&lt;br /&gt;\r\n		Vừa qua, một nh&amp;oacute;m nghi&amp;ecirc;n cứu chất b&amp;aacute;n dẫn của Ch&amp;acirc;u &amp;Acirc;u c&amp;oacute; t&amp;ecirc;n l&amp;agrave; IMEC đ&amp;atilde; mang giấc mơ lại gần t&amp;iacute;n đồ h&amp;igrave;nh ảnh hơn một ch&amp;uacute;t bằng c&amp;aacute;ch giới thiệu c&amp;ocirc;ng nghệ Holographic mới cho ph&amp;eacute;p c&amp;aacute;c trải nghiệm trở n&amp;ecirc;n dễ chịu hơn, ho&amp;agrave;n to&amp;agrave;n kh&amp;ocirc;ng g&amp;acirc;y đau đầu v&amp;agrave; tăng gấp đ&amp;ocirc;i mật độ điểm ảnh.&lt;/p&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n	&lt;p&gt;\r\n		&lt;br /&gt;\r\n		&lt;p&gt;\r\n			&lt;br /&gt;\r\n			&lt;div&gt;\r\n				&lt;table&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td&gt;\r\n								&lt;a href=&quot;http://www.hdvietnam.com/diendan/attachment.php?attachmentid=42286&amp;amp;d=1325096291&quot; rel=&quot;nofollow&quot;&gt;&lt;img alt=&quot;&quot; border=&quot;0&quot; id=&quot;hs_imageresizer_container_1&quot; src=&quot;http://www.hdvietnam.com/diendan/attachment.php?attachmentid=42286&amp;amp;d=1325096291&quot; /&gt;&lt;/a&gt;&lt;/td&gt;\r\n							&lt;td&gt;\r\n								&lt;div&gt;\r\n									&lt;table&gt;\r\n										&lt;tbody&gt;\r\n											&lt;tr&gt;\r\n												&lt;td&gt;\r\n													Trong một hệ thống c&amp;oacute; k&amp;iacute;ch thước nanometer, IMEC tạo ra c&amp;aacute;c con chip bằng c&amp;aacute;ch phủ một lớp oxit silic tr&amp;ecirc;n tấm silicon đặc biệt. Những con chip n&amp;agrave;y c&amp;oacute; h&amp;igrave;nh vu&amp;ocirc;ng v&amp;agrave; được sắp xếp xen kẽ như b&amp;agrave;n cờ. Khi &amp;aacute;nh s&amp;aacute;ng laser chiếu l&amp;ecirc;n chip, &amp;aacute;nh s&amp;aacute;ng sẽ bị phản xạ ra m&amp;eacute;p (ranh giữa chip v&amp;agrave; điểm ảnh).&lt;br /&gt;\r\n													&lt;br /&gt;\r\n													Hiện tượng nhiễu xạ xảy ra, &amp;aacute;nh s&amp;aacute;ng bị giao thoa v&amp;agrave; triệt ti&amp;ecirc;u để tạo ra h&amp;igrave;nh ảnh 3D, những con chip hoạt động như những chiếc pitton c&amp;oacute; thể n&amp;acirc;ng l&amp;ecirc;n hay hạ xuống dễ d&amp;agrave;ng, nhiều lần trong một gi&amp;acirc;y để tạo ra c&amp;aacute;c h&amp;igrave;nh ảnh chuyển động.&lt;/td&gt;\r\n											&lt;/tr&gt;\r\n										&lt;/tbody&gt;\r\n									&lt;/table&gt;\r\n								&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n			&lt;/div&gt;\r\n			&lt;br /&gt;\r\n			&lt;br /&gt;\r\n			Vấn đề lớn nhất của c&amp;ocirc;ng nghệ 3D hiện nay đ&amp;oacute; l&amp;agrave; chứng đau đầu v&amp;agrave; buồn n&amp;ocirc;n m&amp;agrave; người xem thường gặp phải. Nguy&amp;ecirc;n nh&amp;acirc;n ch&amp;iacute;nh, theo c&amp;aacute;c nh&amp;agrave; nghi&amp;ecirc;n cứu v&amp;agrave; nh&amp;agrave; quy phim đ&amp;oacute; l&amp;agrave; c&amp;aacute;c ảo gi&amp;aacute;c đ&amp;atilde; tạo ra sự xung đột giữa hai qu&amp;aacute; tr&amp;igrave;nh quan trọng trong hoạt động tiếp nhận h&amp;igrave;nh ảnh. Đ&amp;ocirc;i mắt của ch&amp;uacute;ng ta phải tập trung quan s&amp;aacute;t c&amp;aacute;c h&amp;igrave;nh ảnh trong mặt phẳng của m&amp;agrave;n h&amp;igrave;nh, vừa phải định hướng c&amp;aacute;c đối tượng trong kh&amp;ocirc;ng gian 3 chiều ảo.&lt;br /&gt;\r\n			&lt;br /&gt;\r\n			IMEC hy vọng sẽ tạo ra bản concept đầu ti&amp;ecirc;n cho c&amp;ocirc;ng nghệ Holographic của m&amp;igrave;nh v&amp;agrave;o giữa năm 2012 v&amp;agrave; hứa hẹn sẽ giải quyết triệt để vấn đề tr&amp;ecirc;n trong tương lai gần.&lt;/p&gt;\r\n	&lt;/p&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n	&lt;p&gt;\r\n		&lt;br /&gt;\r\n		&lt;div&gt;\r\n			&lt;table&gt;\r\n				&lt;tbody&gt;\r\n					&lt;tr&gt;\r\n						&lt;td style=&quot;text-align: center; &quot;&gt;\r\n							C&amp;ocirc;ng nghệ Holographic của IMEC&lt;br /&gt;\r\n							&lt;iframe class=&quot;restrain&quot; frameborder=&quot;0&quot; height=&quot;390&quot; id=&quot;yui-gen0&quot; src=&quot;http://www.youtube.com/embed/xCbQIOqFgC0?wmode=opaque&quot; title=&quot;YouTube video player&quot; width=&quot;640&quot;&gt;&lt;/iframe&gt;&lt;br /&gt;\r\n							&amp;nbsp;&lt;/td&gt;\r\n					&lt;/tr&gt;\r\n				&lt;/tbody&gt;\r\n			&lt;/table&gt;\r\n		&lt;/div&gt;\r\n	&lt;/p&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;', 'imec-phat-trien-cong-nghe-3d-holographic-danh-cho-tuong-lai', '', '', '', '', '', 'Engadget, IEEE', 0, 0, 'upload/3-Ironman_hologram.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 17:08:08', 'admin', '2012-10-10 17:08:08');
INSERT INTO `media` VALUES (62, 'default1349860215', '', 'media_news', '[6],', 'admin', 'Tôn Ngộ Không &#039;tằng tằng&#039; 3D tiến', '', '', 'vn', '&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div&gt;\r\n	Đại n&amp;aacute;o thi&amp;ecirc;n cung 3D&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;Vốn đ&amp;atilde; th&amp;agrave;nh lệ, mỗi dịp năm mới đều l&amp;agrave; dịp &amp;quot;ăn n&amp;ecirc;n l&amp;agrave;m ra&amp;quot; của Cừu vui vẻ v&amp;agrave; S&amp;oacute;i x&amp;aacute;m. Tuy nhi&amp;ecirc;n trong năm 2012 tới, đối thủ thực sự đ&amp;aacute;ng gờm của c&amp;aacute;c bạn trẻ th&amp;ocirc;n Cừu đ&amp;atilde; xuất hiện.&lt;/div&gt;', '&lt;p&gt;\r\n	Đại n&amp;aacute;o thi&amp;ecirc;n cung 3D&lt;/p&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Vốn đ&amp;atilde; th&amp;agrave;nh lệ, mỗi dịp năm mới đều l&amp;agrave; dịp &amp;quot;ăn n&amp;ecirc;n l&amp;agrave;m ra&amp;quot; của Cừu vui vẻ v&amp;agrave; S&amp;oacute;i x&amp;aacute;m. Tuy nhi&amp;ecirc;n trong năm 2012 tới, đối thủ thực sự đ&amp;aacute;ng gờm của c&amp;aacute;c bạn trẻ th&amp;ocirc;n Cừu đ&amp;atilde; xuất hiện. Đ&amp;oacute; kh&amp;ocirc;ng phải ai xa lạ m&amp;agrave; ch&amp;iacute;nh l&amp;agrave; ch&amp;uacute; khỉ huyền thoại T&amp;ocirc;n Ngộ Kh&amp;ocirc;ng. Lần trở lại n&amp;agrave;y, Bật M&amp;atilde; &amp;Ocirc;nđ&amp;atilde; kho&amp;aacute;c l&amp;ecirc;n m&amp;igrave;nh một diện mạo ho&amp;agrave;n to&amp;agrave;n mới với sự trợ gi&amp;uacute;p đắc lực của c&amp;ocirc;ng nghệ 3D.&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; height=&quot;auto&quot; src=&quot;images/autosize-800x800/upload/6_26_1325092658_99_111227cinethiencung3d01.jpg&quot; width=&quot;auto&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;em&gt;&amp;quot;Yahoo! L&amp;atilde;o T&amp;ocirc;n đ&amp;atilde; trở lại rồi!!!&amp;quot;&lt;/em&gt;&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Dự &amp;aacute;n hoạt h&amp;igrave;nh &lt;strong&gt;Đại n&amp;aacute;o thi&amp;ecirc;n cung 3D&lt;/strong&gt; từ l&amp;acirc;u đ&amp;atilde; thu h&amp;uacute;t sự ch&amp;uacute; &amp;yacute; mạnh mẽ của kh&amp;aacute;n giả h&amp;acirc;m mộ điện ảnh tại Trung Quốc. Kh&amp;ocirc;ng chỉ c&amp;oacute; kh&amp;aacute;n giả nh&amp;iacute; mong chờ sự t&amp;aacute;i xuất của một huyền thoại m&amp;agrave; kh&amp;aacute;n giả trưởng th&amp;agrave;nh v&amp;agrave; thậm ch&amp;iacute; l&amp;agrave; giới chuy&amp;ecirc;n m&amp;ocirc;n cũng đặt nhiều niềm tin v&amp;agrave;o t&amp;aacute;c phẩm hoạt h&amp;igrave;nh đ&amp;igrave;nh đ&amp;aacute;m n&amp;agrave;y. Sớm được kỳ vọng l&amp;agrave; t&amp;aacute;c phẩm hoạt h&amp;igrave;nh xuất sắc nhất năm 2012, &lt;strong&gt;Đại n&amp;aacute;o thi&amp;ecirc;n cung 3D&lt;/strong&gt; ra mắt đ&amp;atilde; đem đến sự h&amp;aacute;o hức cho đ&amp;ocirc;ng đảo người y&amp;ecirc;u điện ảnh.&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Kh&amp;ocirc;ng hổ danh l&amp;agrave; &amp;quot;si&amp;ecirc;u bom tấn&amp;quot; hoạt h&amp;igrave;nh Trung Quốc, &lt;strong&gt;Đại n&amp;aacute;o thi&amp;ecirc;n cung&lt;/strong&gt; biết c&amp;aacute;ch tận dụng tối đa những gương mặt nghệ sĩ đ&amp;igrave;nh đ&amp;aacute;m g&amp;oacute;p giọng trong phim. Vừa qua, một loạt clip quảng c&amp;aacute;o đ&amp;atilde; &amp;quot;rầm rộ&amp;quot; đổ bộ, &amp;quot;phong tỏa&amp;quot; v&amp;agrave; g&amp;acirc;y n&amp;aacute;o loạn tại c&amp;aacute;c trang tin điện ảnh, c&amp;aacute;c trang chia sẻ video tại Trung Quốc. Đội ngũ nghệ sĩ tham gia v&amp;agrave;o &lt;strong&gt;Đại n&amp;aacute;o thi&amp;ecirc;n cung 3D&lt;/strong&gt; c&amp;oacute; thể kể đến:&lt;strong&gt;Trương Quốc Lập, Hồ Ca, Lưu Hiểu Kh&amp;aacute;nh, Trần Đạo Minh, Trần Khải Ca...&lt;/strong&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;Đại n&amp;aacute;o thi&amp;ecirc;n cung 3D&lt;/strong&gt; l&amp;agrave; dự &amp;aacute;n tiền tỷ c&amp;oacute; nhiệm vụ &amp;quot;3D h&amp;oacute;a&amp;quot; t&amp;aacute;c phẩm hoạt h&amp;igrave;nh kinh điển &lt;strong&gt;Đại n&amp;aacute;o thi&amp;ecirc;n cung&lt;/strong&gt; (1965). Đ&amp;acirc;y l&amp;agrave; một trong những bộ phim hoạt h&amp;igrave;nh ti&amp;ecirc;u biểu của nền điện ảnh Trung Quốc, được đ&amp;aacute;nh gi&amp;aacute; v&amp;agrave; tr&amp;acirc;n trọng như một m&amp;oacute;n &amp;quot;quốc bảo&amp;quot; của đất nước đ&amp;ocirc;ng d&amp;acirc;n n&amp;agrave;y. Ch&amp;iacute;nh v&amp;igrave; vậy, &lt;strong&gt;Đại n&amp;aacute;o thi&amp;ecirc;n cung 3D&lt;/strong&gt; hứa hẹn sẽ gi&amp;agrave;u t&amp;iacute;nh nghệ thuật chứ kh&amp;ocirc;ng chỉ l&amp;agrave; bộ phim hoạt h&amp;igrave;nh giải tr&amp;iacute; đơn thuần như &amp;quot;đối thủ&amp;quot; &lt;strong&gt;Cừu vui vẻ v&amp;agrave; S&amp;oacute;i x&amp;aacute;m&lt;/strong&gt;.&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; height=&quot;auto&quot; src=&quot;images/autosize-800x800/upload/6_26_1325092659_66_111227cinethiencung3d08.jpg&quot; width=&quot;auto&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; height=&quot;auto&quot; src=&quot;images/autosize-800x800/upload/6_26_1325092660_23_111227cinethiencung3d07.jpg&quot; width=&quot;auto&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; height=&quot;auto&quot; src=&quot;images/autosize-800x800/upload/6_26_1325092660_76_111227cinethiencung3d06.jpg&quot; width=&quot;auto&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; height=&quot;auto&quot; src=&quot;images/autosize-800x800/upload/6_26_1325092661_31_111227cinethiencung3d05.jpg&quot; width=&quot;auto&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;em&gt;Vẻ đẹp m&amp;ecirc; hồn của c&amp;aacute;c thiết kế trong &amp;quot;Đại n&amp;aacute;o thi&amp;ecirc;n cung 3D&amp;quot;&lt;/em&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Tuy c&amp;ocirc;ng bố chưa nhiều h&amp;igrave;nh ảnh trong phim nhưng kh&amp;aacute;n giả c&amp;oacute; thể dễ d&amp;agrave;ng nhận thấy rằng Đại n&amp;aacute;o thi&amp;ecirc;n cung 3D giữ được gần như nguy&amp;ecirc;n vẹn tinh thần cũng như vẻ đẹp của phi&amp;ecirc;n bản 1965. Từ c&amp;aacute;ch tạo h&amp;igrave;nh nh&amp;acirc;n vật mang m&amp;agrave;u sắc kinh kịch v&amp;agrave; nghệ thuật cắt giấy, cho đến c&amp;aacute;ch dựng cảnh, phối m&amp;agrave;u sắc v&amp;agrave; cả c&amp;aacute;ch di động uyển chuyển, mềm mại của c&amp;aacute;c nh&amp;acirc;n vật cũng rất &amp;quot;được l&amp;ograve;ng&amp;quot; người xem.&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; height=&quot;auto&quot; src=&quot;images/autosize-800x800/upload/6_26_1325092661_83_111227cinethiencung3d02.jpg&quot; width=&quot;auto&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; height=&quot;auto&quot; src=&quot;images/autosize-800x800/upload/6_26_1325092662_37_111227cinethiencung3d03.jpg&quot; width=&quot;auto&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; height=&quot;auto&quot; src=&quot;images/autosize-800x800/upload/6_26_1325092662_86_111227cinethiencung3d04.jpg&quot; width=&quot;auto&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	C&amp;ograve;n đ&amp;acirc;y l&amp;agrave; tạo h&amp;igrave;nh kiểu chibi cực cute của T&amp;ocirc;n Ngộ Kh&amp;ocirc;ng n&amp;egrave;&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Như vậy, trong th&amp;aacute;ng 1 sắp tới, kh&amp;aacute;n giả Trung Quốc sẽ được dịp &amp;quot;m&amp;atilde;n nh&amp;atilde;n&amp;quot; trước cuộc chiến nảy lửa của 2 &amp;quot;thế lực&amp;quot;: một b&amp;ecirc;n l&amp;agrave; Đại n&amp;aacute;o thi&amp;ecirc;n cung với vẻ đẹp truyền thống, gi&amp;agrave;u t&amp;iacute;nh nghệ thuật, b&amp;ecirc;n kia l&amp;agrave; Cừu vui vẻ v&amp;agrave; S&amp;oacute;i x&amp;aacute;m với nội dung tươi trẻ. Cả hai hứa hẹn sẽ l&amp;agrave;m n&amp;ecirc;n &amp;quot;Đại chiến 3D&amp;quot; tr&amp;ecirc;n khắp c&amp;aacute;c ph&amp;ograve;ng v&amp;eacute; Trung Quốc v&amp;agrave;o ng&amp;agrave;y 12/1 sắp tới.&lt;/div&gt;', 'ton-ngo-khong-tang-tang-3d-tien', '', '', '', '', '', '', 0, 0, 'upload/6_26_1325092658_99_111227cinethiencung3d01.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 17:10:15', 'admin', '2012-10-10 17:10:15');
INSERT INTO `media` VALUES (63, 'default1349860537', '', 'media_news', '[6],', 'admin', 'Những tác phẩm nghệ thuật đường phố đẹp nhất năm 2011', '', '', 'vn', '&lt;p&gt;\r\n	Bản chất của nghệ thuật đường phố l&amp;agrave; sự tự do, ph&amp;oacute;ng kho&amp;aacute;ng, h&amp;agrave;i hước nhưng s&amp;acirc;u sắc. Th&amp;ocirc;ng qua t&amp;aacute;c phẩm, t&amp;aacute;c giả mang đến những th&amp;ocirc;ng điệp m&amp;agrave; hầu như kh&amp;ocirc;ng bị phụ thuộc v&amp;agrave;o bất cứ quy tắc n&amp;agrave;o.&lt;/p&gt;', '&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div&gt;\r\n	Bản chất của nghệ thuật đường phố l&amp;agrave; sự tự do, ph&amp;oacute;ng kho&amp;aacute;ng, h&amp;agrave;i hước nhưng s&amp;acirc;u sắc. Th&amp;ocirc;ng qua t&amp;aacute;c phẩm, t&amp;aacute;c giả mang đến những th&amp;ocirc;ng điệp m&amp;agrave; hầu như kh&amp;ocirc;ng bị phụ thuộc v&amp;agrave;o bất cứ quy tắc n&amp;agrave;o. Đ&amp;ocirc;i khi t&amp;aacute;c phẩm chỉ l&amp;agrave; một sự sắp đặt mang t&amp;iacute;nh chất giải tr&amp;iacute;, nhưng chỉ thế th&amp;ocirc;i cũng đủ để biến ch&amp;uacute;ng th&amp;agrave;nh những thứ c&amp;oacute; gi&amp;aacute; trị hơn cả những bức tranh được đặt trong viện bảo t&amp;agrave;ng, thứ m&amp;agrave; để chi&amp;ecirc;m ngưỡng, bạn phải mua v&amp;eacute; v&amp;agrave; xếp h&amp;agrave;ng.&lt;/div&gt;\r\n&lt;div id=&quot;ymfckdetail&quot;&gt;\r\n	&lt;p style=&quot;text-align: center; &quot;&gt;\r\n		&lt;a href=&quot;http://www.hdvietnam.com/diendan/attachment.php?attachmentid=42856&amp;amp;d=1325261632&quot; target=&quot;_blank&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.hdvietnam.com/diendan/attachment.php?attachmentid=42856&amp;amp;d=1325261632&quot; style=&quot;border-width: 0px; border-style: solid; width: 700px; height: 1057px; &quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n	&lt;div style=&quot;text-align: center; &quot;&gt;\r\n		&lt;br /&gt;\r\n		&lt;a href=&quot;http://www.hdvietnam.com/diendan/attachment.php?attachmentid=42855&amp;amp;d=1325261628&quot;