INSERT INTO `media` VALUES (37, 'default1349513586', '', 'media_partners', '[8],', 'admin', 'Cafe 3D Nắng Thuỷ Tinh - Kiên Lương', '', '', 'vn', '&lt;p&gt;\r\n	Tọa lạc tại 335 Quốc Lộ 80 Ki&amp;ecirc;n Lương - Ki&amp;ecirc;n Giang.&lt;/p&gt;', '&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Qu&amp;aacute;n sử dụng hệ thống 3D ph&amp;acirc;n cực d&amp;agrave;nh cho 40 người xem. M&amp;agrave;n h&amp;igrave;nh bạc nguy&amp;ecirc;n tấm 150 inch tỷ lệ điện ảnh 16:9.Hệ thống &amp;acirc;m thanh Jamo 5.1.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Phim &amp;nbsp;3D đang chiếu : Captain America, Car 2, Green Lantern,Thor,..&lt;/p&gt;', 'cafe-3d-nang-thuy-tinh--kien-luong', '', '', '', '', '', '', 0, 0, 'upload/ntt3d-hd05.jpg', 0, '', '', '', 0, 0, 'delete', '', '2012-10-06 17:06:09', 'admin', '2012-10-06 16:53:06');
