INSERT INTO `media` VALUES (67, 'default1349862398', '', 'media_news', '[6],', 'admin', 'Dolby ra mắt chuẩn hình ảnh 3D tại gia', '', '', 'vn', '&lt;p&gt;\r\n	Dolby Digital hợp t&amp;aacute;c với Philips để tr&amp;igrave;nh l&amp;agrave;ng một chuẩn 3D mới, gi&amp;uacute;p h&amp;igrave;nh ảnh nổi trở n&amp;ecirc;n sắc n&amp;eacute;t v&amp;agrave; r&amp;otilde; r&amp;agrave;ng hơn.&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;', '&lt;p&gt;\r\n	Dolby Digital hợp t&amp;aacute;c với Philips để tr&amp;igrave;nh l&amp;agrave;ng một chuẩn 3D mới, gi&amp;uacute;p h&amp;igrave;nh ảnh nổi trở n&amp;ecirc;n sắc n&amp;eacute;t v&amp;agrave; r&amp;otilde; r&amp;agrave;ng hơn.&lt;/p&gt;\r\n&lt;table border=&quot;0&quot; cellpadding=&quot;3&quot; cellspacing=&quot;0&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;img alt=&quot;Sau âm thanh vòm đa kênh, Dolby sẽ có chuẩn hình ảnh 3D.&quot; border=&quot;1&quot; src=&quot;http://www.tin247.com/sohoa/120416094400-113-639.jpg&quot; /&gt;&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td style=&quot;text-align: center; &quot;&gt;\r\n				Sau &amp;acirc;m thanh v&amp;ograve;m đa k&amp;ecirc;nh, Dolby sẽ c&amp;oacute; chuẩn h&amp;igrave;nh ảnh 3D.&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Việc từng th&amp;agrave;nh c&amp;ocirc;ng với c&amp;aacute;c t&amp;aacute;c phẩm điện ảnh 3D ở&amp;nbsp;&lt;a href=&quot;http://vatgia.com/home/quicksearch.php?keyword=r%E1%BA%A1p+chi%E1%BA%BFu+phim&quot; target=&quot;_blank&quot;&gt;rạp chiếu phim&lt;/a&gt;&amp;nbsp;trong thời gian qua, Dolby muốn hướng đến việc phổ cập c&amp;ocirc;ng nghệ 3D hơn nữa c&amp;aacute;c d&amp;ograve;ng thiết bị phổ th&amp;ocirc;ng v&amp;agrave; gần gũi với người d&amp;ugrave;ng như TV,&amp;nbsp;&lt;a href=&quot;http://vatgia.com/512/man-chieu-projector.html&quot; target=&quot;_blank&quot;&gt;m&amp;aacute;y chiếu&lt;/a&gt;&amp;nbsp;hay&amp;nbsp;&lt;a href=&quot;http://www.vatgia.com/317/may-tinh-laptop.html&quot; target=&quot;_blank&quot;&gt;laptop&lt;/a&gt;&amp;nbsp;bằng việc hợp t&amp;aacute;c với Philips. Sự hợp t&amp;aacute;c tr&amp;ecirc;n gi&amp;uacute;p tạo ra một chuẩn h&amp;igrave;nh ảnh 3D mới với nhiều ưu điểm, như c&amp;aacute;c h&amp;igrave;nh ảnh nổi trở n&amp;ecirc;n sắc n&amp;eacute;t v&amp;agrave; r&amp;otilde; r&amp;agrave;ng hơn, người d&amp;ugrave;ng c&amp;oacute; thể điều chỉnh hiệu ứng nổi v&amp;agrave; chiều theo &amp;yacute; th&amp;iacute;ch v&amp;agrave; hỗ trợ c&amp;ocirc;ng nghệ 3D kh&amp;ocirc;ng k&amp;iacute;nh. B&amp;ecirc;n cạnh đ&amp;oacute; ti&amp;ecirc;u chuẩn n&amp;agrave;y c&amp;ograve;n cho ph&amp;eacute;p c&amp;aacute;c nh&amp;agrave; sản xuất c&amp;oacute; thể sử dụng tr&amp;ecirc;n nhiều thiết bị kh&amp;aacute;c nhau từ TV, laptop, m&amp;aacute;y t&amp;iacute;nh bảng cho tới smarphone... , tối ưu h&amp;oacute;a chất lượng video cho bất kỳ loại&amp;nbsp;&lt;a href=&quot;http://vatgia.com/3044/man-hinh-cam-ung-lcd.html&quot; target=&quot;_blank&quot;&gt;m&amp;agrave;n h&amp;igrave;nh&lt;/a&gt;&amp;nbsp;n&amp;agrave;o.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Tuy nhi&amp;ecirc;n, ưu điểm lớn nhất của Dolby 3D l&amp;agrave; mang tới cho người d&amp;ugrave;ng một chuẩn 3D thống nhất để c&amp;oacute; dễ d&amp;agrave;ng thưởng thức được hiệu ứng h&amp;igrave;nh ảnh một c&amp;aacute;ch tốt nhất. Điều n&amp;agrave;y tương tự như chuẩn &amp;acirc;m thanh v&amp;ograve;m Dolby Digital đang rất th&amp;ocirc;ng dụng hiện nay.&lt;/p&gt;', 'dolby-ra-mat-chuan-hinh-anh-3d-tai-gia-1', '', '', '', '', '', '', 0, 0, 'upload/120416094400-113-639.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 17:47:04', 'admin', '2012-10-10 17:47:04');
INSERT INTO `media` VALUES (68, 'default1349863111', '', 'media_news', '[6],', 'admin', 'Đánh giá máy chiếu 3D Optoma HD33', '', '', 'vn', '&lt;p&gt;\r\n	&lt;span style=&quot;text-align: justify; &quot;&gt;Optoma HD33 l&amp;agrave; mẫu m&amp;aacute;y chiếu 3D được đ&amp;aacute;nh gi&amp;aacute; cao, hấp dẫn khi sở hữu t&amp;iacute;nh năng tr&amp;igrave;nh diễn 3D Full HD, chất lượng h&amp;igrave;nh ảnh tốt với mức gi&amp;aacute; cạnh tranh.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Optoma HD33 l&amp;agrave; mẫu m&amp;aacute;y chiếu 3D được đ&amp;aacute;nh gi&amp;aacute; cao, hấp dẫn khi sở hữu t&amp;iacute;nh năng tr&amp;igrave;nh diễn 3D Full HD, chất lượng h&amp;igrave;nh ảnh tốt với mức gi&amp;aacute; cạnh tranh.&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Mức gi&amp;aacute; 44.100.000 đồng (bao gồm thuế VAT) d&amp;agrave;nh cho HD33 kh&amp;ocirc;ng phải l&amp;agrave; qu&amp;aacute; cao nếu x&amp;eacute;t trong c&amp;aacute;c d&amp;ograve;ng m&amp;aacute;y chiếu hỗ trợ ph&amp;aacute;t Full HD 3D hiện c&amp;oacute; tr&amp;ecirc;n thị trường. Thay cho kết nối HDMI 1.3 ở c&amp;aacute;c model tiền nhiệm như HD66 v&amp;agrave; HD67, HD33 được trang bị HDMI chuẩn 1.4a nhờ vậy tương th&amp;iacute;ch với tất cả c&amp;aacute;c định dạng 3D th&amp;ocirc;ng dụng hiện nay từ Side-by-side, Top-and-Bottom cho tới Frame Sequential. Người d&amp;ugrave;ng c&amp;oacute; thể kết nối trực tiếp m&amp;aacute;y chiếu với đầu Blu-ray, đầu HD, m&amp;aacute;y t&amp;iacute;nh x&amp;aacute;ch tay hay m&amp;aacute;y chơi game để thưởng thức h&amp;igrave;nh ảnh nổi. Model sử dụng c&amp;ocirc;ng nghệ 3D m&amp;agrave;n trập với k&amp;iacute;nh chuy&amp;ecirc;n dụng từ Optoma.&lt;/p&gt;\r\n&lt;div id=&quot;article_content&quot;&gt;\r\n	&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n		Optoma HD33 l&amp;agrave; sự lựa chọn hợp l&amp;yacute; v&amp;agrave; hấp dẫn với những người c&amp;oacute; nhu cầu thưởng thức 3D v&amp;agrave; HD tại gia. Ưu thế về khả năng tr&amp;igrave;nh diễn h&amp;igrave;nh ảnh tốt ở k&amp;iacute;ch thước rộng, chất lượng 3D ổn định cũng sẽ khiến cho nhiều mẫu HDTV 3D đắt tiền phải lo lắng trước mẫu m&amp;aacute;y chiếu 3D Full HD của Optoma.&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n		Dưới đ&amp;acirc;y l&amp;agrave; một số tổng hợp, đ&amp;aacute;nh gi&amp;aacute; chi tiết về model n&amp;agrave;y (nhấp v&amp;agrave;o từng phần để xem chi tiết).&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n		&amp;nbsp;&lt;/p&gt;\r\n	&lt;p class=&quot;Normal&quot;&gt;\r\n		&lt;em&gt;Phần 1: Thiết kế v&amp;agrave; th&amp;ocirc;ng số kỹ thuật&lt;/em&gt;&lt;/p&gt;\r\n	&lt;p class=&quot;Normal&quot; style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img alt=&quot;&quot; src=&quot;http://l.f5.img.vnexpress.net/2012/04/10/1000037709_Optoma-HD33-4_480x0.jpg&quot; style=&quot;width: 480px; height: 319px; &quot; /&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		So với những model của Optoma đ&amp;atilde; v&amp;agrave; đang c&amp;oacute; mặt ở Việt Nam, HD33 c&amp;oacute; d&amp;aacute;ng vẻ kh&amp;aacute;c biệt ho&amp;agrave;n to&amp;agrave;n. Sản phẩm c&amp;oacute; k&amp;iacute;ch thước lớn, kiểu d&amp;aacute;ng b&amp;oacute;ng bẩy thời trang v&amp;agrave; tỏ ra điệu đ&amp;agrave;. To&amp;agrave;n bộ vỏ ngo&amp;agrave;i của m&amp;aacute;y được phủ một lớp sơn m&amp;agrave;u trắng dạng b&amp;oacute;ng c&amp;oacute; thể hạn chế việc b&amp;aacute;m bụi v&amp;agrave; bẩn. Thay cho c&amp;aacute;c mắt lưới đan ngang vốn xuất hiện tr&amp;ecirc;n nhiều model như HD23, HD66/67 hay mới đ&amp;acirc;y l&amp;agrave; GT750, mặt ph&amp;iacute;a trước của HD33 tho&amp;aacute;ng hơn nhiều khi chỉ c&amp;oacute; duy nhất ống k&amp;iacute;nh của m&amp;aacute;y chiếu đặt lệch về b&amp;ecirc;n phải.&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Mặt lưng của m&amp;aacute;y cũng kh&amp;ocirc;ng hề c&amp;oacute; bất kỳ n&amp;uacute;t bấm tiện dụng n&amp;agrave;o, thay v&amp;agrave;o đ&amp;oacute; chỉ c&amp;oacute; một v&amp;ograve;ng xoay điều chỉnh chế độ Zoom nằm ở gần mặt trước. S&amp;aacute;t về ph&amp;iacute;a đu&amp;ocirc;i của m&amp;aacute;y l&amp;agrave; phần thu nhận t&amp;iacute;n hiệu m&amp;agrave;u đen, đ&amp;acirc;y l&amp;agrave; một điểm nhấn khiến cho sản phẩm trở n&amp;ecirc;n đẹp hơn v&amp;agrave; bớt đi sự đơn điệu nếu như to&amp;agrave;n m&amp;agrave;u trắng. Hai b&amp;ecirc;n sườn HD33 l&amp;agrave; to&amp;agrave;n bộ phần lưới gi&amp;uacute;p tho&amp;aacute;t v&amp;agrave; tản nhiệt của th&amp;acirc;n m&amp;aacute;y. Khi hoạt động li&amp;ecirc;n tục, th&amp;acirc;n m&amp;aacute;y ph&amp;aacute;t ra lượng nhiệt lớn ở gần ph&amp;iacute;a đ&amp;egrave;n chiếu. Trong khi đ&amp;oacute;, quạt tạt nhiệt hoạt động kh&amp;aacute; &amp;ecirc;m &amp;aacute;i v&amp;agrave; kh&amp;ocirc;ng g&amp;acirc;y ra tiếng ồn qu&amp;aacute; lớn.&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img alt=&quot;&quot; src=&quot;http://l.f5.img.vnexpress.net/2012/04/10/1000037709_Optoma-HD33-8_480x0.jpg&quot; style=&quot;width: 480px; height: 319px; &quot; /&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Optoma HD33 c&amp;oacute; k&amp;iacute;ch thước kh&amp;aacute; lớn v&amp;agrave; đồ sộ, gấp rưỡi nếu so với đ&amp;agrave;n em như HD66 v&amp;agrave; HD67. K&amp;iacute;ch thước cụ thể của m&amp;aacute;y l&amp;agrave; 38,9 x 31,9 x 12,9 cm. Trọng lượng của m&amp;aacute;y cũng nặng gần gấp đ&amp;ocirc;i khi l&amp;ecirc;n tới 4,5 Kg. Tuy nhi&amp;ecirc;n về tổng thế, Optoma HD33 l&amp;agrave; một model c&amp;oacute; thiết kế đẹp, c&amp;oacute; sự kh&amp;aacute;c biệt v&amp;agrave; mang lại nhiều hấp dẫn nếu x&amp;eacute;t trong số tất cả sản phẩm m&amp;agrave; Optoma hiện c&amp;oacute;.&lt;/p&gt;\r\n	&lt;p&gt;\r\n		To&amp;agrave;n bộ hệ thống cổng kết nối được đặt ở ph&amp;iacute;a mặt sau. Ngo&amp;agrave;i 2 cổng HDMI 1.4 đ&amp;aacute;ng gi&amp;aacute;, Optoma HD33 c&amp;ograve;n sở hữu đầy đủ c&amp;aacute;c cổng t&amp;iacute;n hiệu cần thiết cho nhu cầu chiếu phim HD 3D từ VGA cho tới Component v&amp;agrave; S-video. N&amp;uacute;t nguồn được đặt gọn gần ph&amp;iacute;a m&amp;eacute;p của m&amp;aacute;y v&amp;agrave; đi k&amp;egrave;m với ba n&amp;uacute;t th&amp;ocirc;ng b&amp;aacute;o trạng th&amp;aacute;i khi m&amp;aacute;y hoạt động. Kh&amp;ocirc;ng c&amp;oacute; loa t&amp;iacute;ch hợp như GT750 n&amp;ecirc;n HD33 cũng thiếu đi c&amp;aacute;c cổng xuất t&amp;iacute;n hiệu &amp;acirc;m thanh, hỗ trợ kết nối th&amp;ecirc;m với loa sub như đ&amp;agrave;n em.&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Th&amp;ocirc;ng số kỹ thuật:&lt;/p&gt;\r\n	&lt;table border=&quot;1&quot; cellpadding=&quot;2&quot; cellspacing=&quot;2&quot; cexpand=&quot;false&quot; visible=&quot;1&quot;&gt;\r\n		&lt;tbody&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#efefef&quot; colspan=&quot;2&quot;&gt;\r\n					Độ ph&amp;acirc;n giải&lt;/td&gt;\r\n				&lt;td colspan=&quot;2&quot;&gt;\r\n					&lt;p&gt;\r\n						Full HD (1920 x 1080pixel ) / Full 3D 720p&lt;/p&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#efefef&quot; colspan=&quot;2&quot;&gt;\r\n					Độ tương phản&lt;/td&gt;\r\n				&lt;td colspan=&quot;2&quot;&gt;\r\n					10.000:1&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#efefef&quot; colspan=&quot;2&quot;&gt;\r\n					Cường độ s&amp;aacute;ng&lt;/td&gt;\r\n				&lt;td colspan=&quot;2&quot;&gt;\r\n					1800 ANSI Lumen&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#efefef&quot; colspan=&quot;2&quot;&gt;\r\n					Tuổi thọ b&amp;oacute;ng đ&amp;egrave;n&lt;/td&gt;\r\n				&lt;td colspan=&quot;2&quot;&gt;\r\n					4.000 giờ sử dụng th&amp;ocirc;ng thường&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#efefef&quot; colspan=&quot;2&quot;&gt;\r\n					K&amp;iacute;ch thước h&amp;igrave;nh ảnh&lt;/td&gt;\r\n				&lt;td colspan=&quot;2&quot;&gt;\r\n					37.6 &amp;ndash; 301 inch (16:10)&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#efefef&quot; colspan=&quot;2&quot;&gt;\r\n					Throw Ratio&lt;/td&gt;\r\n				&lt;td colspan=&quot;2&quot;&gt;\r\n					1,5 - 1,8:1&amp;nbsp;&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#efefef&quot; colspan=&quot;2&quot;&gt;\r\n					Cổng kết nối&lt;/td&gt;\r\n				&lt;td colspan=&quot;2&quot;&gt;\r\n					2 HDMI 1.4a, 1 VGA in, 1 Composite Video, 01 S-Video, 1 RS232, mini USB, VESA 3D Port, IR Receiver&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#efefef&quot; colspan=&quot;2&quot;&gt;\r\n					Nguồn điện&lt;/td&gt;\r\n				&lt;td colspan=&quot;2&quot;&gt;\r\n					AC 100 ~ 240 V, 50/ 60Hz&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#efefef&quot; colspan=&quot;2&quot;&gt;\r\n					Tương th&amp;iacute;ch video&lt;/td&gt;\r\n				&lt;td colspan=&quot;2&quot;&gt;\r\n					3D/2D 1080p, 1080i, 720p, PAL, NTSC, SECAM&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#efefef&quot; colspan=&quot;2&quot;&gt;\r\n					Độ ồn&lt;/td&gt;\r\n				&lt;td colspan=&quot;2&quot;&gt;\r\n					25dB&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#efefef&quot; colspan=&quot;2&quot;&gt;\r\n					K&amp;iacute;ch thước/ Trọng lượng&lt;/td&gt;\r\n				&lt;td colspan=&quot;2&quot;&gt;\r\n					389 x319 x 129 mm/ 4,5kg&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n		&lt;/tbody&gt;\r\n	&lt;/table&gt;\r\n	&lt;p&gt;\r\n		Phần 2: T&amp;iacute;nh năng v&amp;agrave; tr&amp;igrave;nh diễn h&amp;igrave;nh ảnh&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img alt=&quot;&quot; src=&quot;http://l.f5.img.vnexpress.net/2012/04/10/1000037709_HD33_480x0.jpg&quot; style=&quot;width: 480px; height: 266px; &quot; /&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Những trải nghiệm ban đầu cho thấy h&amp;igrave;nh ảnh ở chế độ 2D thể hiện rất sống động, độ b&amp;atilde;o h&amp;ograve;a m&amp;agrave;u vừa phải khiến h&amp;igrave;nh ảnh rực rỡ nhưng kh&amp;ocirc;ng rực. C&amp;aacute;c chi tiết c&amp;oacute; được sự sắc n&amp;eacute;t nhờ độ ph&amp;acirc;n giải cao. Độ tương phản đạt mức 10.000:1 l&amp;agrave; một trang bị tốt ở sản phẩm khi gi&amp;uacute;p cho h&amp;igrave;nh ảnh thể hiện c&amp;oacute; được độ s&amp;acirc;u. Khả năng thể hiện dải m&amp;agrave;u đen của HD33 tốt v&amp;agrave; mang được ưu điểm từng c&amp;oacute; tr&amp;ecirc;n người tiền nhiệm HD20. So với những đối thủ trong c&amp;ugrave;ng tầm gi&amp;aacute;, phần thể hiện m&amp;agrave;u đen của Optoma vượt trội hơn.&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Vẫn c&amp;ograve;n một ch&amp;uacute;t gợn khiến sản phẩm chưa thể ho&amp;agrave;n hảo ở khả năng thể hiện 2D khi m&amp;agrave;u sắc c&amp;oacute; hiện tượng nhiễu v&amp;agrave; chuyển m&amp;agrave;u kh&amp;ocirc;ng thực ở một v&amp;agrave;i trường hợp, c&amp;oacute; thể ph&amp;aacute;t hiện ra điều n&amp;agrave;y khi tr&amp;igrave;nh chiếu khung cảnh bầu trời xanh. Nhưng với mức gi&amp;aacute; v&amp;agrave; t&amp;iacute;nh năng ở HD33, điểm yếu tr&amp;ecirc;n ho&amp;agrave;n to&amp;agrave;n c&amp;oacute; thể chấp nhận được.&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Trang bị 3D Full HD với giao tiếp HDMI 1.4 l&amp;agrave; t&amp;iacute;nh năng quan trọng tr&amp;ecirc;n HD33, cho ph&amp;eacute;p m&amp;aacute;y vận h&amp;agrave;nh v&amp;agrave; tương th&amp;iacute;ch nhanh với nhiều nội dung v&amp;agrave; nguồn ph&amp;aacute;t 3D. Với bất kỳ định dạng 3D n&amp;agrave;o từ Side-by-side, Top-and-bottom cho tới Frame-underpack, người d&amp;ugrave;ng chỉ cần ph&amp;aacute;t l&amp;agrave; m&amp;aacute;y tự động nhận diện t&amp;iacute;n hiệu v&amp;agrave; chuyển sang chế độ 3D tương th&amp;iacute;ch. Kh&amp;ocirc;ng cần đến c&amp;aacute;c chương tr&amp;igrave;nh ph&amp;aacute;t 3D như Stereoscopic Player m&amp;agrave; chỉ cần c&amp;aacute;c ứng dụng th&amp;ocirc;ng thường như Media Player Classic hay Windows Media Player, người chơi vẫn c&amp;oacute; thể ph&amp;aacute;t nội dung 3D với m&amp;aacute;y chiếu.&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img alt=&quot;&quot; src=&quot;http://l.f5.img.vnexpress.net/2012/04/10/1000037709_IMG_8771_480x0.JPG&quot; /&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Một b&amp;agrave;i thử nghiệm nhỏ thực hiện với đoạn phim 3D IMAX:&amp;nbsp;The Ultimate Wave: Tahiti.&amp;nbsp;H&amp;igrave;nh ảnh thể hiện từ m&amp;aacute;y chiếu 3D của Optoma kh&amp;aacute; s&amp;aacute;ng, thậm ch&amp;iacute; l&amp;agrave; qu&amp;aacute; s&amp;aacute;ng v&amp;agrave; bởi vậy nhưng &amp;aacute;nh s&amp;aacute;ng dư thừa của ph&amp;ograve;ng chiếu cũng kh&amp;ocirc;ng g&amp;acirc;y ảnh hưởng nhiều tới chất lượng của h&amp;igrave;nh. Tương tự như phần thể hiện 2D, ở chế độ ba chiều h&amp;igrave;nh ảnh m&amp;agrave; người xem cảm nhận vẫn rất s&amp;acirc;u với dải m&amp;agrave;u đen thể hiện tốt. Ở chế độ tr&amp;igrave;nh chiếu h&amp;igrave;nh ảnh nổi, điểm mạnh ở HD33 l&amp;agrave; khả năng c&amp;acirc;n bằng giữa nguồn s&amp;aacute;ng v&amp;agrave; loại bỏ hiện tượng chồng h&amp;igrave;nh Crosstalk, cho thấy khả năng chiếu phim 3D kh&amp;aacute; chuy&amp;ecirc;n nghiệp v&amp;agrave; kh&amp;ocirc;ng thua k&amp;eacute;m c&amp;aacute;c model c&amp;oacute; gi&amp;aacute; b&amp;aacute;n đắt gấp đ&amp;ocirc;i.&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Với g&amp;oacute;c nh&amp;igrave;n trực diện, hiện tượng Crosstalk tr&amp;ecirc;n k&amp;iacute;nh chuy&amp;ecirc;n dụng dạng m&amp;agrave;n trập m&amp;agrave; Optoma cung cấp kh&amp;ocirc;ng xuất hiện. Chỉ khi thực hiện việc quay đầu ra khỏi m&amp;agrave;n h&amp;igrave;nh khoảng tr&amp;ecirc;o 70 độ, người đeo k&amp;iacute;nh mới thấy hiện tượng mắt k&amp;iacute;nh bị chớp tắt kh&amp;ocirc;ng b&amp;igrave;nh thường. Kh&amp;ocirc;ng sử dụng c&amp;ocirc;ng nghệ truyền t&amp;iacute;n hiệu hồng ngoại (IR) m&amp;agrave; thay v&amp;agrave;o đ&amp;oacute; l&amp;agrave; tần số s&amp;oacute;ng radio (RF) n&amp;ecirc;n phạm vi hoạt động của k&amp;iacute;nh chuy&amp;ecirc;n dụng l&amp;agrave; kh&amp;aacute; lớn, kh&amp;oacute; mất t&amp;iacute;n hiệu v&amp;agrave; thuận tiện cho người xem khi c&amp;oacute; khoảng hoạt động 10 m&amp;eacute;t xung quanh m&amp;aacute;y chiếu.&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Những thử nghiệm ở Optoma HD33 về c&amp;aacute;c th&amp;ocirc;ng số kỹ thuật cho thấy, ở chế độ thiết lập chiếu phim, Cinema, Optoma HD33 đạt độ s&amp;aacute;ng 847 Lumen khi đ&amp;egrave;n chiếu ở c&amp;ocirc;ng suất cực đại, một mức s&amp;aacute;ng ấn tượng. Chuyển sang chế độ thấp hơn, Standard, độ s&amp;aacute;ng của model thu về l&amp;agrave; 661 Lumen, một mức s&amp;aacute;ng vẫn rất thừa th&amp;atilde;i. Ở chế độ tối ưu cho khả năng thể hiện m&amp;agrave;u sắc một c&amp;aacute;ch ch&amp;iacute;nh x&amp;aacute;c, Neutral, độ s&amp;aacute;ng của m&amp;aacute;y đạt tới 929 Lumen v&amp;agrave; đạt tới hơn 1.000 Lumen nếu người d&amp;ugrave;ng chuyển sang chế độ xem ảnh.&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img alt=&quot;&quot; src=&quot;http://l.f5.img.vnexpress.net/2012/04/10/1000037709_IMG_8806_480x0.JPG&quot; /&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Một t&amp;iacute;nh năng hấp dẫn với người chơi HD ở HD33 l&amp;agrave; PureMotion, đ&amp;acirc;y l&amp;agrave; c&amp;ocirc;ng nghệ ch&amp;egrave;n h&amp;igrave;nh nội suy gi&amp;uacute;p tăng tần số qu&amp;eacute;t h&amp;igrave;nh v&amp;agrave; l&amp;agrave;m giảm hiện tượng giật h&amp;igrave;nh. PureMotion c&amp;oacute; tới ba chế độ v&amp;agrave; mang lại những t&amp;aacute;c dụng kh&amp;aacute;c nhau. Nếu như xem c&amp;aacute;c bộ phim c&amp;oacute; hiệu ứng đồ họa nhiều hay c&amp;oacute; tốc độ cao, người d&amp;ugrave;ng n&amp;ecirc;n thiết lập ở mức độ cao nhất c&amp;ograve;n nếu xem c&amp;aacute;c h&amp;igrave;nh ảnh như truyền h&amp;igrave;nh hay chương tr&amp;igrave;nh live, thiết lập ở PureMotion ở mức độ thấp sẽ mang lại trải nghiệm tốt hơn. Ngo&amp;agrave;i ra PureMotion cũng c&amp;oacute; thể hoạt động b&amp;igrave;nh thường ở chế độ 3D.&lt;/p&gt;\r\n	&lt;p style=&quot;text-align: center; &quot;&gt;\r\n		&lt;img alt=&quot;&quot; src=&quot;http://l.f5.img.vnexpress.net/2012/04/10/1000037709_Optoma-HD33-1_480x0.jpg&quot; style=&quot;width: 480px; height: 319px; &quot; /&gt;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		C&amp;oacute; khả năng thể hiện h&amp;igrave;nh ảnh tốt v&amp;agrave; được đ&amp;aacute;nh gi&amp;aacute; cao tuy nhi&amp;ecirc;n điểm yếu của Optoma HD33 lại nằm ở phần cứng của sản phẩm. Đ&amp;oacute; l&amp;agrave; việc model chỉ đi k&amp;egrave;m với ống k&amp;iacute;nh 1.2:1 v&amp;agrave; kh&amp;ocirc;ng c&amp;oacute; t&amp;iacute;nh năng Lens Shift. Ống k&amp;iacute;nh cố định với g&amp;oacute;c chiếu lệch t&amp;acirc;m 7% l&amp;ecirc;n ph&amp;iacute;a tr&amp;ecirc;n khiến cho sản phẩm n&amp;agrave;y ph&amp;ugrave; hợp hơn khi được đặt tr&amp;ecirc;n b&amp;agrave;n v&amp;agrave; việc lắp đặt dạng treo tường sẽ khiễn người d&amp;ugrave;ng phải xoay sở sao cho c&amp;oacute; một vị tr&amp;iacute; ph&amp;ugrave; hợp.&amp;nbsp;&lt;/p&gt;\r\n	&lt;p&gt;\r\n		Kh&amp;ocirc;ng giống như nhiều model anh em kh&amp;aacute;c, HD33 lại kh&amp;ocirc;ng c&amp;oacute; c&amp;aacute;c n&amp;uacute;t bấm t&amp;iacute;ch hợp nằm ở mặt lưng của m&amp;aacute;y v&amp;agrave; điều n&amp;agrave;y khiến cho người d&amp;ugrave;ng phụ thuộc nhiều v&amp;agrave;o điều khiển đi k&amp;egrave;m. Tuy vậy điểm đ&amp;aacute;ng khen ngợi cuối c&amp;ugrave;ng ở sản phẩm 3D Full HD của Optoma l&amp;agrave; điểu khiển đi k&amp;egrave;m nhỏ gọn nhưng c&amp;oacute; đ&amp;egrave;n nền, rất thuận tiện cho việc sử dụng với nhu cầu chơi HD.&lt;/p&gt;\r\n	&lt;table border=&quot;0&quot; cellpadding=&quot;3&quot; cellspacing=&quot;0&quot; cexpand=&quot;false&quot; visible=&quot;1&quot;&gt;\r\n		&lt;tbody&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#c0c0c0&quot; style=&quot;text-align: justify; &quot;&gt;\r\n					Ưu điểm&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#efefef&quot;&gt;\r\n					&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n						- Chất lượng h&amp;igrave;nh ảnh tốt&lt;br /&gt;\r\n						- Hỗ trợ 3D Full HD với HDMI 1.4a&lt;br /&gt;\r\n						- Điều khiển c&amp;oacute; đ&amp;egrave;n nền Backlight&lt;br /&gt;\r\n						- Gi&amp;aacute; hấp dẫn&lt;/p&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#c0c0c0&quot; style=&quot;text-align: justify; &quot;&gt;\r\n					Nhược điểm&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n			&lt;tr&gt;\r\n				&lt;td bgcolor=&quot;#efefef&quot;&gt;\r\n					&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n						- Kh&amp;ocirc;ng c&amp;oacute; n&amp;uacute;t bấm điều khiển tr&amp;ecirc;n th&amp;acirc;n m&amp;aacute;y&lt;br /&gt;\r\n						- Ống k&amp;iacute;nh kh&amp;ocirc;ng c&amp;oacute; chế độ Lens Shift.&lt;/p&gt;\r\n				&lt;/td&gt;\r\n			&lt;/tr&gt;\r\n		&lt;/tbody&gt;\r\n	&lt;/table&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;', '', '', '', '', '', '', '', 0, 0, 'upload/1000037709_Optoma-HD33-14_480x0.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 17:58:48', 'admin', '2012-10-10 17:58:48');
INSERT INTO `media` VALUES (69, 'default1349863432', '', 'media_news', '[6],', 'admin', 'LG ra mắt loạt Smart TV 3D 2012 ở VN', '', '', 'vn', '&lt;p&gt;\r\n	LG cho biết h&amp;atilde;ng đ&amp;atilde; c&amp;oacute; sự cải tiến về thiết kế để cạnh tranh với c&amp;aacute;c đối thủ, đặc biệt l&amp;agrave; Samsung. Hai h&amp;atilde;ng đến từ xứ sở kim chi n&amp;agrave;y đ&amp;atilde; bắt đầu chạy đua tr&amp;ecirc;n thị trường TV ngay từ đầu năm nay khi cả hai đều tr&amp;igrave;nh diễn những sản phẩm TV đặc biệt nhất của&amp;nbsp;m&amp;igrave;nh tại Triển l&amp;atilde;m CES 2012.&lt;/p&gt;', '&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	LG cho biết h&amp;atilde;ng đ&amp;atilde; c&amp;oacute; sự cải tiến về thiết kế để cạnh tranh với c&amp;aacute;c đối thủ, đặc biệt l&amp;agrave; Samsung. Hai h&amp;atilde;ng đến từ xứ sở kim chi n&amp;agrave;y đ&amp;atilde; bắt đầu chạy đua tr&amp;ecirc;n thị trường TV ngay từ đầu năm nay khi cả hai đều tr&amp;igrave;nh diễn những sản phẩm TV đặc biệt nhất của&amp;nbsp;m&amp;igrave;nh tại Triển l&amp;atilde;m CES 2012.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Ngay sau khi Samsung đưa Smart TV thế hệ mới về Việt Nam, đối thủ LG cũng tr&amp;igrave;nh diện loạt sản phẩm mới của m&amp;igrave;nh. Cinema 3D Smart TV 2012 của LG c&amp;oacute; m&amp;agrave;n h&amp;igrave;nh rộng (Cinema Screen) với đường viền mỏng hơn nhiều so với thế hệ TV cũ của h&amp;atilde;ng n&amp;agrave;y.&lt;/p&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;LG ra mắt loạt Cinema 3D Smart TV thế hệ 2012&quot; src=&quot;http://dantri4.vcmedia.vn/2ZQdZNVEKFCFuBcccccc/Image/2012/04/04a/LGCine1_ad11e.jpg&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	LG ra&amp;nbsp;mắt TV thế hệ&amp;nbsp;2012.&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;br /&gt;\r\n	CINEMA 3D Smart TV tiếp tục sử dụng c&amp;ocirc;ng nghệ 3D FPR của LG, dựa tr&amp;ecirc;n việc t&amp;aacute;i tạo h&amp;igrave;nh ảnh 3D qua phương thức thụ động, người xem sẽ sử dụng k&amp;iacute;nh ph&amp;acirc;n cực để thấy được c&amp;aacute;c h&amp;igrave;nh ảnh nổi. C&amp;ocirc;ng nghệ n&amp;agrave;y gi&amp;uacute;p mang tới cho người xem hiệu ứng 3D tốt hơn, g&amp;oacute;c nh&amp;igrave;n rộng hơn (l&amp;ecirc;n tới 178 độ) v&amp;agrave; loại bỏ ho&amp;agrave;n to&amp;agrave;n sự rung nho&amp;egrave; c&amp;ugrave;ng sọc nhiễu.&lt;/div&gt;\r\n&lt;p&gt;\r\n	Ngo&amp;agrave;i ra, thế hệ CINEMA 3D Smart TV mới của LG cũng được t&amp;iacute;ch hợp c&amp;ocirc;ng nghệ t&amp;ugrave;y chỉnh độ s&amp;acirc;u hiệu ứng 3D gi&amp;uacute;p người d&amp;ugrave;ng ho&amp;agrave;n to&amp;agrave;n kiểm so&amp;aacute;t hiệu ứng h&amp;igrave;nh ảnh 3D theo &amp;yacute; muốn nhằm đem lại một trải nghiệm giải tr&amp;iacute; 3D ấn tượng.&lt;/p&gt;\r\n&lt;p&gt;\r\n	LG cũng giới thiệu cặp k&amp;iacute;nh 3D ph&amp;acirc;n cực nhẹ (khoảng 16gram). H&amp;atilde;ng n&amp;agrave;y cho biết loại k&amp;iacute;nh mới kh&amp;ocirc;ng k&amp;egrave;m theo pin, kh&amp;ocirc;ng c&amp;oacute; s&amp;oacute;ng điện từ v&amp;agrave; c&amp;oacute; gi&amp;aacute; th&amp;agrave;nh hợp l&amp;yacute; (khoảng 200 ng&amp;agrave;n đồng). Cặp k&amp;iacute;nh 3D n&amp;agrave;y gi&amp;uacute;p g&amp;oacute;c nh&amp;igrave;n mở rộng tới 178 độ, cho ph&amp;eacute;p tư thế xem linh hoạt.&lt;/p&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&amp;nbsp;&lt;img alt=&quot;LG ra mắt loạt Cinema 3D Smart TV thế hệ 2012&quot; src=&quot;http://dantri4.vcmedia.vn/2ZQdZNVEKFCFuBcccccc/Image/2012/04/04a/LGCine3_17f93.jpg&quot; /&gt;&lt;/div&gt;\r\n&lt;div&gt;\r\n	Nhiều kh&amp;aacute;ch tham dự tỏ ra ấn tượng khả năng hiển thị h&amp;igrave;nh ảnh 3D của TV mới của LG.&lt;br /&gt;\r\n	N&amp;eacute;t ấn tượng của CINEMA 3D Smart TV l&amp;agrave; c&amp;oacute; khả năng khuyếch đại &amp;acirc;m thanh đa chiều 3D Sound Zooming.&lt;/div&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;text-align: justify; &quot;&gt;CINEMA 3D Smart TV được t&amp;iacute;ch hợp chip l&amp;otilde;i k&amp;eacute;p tốc độ 2GHz, cho ph&amp;eacute;p người&amp;nbsp;d&amp;ugrave;ng truy cập Internet tốc độ cao để lướt web, thoại video Skype hay sử dụng&amp;nbsp;&amp;nbsp;mạng x&amp;atilde; hội. Ngo&amp;agrave;i ra, người d&amp;ugrave;ng cũng c&amp;oacute; thể chia sẻ c&amp;aacute;c nội dung v&amp;agrave; dữ liệu tr&amp;ecirc;n network (DNLA&lt;/span&gt;&lt;span style=&quot;text-align: justify; &quot;&gt;) hay đồng bộ thưởng thức với smartphone, m&amp;aacute;y t&amp;iacute;nh bảng qua Mobile HD Link v&amp;agrave; Wifi Screen Share.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	TV th&amp;ocirc;ng minh thế hệ mới n&amp;agrave;y được b&amp;aacute;n k&amp;egrave;m với bộ điều khiển LG Magic Remote Control với m&amp;agrave;n h&amp;igrave;nh cảm ứng, cho ph&amp;eacute;p người d&amp;ugrave;ng sử dụng điều khiển từ xa bằng h&amp;agrave;nh động, thay v&amp;igrave; phải nhấn n&amp;uacute;t như trước đ&amp;acirc;y.&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;LG ra mắt loạt Cinema 3D Smart TV thế hệ 2012&quot; src=&quot;http://dantri4.vcmedia.vn/2ZQdZNVEKFCFuBcccccc/Image/2012/04/04a/LGLM1_45b48.jpg&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	LM9600 m&amp;agrave;n h&amp;igrave;nh 55 inch l&amp;agrave; d&amp;ograve;ng TV cao cấp nhất được LG ra mắt ng&amp;agrave;y h&amp;ocirc;m qua.&lt;br /&gt;\r\n	&lt;br /&gt;\r\n	&lt;img alt=&quot;LG ra mắt loạt Cinema 3D Smart TV thế hệ 2012&quot; src=&quot;http://dantri4.vcmedia.vn/2ZQdZNVEKFCFuBcccccc/Image/2012/04/04a/LGLM2_f3c34.jpg&quot; /&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	D&amp;ograve;ng LM7600 c&amp;oacute; c&amp;aacute;c k&amp;iacute;ch thước m&amp;agrave;n h&amp;igrave;nh 55 inch, 47 inch v&amp;agrave; 42 inch.&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Tại lễ ra mắt TV thế hệ mới, LG cũng c&amp;ocirc;ng bố hợp t&amp;aacute;c ch&amp;iacute;nh thức với 5 nh&amp;agrave; cung cấp nội dung trực tuyến gồm c&amp;ocirc;ng ty CP Truyền th&amp;ocirc;ng Việt Nam (VCCorp), VDC, Peacesoft, Bongda.com, PMSV (C&amp;ocirc;ng ty CP phần mềm sinh vi&amp;ecirc;n học sinh).&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	CINEMA 3D Smart TV ra mắt lần n&amp;agrave;y gồm c&amp;oacute; 2 model: LM9600 (55 inch) v&amp;agrave; LM7600 (55 inch, 47 inch, 42 inch), với mức gi&amp;aacute; dự kiến từ 20-50 triệu đồng.&lt;/div&gt;', 'lg-ra-mat-loat-smart-tv-3d-2012-o-vn', '', '', '', '', '', '', 0, 0, 'upload/1000038219_LG-Cinema-3D-2012-1_480x0.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 18:03:52', 'admin', '2012-10-10 18:03:52');
INSERT INTO `media` VALUES (70, 'default1349863581', '', 'media_news', '[6],', 'admin', 'Dolby gây ấn tượng với công nghệ 3D không kính mới', '', '', 'vn', '&lt;p&gt;\r\n	The Verge chia sẻ cảm nhận sau khi d&amp;ugrave;ng thử hệ thống 3D c&amp;oacute; chuẩn mới của Dolby v&amp;agrave; Philips rằng, h&amp;igrave;nh ảnh thể hiện rất sống động v&amp;agrave; n&amp;eacute;t, cuốn h&amp;uacute;t khiến người xem kh&amp;ocirc;ng nhận thấy l&amp;agrave; m&amp;igrave;nh đang xem một đoạn phim 3D.&lt;/p&gt;', '&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div&gt;\r\n	&lt;p&gt;\r\n		The Verge chia sẻ cảm nhận sau khi d&amp;ugrave;ng thử hệ thống 3D c&amp;oacute; chuẩn mới của Dolby v&amp;agrave; Philips rằng, h&amp;igrave;nh ảnh thể hiện rất sống động v&amp;agrave; n&amp;eacute;t, cuốn h&amp;uacute;t khiến người xem kh&amp;ocirc;ng nhận thấy l&amp;agrave; m&amp;igrave;nh đang xem một đoạn phim 3D. D&amp;ugrave; thưởng thức ở nhiều g&amp;oacute;c nh&amp;igrave;n kh&amp;aacute;c nhau nhưng h&amp;igrave;nh ảnh vấn ấn tượng, thậm ch&amp;iacute; người xem đứng ở c&amp;aacute;c g&amp;oacute;c nh&amp;igrave;n hạn chế như từ xa hay hai b&amp;ecirc;n m&amp;agrave;n h&amp;igrave;nh th&amp;igrave; c&amp;aacute;c hiệu ứng vẫn thể hiện kh&amp;aacute; r&amp;otilde; r&amp;agrave;ng. C&amp;ocirc;ng nghệ 3D của Philips v&amp;agrave; Dolby c&amp;oacute; một tầm cao mới so với 3D kh&amp;ocirc;ng k&amp;iacute;nh hiện tại v&amp;agrave; ho&amp;agrave;n to&amp;agrave;n c&amp;oacute; thể thương mại h&amp;oacute;a sớm được, trang c&amp;ocirc;ng nghệ tr&amp;ecirc;n chia sẻ.&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	Trước khi được giới thiệu tại triển l&amp;atilde;m NAB 2012, Dolby v&amp;agrave; Philips đ&amp;atilde; c&amp;ocirc;ng bố việc đang ph&amp;aacute;t triển một chuẩn 3D thế hệ mới. Nhưng phải tới sự kiện n&amp;agrave;y, cả hai mới tiết lộ chi tiết về sản phẩm 3D hợp t&amp;aacute;c giữa hai b&amp;ecirc;n. C&amp;ocirc;ng nghệ 3D mới của Dolby cho ph&amp;eacute;p ph&amp;aacute;t h&amp;igrave;nh ảnh nổi kh&amp;ocirc;ng cần k&amp;iacute;nh với 26 g&amp;oacute;c nh&amp;igrave;n kh&amp;aacute;c nhau, độ ph&amp;acirc;n giải l&amp;ecirc;n tới 4K gấp 4 lần chuẩn Full HD th&amp;ocirc;ng thường.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Theo Flatpanels HD, c&amp;oacute; thể v&amp;agrave;o m&amp;ugrave;a thu năm nay, Philips sẽ cho ra mắt mẫu TV 3D kh&amp;ocirc;ng cần k&amp;iacute;nh đầu ti&amp;ecirc;n sở hữu c&amp;ocirc;ng nghệ 3D thế hệ mới của Dolby.&lt;/p&gt;', 'dolby-gay-an-tuong-voi-cong-nghe-3d-khong-kinh-moi-1', '', '', '', '', '', '', 0, 0, 'upload/0011317.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 18:06:21', 'admin', '2012-10-10 18:06:21');
INSERT INTO `media` VALUES (71, 'default1349863770', '', 'media_news', '[6],', 'admin', 'Phiên bản 3D giúp Titanic phá vỡ kỷ lục về doanh số', '', '', 'vn', '&lt;p&gt;\r\n	&lt;span style=&quot;text-align: justify; &quot;&gt;Một lần nữa, c&amp;ocirc;ng nghệ 3D đ&amp;atilde; gi&amp;uacute;p cho bộ phim của James Cameron lừng danh đi v&amp;agrave;o huyền thoại.&amp;nbsp;&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Một lần nữa, c&amp;ocirc;ng nghệ 3D đ&amp;atilde; gi&amp;uacute;p cho bộ phim của James Cameron lừng danh đi v&amp;agrave;o huyền thoại.&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;images/autosize-800x800/upload/titanic.jpg&quot; style=&quot;height: 1039px; width: 700px; &quot; /&gt;&lt;br /&gt;\r\n	Theo th&amp;ocirc;ng tin vừa mới nhận được, v&amp;agrave;o cuối tuần qua, phi&amp;ecirc;n bản 3D của si&amp;ecirc;u phẩm Titanic đ&amp;atilde; ch&amp;iacute;nh thức c&amp;aacute;n mốc doanh thu 190,8 triệu USD tr&amp;ecirc;n to&amp;agrave;n cầu. Điều đ&amp;aacute;ng buồn l&amp;agrave; thị trường Hoa Kỳ chỉ thu về được c&amp;oacute; 11,6 triệu USD, trong khi đ&amp;oacute; thị trường Trung Quốc lại đ&amp;oacute;ng g&amp;oacute;p tới 58 triệu USD v&amp;agrave; 69 nước được ph&amp;aacute;t h&amp;agrave;nh c&amp;ograve;n lại đ&amp;oacute;ng g&amp;oacute;p 88,2 triệu USD.&amp;nbsp;&lt;br /&gt;\r\n	Điều n&amp;agrave;y đồng nghĩa với tổng doanh thu của phim Titanic đ&amp;atilde; vượt ngưỡng 2 tỷ USD, trở th&amp;agrave;nh bộ phim thứ 2 sau Avatar (2,8 tỷ USD) l&amp;agrave;m được điều n&amp;agrave;y.&lt;br /&gt;\r\n	V&amp;agrave;... như bạn đ&amp;atilde; biết rồi đ&amp;oacute;, cả hai bộ phim c&amp;oacute; danh thu h&amp;agrave;ng đầu đều được đạo diễn bởi James Cameron.&amp;nbsp;&lt;br /&gt;\r\n	Liệu khi n&amp;agrave;o th&amp;igrave; ng&amp;agrave;i &amp;quot;vua ph&amp;ograve;ng v&amp;eacute;&amp;quot; mới chịu dừng lại đ&amp;acirc;y? Avarta 2 ư? chắc chắn l&amp;agrave; kh&amp;ocirc;ng phải rồi.&lt;/p&gt;', 'phien-ban-3d-giup-titanic-pha-vo-ky-luc-ve-doanh-so-1', '', '', '', '', '', '', 0, 0, 'upload/titanic_3d-HD.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 18:11:41', 'admin', '2012-10-10 18:11:41');
INSERT INTO `media` VALUES (72, 'default1349864310', '', 'media_news', '[6],', 'admin', 'Nhu cầu mua TV 3D tăng cao', '', '', 'vn', '&lt;p&gt;\r\n	Những b&amp;aacute;o c&amp;aacute;o c&amp;aacute;o về thị trường gần đ&amp;acirc;y cho thấy nhu cầu mua TV 3D trong năm 2012 c&amp;oacute; thể tăng tới 122%, trong khi Smart TV chỉ 56%.&lt;/p&gt;', '&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Những b&amp;aacute;o c&amp;aacute;o c&amp;aacute;o về thị trường gần đ&amp;acirc;y cho thấy nhu cầu mua TV 3D trong năm 2012 c&amp;oacute; thể tăng tới 122%, trong khi Smart TV chỉ 56%.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://l.f5.img.vnexpress.net/2012/05/09/1000038449_TV-3D_480x0.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Tại họp b&amp;aacute;o chuẩn bị cho triển l&amp;atilde;m IFA 2012, đại diện của c&amp;ocirc;ng ty nghi&amp;ecirc;n cứu thị trường Gfk cho biết, TV 3D đang c&amp;oacute; đ&amp;agrave; tăng trưởng mạnh trong năm nay. Trong đ&amp;oacute; nhu cầu mua TV 3D hiện tại trong năm 2012 tăng 122% so với năm ngo&amp;aacute;i, trong khi với Smart TV chi l&amp;agrave; 56%. Người d&amp;ugrave;ng cũng cho biết, họ kh&amp;ocirc;ng quan t&amp;acirc;m nhiều đến những t&amp;iacute;nh năng th&amp;ocirc;ng minh nhưng lại d&amp;agrave;nh ra sự th&amp;iacute;ch th&amp;uacute; đặc biệt với việc thưởng thức c&amp;aacute;c h&amp;igrave;nh ảnh nổi tr&amp;ecirc;n TV.&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Cũng theo những b&amp;aacute;o c&amp;aacute;o mới, ch&amp;acirc;u &amp;Acirc;u đang l&amp;agrave; thị trường c&amp;oacute; mức độ tăng trưởng về 3D thuộc h&amp;agrave;ng cao nhất hiện nay v&amp;agrave; c&amp;oacute; thể cạnh tranh với Mỹ, thị trường TV lớn nhất thế giới. Điển h&amp;igrave;nh c&amp;oacute; thể kể đến Thụy Sỹ khi lập kỷ lục, 42,3% lượng TV b&amp;aacute;n ra trong th&amp;aacute;ng 1/2012 l&amp;agrave; c&amp;aacute;c model hỗ trợ 3D.&lt;/p&gt;', 'nhu-cau-mua-tv-3d-tang-cao-1', '', '', '', '', '', '', 0, 0, 'upload/1000038449_TV-3D_480x0.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 18:18:30', 'admin', '2012-10-10 18:18:30');
INSERT INTO `media` VALUES (73, 'default1349864857', '', 'media_news', '[6],', 'admin', 'Tại sao người Việt Nam lại dùng Yahoo Messenger?', '', '', 'vn', '&lt;p&gt;\r\n	&lt;span style=&quot;text-align: justify; &quot;&gt;Đ&amp;acirc;y kh&amp;ocirc;ng phải l&amp;agrave; một dạng c&amp;acirc;u hỏi, m&amp;agrave; l&amp;agrave; một lời khẳng định th&amp;igrave; đ&amp;uacute;ng hơn. T&amp;ocirc;i c&amp;ograve;n nhớ lần sử dụng internet đầu ti&amp;ecirc;n v&amp;agrave;o năm 2002&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Đ&amp;acirc;y kh&amp;ocirc;ng phải l&amp;agrave; một dạng c&amp;acirc;u hỏi, m&amp;agrave; l&amp;agrave; một lời khẳng định th&amp;igrave; đ&amp;uacute;ng hơn. T&amp;ocirc;i c&amp;ograve;n nhớ lần sử dụng internet đầu ti&amp;ecirc;n v&amp;agrave;o năm 2002 - l&amp;uacute;c đ&amp;oacute; kh&amp;aacute;i niệm internet đối với t&amp;ocirc;i cực kỳ mơ hồ v&amp;agrave; chỉ l&amp;agrave; hai chữ: Yahooo Messenger. Bạn b&amp;egrave; cấp 2 của t&amp;ocirc;i sử dụng Yahoo, bạn b&amp;egrave; cấp 3 của t&amp;ocirc;i sử dụng Yahoo, bạn b&amp;egrave; đại học của t&amp;ocirc;i d&amp;ugrave;ng Yahoo v&amp;agrave; thậm ch&amp;iacute; l&amp;agrave; thầy c&amp;ocirc; t&amp;ocirc;i cũng Yahoo. C&amp;oacute; thể gọi c&amp;aacute;i thứ internet qu&amp;aacute;i quỷ đầu ti&amp;ecirc;n trong đời t&amp;ocirc;i l&amp;agrave; chủ nghĩa &amp;ldquo;Dạ H&amp;uacute;...u...u&amp;rdquo;.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.hdvietnam.com/diendan/members/6497-albums25673-picture59790.jpg&quot; /&gt;&lt;br /&gt;\r\n	L&amp;uacute;c đ&amp;oacute;, giả sử đ&amp;atilde; c&amp;oacute; Facebook, Gmail v&amp;agrave; Skype th&amp;igrave; chắc chắn t&amp;ocirc;i sẽ chẳng phải nếm thử c&amp;aacute;i m&amp;oacute;n &amp;ldquo;Dạ H&amp;uacute;...u....&amp;rdquo; đơn điệu đến b&amp;acirc;y giờ.&amp;nbsp;&lt;br /&gt;\r\n	T&amp;ocirc;i nghĩ rằng,&amp;nbsp;nguy&amp;ecirc;n nh&amp;acirc;n đầu ti&amp;ecirc;n&amp;nbsp;khiến cho hầu hết người d&amp;ugrave;ng internet Việt Nam đ&amp;atilde; v&amp;agrave; đang tiếp tục sử dụng Yahoo ch&amp;iacute;nh l&amp;agrave; th&amp;oacute;i quen. Th&amp;oacute;i quen đối với người Việt rất kh&amp;oacute; bỏ, đặc biệt khi m&amp;agrave; th&amp;oacute;i quen đ&amp;oacute; được nu&amp;ocirc;i dưỡng bởi t&amp;iacute;nh cộng đồng cực kỳ cao: những người xung quanh bạn sử dụng Yahoo Messenger v&amp;agrave; chắc chắn bạn sẽ kh&amp;ocirc;ng c&amp;oacute; lựa chọn n&amp;agrave;o kh&amp;aacute;c ngo&amp;agrave;i Yahoo Messenger. Dường như trong một thời gian d&amp;agrave;i, người Việt nghĩ về việc li&amp;ecirc;n lạc qua mạng ch&amp;iacute;nh l&amp;agrave; sử dụng Yahoo, v&amp;agrave; kh&amp;ocirc;ng c&amp;oacute; bất cứ thứ g&amp;igrave; kh&amp;aacute;c c&amp;oacute; thể thay thế.&lt;br /&gt;\r\n	Yếu tố thứ 2, cũng l&amp;agrave; một dạng chất dinh dưỡng đặc biệt để nu&amp;ocirc;i dưỡng c&amp;aacute;i chủ nghĩa Yahoo ở Việt Nam, đ&amp;oacute; l&amp;agrave; mặt cười.&lt;br /&gt;\r\n	Nghe th&amp;igrave; c&amp;oacute; vẻ h&amp;agrave;i hước, nhưng phải thừa nhận rằng emoticon của Yahoo c&amp;oacute; một sức h&amp;uacute;t đặc biệt, khiến cho nhiều người kh&amp;oacute; từ bỏ. C&amp;ograve;n nhớ c&amp;oacute; một khoảng thời gian rất d&amp;agrave;i t&amp;ocirc;i đ&amp;atilde; phải đảo lộn đống tạp ch&amp;iacute; Echip v&amp;agrave; cả Google nữa chỉ để t&amp;igrave;m một phần mềm n&amp;agrave;o đ&amp;oacute; hỗ trợ chat Yahoo lẫn emotion của n&amp;oacute;. Tuy nhi&amp;ecirc;n thật v&amp;ocirc; vọng khi kh&amp;ocirc;ng c&amp;oacute; c&amp;aacute;i t&amp;ecirc;n n&amp;agrave;o thực hiện được, v&amp;agrave; t&amp;ocirc;i lại phải quay về với Yahoo Messenger chỉ để nh&amp;igrave;n c&amp;aacute;i mặt cười dễ thương từ bạn b&amp;egrave;.&lt;br /&gt;\r\n	Sự thật l&amp;agrave; emoticon của Yahoo được coi l&amp;agrave; &amp;ldquo;đồng tiền chung&amp;rdquo; cho biểu tượng cảm x&amp;uacute;c của hầu hết c&amp;aacute;c cộng đồng internet Việt Nam. V&amp;agrave; kh&amp;ocirc;ng đi ở đ&amp;acirc;u xa, HDvietnam của ch&amp;uacute;ng ta hiện cũng đang d&amp;ugrave;ng n&amp;oacute; để l&amp;agrave;m biểu tượng cảm x&amp;uacute;c mặc định.&lt;br /&gt;\r\n	N&amp;oacute;i về emoticon của Yahoo, nhiều bạn sẽ rất bất ngờ khi t&amp;ocirc;i n&amp;oacute;i rằng đ&amp;acirc;y l&amp;agrave; một sản phẩm của một người Việt Nam, đ&amp;oacute; l&amp;agrave; họa sĩ Nguyễn Văn Cường.&amp;nbsp;&lt;br /&gt;\r\n	Như vậy, 2 thứ chất dinh dưỡng duy nhất đang nu&amp;ocirc;i sống một Yahoo Messenger thoi th&amp;oacute;p ở Việt Nam ch&amp;iacute;nh l&amp;agrave; th&amp;oacute;i quen v&amp;agrave; emoticon. C&amp;ograve;n nếu phải t&amp;igrave;m ra một l&amp;yacute; do cao cấp hơn, c&amp;oacute; lẽ t&amp;ocirc;i nghĩ rằng gần như kh&amp;ocirc;ng c&amp;oacute;.&lt;br /&gt;\r\n	Hiện nay, điều tuyệt vời nhất v&amp;agrave; phổ biến nhất của internet đ&amp;oacute; l&amp;agrave; kết nối tất cả mọi người lại với nhau. Nếu bạn đang muốn t&amp;igrave;m kiếm bạn b&amp;egrave; để t&amp;aacute;n gẫu th&amp;igrave; Facebook sẽ l&amp;agrave; một lựa chọn tuyệt vời. Nếu bạn đang c&amp;oacute; &amp;yacute; định kinh doanh th&amp;igrave; Facebook, Gmail v&amp;agrave; Skype l&amp;agrave; những c&amp;ocirc;ng cụ v&amp;ocirc; c&amp;ugrave;ng hiệu quả. Đ&amp;oacute; l&amp;agrave; chưa kể tới sự phổ biến của h&amp;agrave;ng ng&amp;agrave;n diễn đ&amp;agrave;n, nơi bạn c&amp;oacute; thể trao đổi mọi thứ, mọi chủ đề m&amp;agrave; bạn quan t&amp;acirc;m...&amp;nbsp;&lt;br /&gt;\r\n	Sự thật l&amp;agrave; thỉnh thoảng t&amp;ocirc;i vẫn đăng nhập v&amp;agrave;o Yahoo Messenger (với giao diện hơn 10 năm gần như kh&amp;ocirc;ng thay đổi), nhưng đ&amp;oacute; chỉ l&amp;agrave; sự tiếc nuối về những kỷ niệm - m&amp;agrave; kỷ niệm th&amp;igrave; lu&amp;ocirc;n đẹp v&amp;agrave; xứng đ&amp;aacute;ng để ch&amp;uacute;ng ta đưa v&amp;agrave;o k&amp;yacute; ức.&lt;br /&gt;\r\n	Cuộc sống l&amp;agrave; vậy, những c&amp;aacute;i g&amp;igrave; lu&amp;ocirc;n đổi thay sẽ chiến thắng. C&amp;ograve;n những người chỉ biết ngủ tr&amp;ecirc;n chiến thắng th&amp;igrave; sẽ phải thất bại. V&amp;agrave; ngay h&amp;ocirc;m nay, t&amp;ocirc;i đ&amp;atilde; phải tạm biệt Yahoo Messenger - giống như t&amp;ocirc;i đ&amp;atilde; từng tạm biệt với Yahoo 360!&lt;/p&gt;', 'tai-sao-nguoi-viet-nam-lai-dung-yahoo-messenger', '', '', '', '', '', '', 0, 0, 'upload/11544193_yqp1331453467 (1).jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 18:27:37', 'admin', '2012-10-10 18:27:37');
INSERT INTO `media` VALUES (75, 'default1349865077', '', 'media_news', '[6],', 'admin', 'Cái nhìn đầu tiên về TV 3D không kính của Sony', '', '', 'vn', '&lt;p&gt;\r\n	&lt;span style=&quot;text-align: justify; &quot;&gt;Bạn muốn một c&amp;aacute;i TV 3D kh&amp;ocirc;ng cần sử dụng k&amp;iacute;nh của một h&amp;atilde;ng nỗi tiếng? Sony hứa hẹn sẽ mang lại điều đ&amp;oacute;. Dưới đ&amp;acirc;y l&amp;agrave; c&amp;aacute;i nh&amp;igrave;n đầu ti&amp;ecirc;n về TV 3D kh&amp;ocirc;ng k&amp;iacute;nh của Sony.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Bạn muốn một c&amp;aacute;i TV 3D kh&amp;ocirc;ng cần sử dụng k&amp;iacute;nh của một h&amp;atilde;ng nỗi tiếng? Sony hứa hẹn sẽ mang lại điều đ&amp;oacute;. Dưới đ&amp;acirc;y l&amp;agrave; c&amp;aacute;i nh&amp;igrave;n đầu ti&amp;ecirc;n về TV 3D kh&amp;ocirc;ng k&amp;iacute;nh của Sony.&lt;br /&gt;\r\n	Samsung đ&amp;atilde; đặt một ch&amp;acirc;n v&amp;agrave;o kh&amp;aacute;i niệm n&amp;agrave;y (TV 3D kh&amp;ocirc;ng k&amp;iacute;nh) c&amp;aacute;ch đ&amp;acirc;y 2 năm, khi họ trưng b&amp;agrave;y TV 3D kh&amp;ocirc;ng k&amp;iacute;nh tại một cuộc triển l&amp;atilde;m ở Mỹ, n&amp;oacute; đ&amp;atilde; tiết lộ l&amp;agrave; chỉ c&amp;oacute; 9 điểm quan s&amp;aacute;t m&amp;agrave; từ đ&amp;oacute; bạn c&amp;oacute; thể nh&amp;igrave;n thấy h&amp;igrave;nh ảnh 3D thực tế. Đ&amp;oacute; l&amp;agrave; một vấn đề nho nhỏ, họ cho rằng cần c&amp;oacute; &amp;iacute;t nhất 32 điểm quan s&amp;aacute;t.&lt;br /&gt;\r\n	Samsung n&amp;oacute;i rằng: &amp;ldquo;&amp;Iacute;t nhất 10 năm nữa bạn mới c&amp;oacute; thể c&amp;oacute; được TV 3D&amp;rdquo;.&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;a href=&quot;http://www.hdvietnam.com/diendan/members/165122-albums25881-picture60077.jpg&quot;&gt;&lt;img alt=&quot;&quot; border=&quot;0&quot; id=&quot;hs_imageresizer_container_1&quot; src=&quot;http://www.hdvietnam.com/diendan/members/165122-albums25881-picture60077.jpg&quot; /&gt;&lt;/a&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	H&amp;igrave;nh tr&amp;ecirc;n l&amp;agrave; TV 3D mẫu được trưng b&amp;agrave;y của Sony tại cuộc triển l&amp;atilde;m Cable, một cuộc triễn l&amp;atilde;m kh&amp;aacute; lớn được tổ chức tại Boston, Mỹ. V&amp;agrave; một k&amp;ecirc;nh truyền h&amp;igrave;nh 3D 3net - một k&amp;ecirc;nh 3D của Discovery, Sony v&amp;agrave; Imax - l&amp;agrave; v&amp;iacute; dụ về c&amp;ocirc;ng nghệ n&amp;agrave;y.&lt;br /&gt;\r\n	Discovery c&amp;oacute; một c&amp;aacute;i m&amp;agrave;n h&amp;igrave;nh 20 foots (khoảng 6,1m) với h&amp;igrave;nh ảnh tuyệt vời, nhưng t&amp;ocirc;i đề nghị thế n&amp;agrave;y, nh&amp;igrave;n xung quanh v&amp;agrave; t&amp;igrave;m một TV 3D mẫu, c&amp;aacute;i tr&amp;ocirc;ng to khoảng 50 inches.&lt;br /&gt;\r\n	&amp;ldquo;Mẫu n&amp;agrave;y l&amp;agrave; nguy&amp;ecirc;n bản&amp;rdquo;, ph&amp;aacute;t ng&amp;ocirc;n vi&amp;ecirc;n của Discovery n&amp;oacute;i, &amp;ldquo;Ch&amp;uacute;ng t&amp;ocirc;i l&amp;agrave; đối t&amp;aacute;c của Sony ở k&amp;ecirc;nh 3net, k&amp;ecirc;nh n&amp;agrave;y tr&amp;igrave;nh chiếu phim 3D 24 giờ mỗi ng&amp;agrave;y v&amp;agrave; họ y&amp;ecirc;u cầu rằng họ muốn tr&amp;igrave;nh chiếu bản mẫu tr&amp;ecirc;n k&amp;ecirc;nh của ch&amp;uacute;ng t&amp;ocirc;i v&amp;agrave; sử dụng c&amp;aacute;c phần từ chương tr&amp;igrave;nh của ch&amp;uacute;ng t&amp;ocirc;i&amp;rdquo;. Ch&amp;uacute;ng t&amp;ocirc;i n&amp;oacute;i &amp;ldquo;Dĩ nhi&amp;ecirc;n rồi&amp;rdquo;.&lt;br /&gt;\r\n	&amp;ldquo;Những người đ&amp;atilde; ngồi l&amp;ecirc;n ghế v&amp;agrave; xem th&amp;igrave; cảm thấy rất ấn tượng.&amp;rdquo;&lt;br /&gt;\r\n	T&amp;ocirc;i đ&amp;atilde; ngồi xuống v&amp;agrave; l&amp;agrave;m điều đ&amp;oacute; v&amp;agrave; t&amp;ocirc;i đ&amp;atilde; bị th&amp;ocirc;i bay. Nếu t&amp;ocirc;i di chuyển, th&amp;igrave; h&amp;igrave;nh ảnh sẽ mờ đi một ch&amp;uacute;t rồi lại chuyển về dạng 3D v&amp;agrave; nhảy ra khỏi m&amp;agrave;n h&amp;igrave;nh.&lt;br /&gt;\r\n	Một nh&amp;acirc;n vi&amp;ecirc;n người Nhật đ&amp;atilde; giải th&amp;iacute;ch về mẫu n&amp;agrave;y một c&amp;aacute;ch hạnh ph&amp;uacute;c, mặc d&amp;ugrave; họ đ&amp;atilde; nhấn mạnh rằng mọi chuyện vẫn chưa ch&amp;iacute;nh thức.&lt;br /&gt;\r\n	Nhiều mẫu m&amp;agrave;n h&amp;igrave;nh rất mỏng, t&amp;ocirc;i n&amp;oacute;i, n&amp;oacute; chẳng kh&amp;aacute;c nhau nhiều. Mắt phải của bạn chọn lấy một m&amp;agrave;n h&amp;igrave;nh, c&amp;ograve;n mắt c&amp;ograve;n lại cho hiệu ứng nổi (cũng giống như đ&amp;ocirc;i mắt của bạn chọn lấy nhiều điểm kh&amp;aacute;c nhau để thấy được hiệu ứng 3D) trong cuộc sống hằng ng&amp;agrave;y.&lt;br /&gt;\r\n	Nguồn phim hay h&amp;igrave;nh ảnh th&amp;igrave; phải ở định dạng 3D, như bộ phim của Discovery m&amp;agrave; t&amp;ocirc;i đ&amp;atilde; n&amp;oacute;i hoặc l&amp;agrave; những bộ phim 3D mới. &amp;ldquo;C&amp;ograve;n nếu kh&amp;ocirc;ng th&amp;igrave; n&amp;oacute; chỉ l&amp;agrave; 2D.&amp;rdquo;&lt;br /&gt;\r\n	Những kỹ sư người Nhật n&amp;oacute;i rằng những mẫu n&amp;agrave;y vẫn đang trong giai đoạn ph&amp;aacute;t triển với nhiều sự lựa chọn cần phải được thực hiện. Một trong số n&amp;agrave;y l&amp;agrave; gi&amp;aacute; cả, t&amp;ocirc;i đo&amp;aacute;n l&amp;agrave; bạn mong l&amp;agrave; n&amp;oacute; c&amp;oacute; gi&amp;aacute; kh&amp;ocirc;ng qu&amp;aacute; &amp;ldquo;ch&amp;aacute;t&amp;rdquo;.&amp;nbsp;&lt;br /&gt;\r\n	Thời gian của dự &amp;aacute;n: &amp;ldquo;T&amp;ocirc;i mong n&amp;oacute; chỉ k&amp;eacute;o d&amp;agrave;i khoảng một hai năm&amp;rdquo;, một người đến từ Discovery n&amp;oacute;i.&lt;br /&gt;\r\n	Mọi nỗ lực li&amp;ecirc;n hệ với Sony đều kh&amp;ocirc;ng th&amp;agrave;nh c&amp;ocirc;ng v&amp;agrave; kh&amp;ocirc;ng c&amp;oacute; t&amp;agrave;i liệu g&amp;igrave; về n&amp;oacute;. Nhưng bạn đ&amp;atilde; nh&amp;igrave;n thấy n&amp;oacute; v&amp;agrave; tin n&amp;oacute; c&amp;oacute; thật &amp;ndash; v&amp;agrave; t&amp;ocirc;i cũng đ&amp;atilde; thấy n&amp;oacute;!&lt;/div&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;', 'cai-nhin-dau-tien-ve-tv-3d-khong-kinh-cua-sony-1', '', '', '', '', '', '', 0, 0, 'upload/cycTZ.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 18:31:17', 'admin', '2012-10-10 18:31:17');
INSERT INTO `media` VALUES (76, 'default1349865222', '', 'media_news', '[6],', 'admin', 'Game: cuộc chiến 3D thụ động và 3D chủ động', '', '', 'vn', '&lt;p&gt;\r\n	&lt;span style=&quot;text-align: justify; &quot;&gt;Nếu t&amp;ocirc;i n&amp;oacute;i rằng m&amp;agrave;n h&amp;igrave;nh 3D thụ động cho fps cao hơn b&amp;igrave;nh thường trong tr&amp;ograve; chơi điện tử, hẳn c&amp;oacute; người sẽ cười nhạo v&amp;agrave;o mặt t&amp;ocirc;i v&amp;igrave; từ trước đến nay ch&amp;uacute;ng ta đều biết rằng m&amp;agrave;n h&amp;igrave;nh 3D thụ động chỉ sử dụng một nửa độ ph&amp;acirc;n giải chiều dọc cho mỗi khu&amp;ocirc;n h&amp;igrave;nh cho mỗi mắt.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Nếu t&amp;ocirc;i n&amp;oacute;i rằng m&amp;agrave;n h&amp;igrave;nh 3D thụ động cho fps cao hơn b&amp;igrave;nh thường trong tr&amp;ograve; chơi điện tử, hẳn c&amp;oacute; người sẽ cười nhạo v&amp;agrave;o mặt t&amp;ocirc;i v&amp;igrave; từ trước đến nay ch&amp;uacute;ng ta đều biết rằng m&amp;agrave;n h&amp;igrave;nh 3D thụ động chỉ sử dụng một nửa độ ph&amp;acirc;n giải chiều dọc cho mỗi khu&amp;ocirc;n h&amp;igrave;nh cho mỗi mắt.&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;a href=&quot;http://www.hdvietnam.com/diendan/members/291015-albums25978-picture60224.jpg&quot;&gt;&lt;img alt=&quot;&quot; border=&quot;0&quot; id=&quot;hs_imageresizer_container_1&quot; src=&quot;http://www.hdvietnam.com/diendan/members/291015-albums25978-picture60224.jpg&quot; /&gt;&lt;/a&gt;&lt;br /&gt;\r\n	Trong phim ảnh, 3D thụ động c&amp;oacute; thể thua. Nhưng trong game th&amp;igrave; chưa chắc đ&amp;acirc;u nh&amp;eacute;. Ảnh internet&lt;/div&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;br /&gt;\r\n	Nhưng đ&amp;oacute; lại l&amp;agrave; sự thật mới chết, bởi v&amp;igrave; với 3D thụ động mặc d&amp;ugrave; mỗi khu&amp;ocirc;n h&amp;igrave;nh cho mỗi mắt được hiển thị c&amp;oacute; độ ph&amp;acirc;n giải 1920x540 nhưng mỗi khu&amp;ocirc;n h&amp;igrave;nh nội dung tr&amp;ograve; chơi đ&amp;oacute; đ&amp;atilde; được dựng h&amp;igrave;nh (render) c&amp;oacute; độ ph&amp;acirc;n giải 1920x1080 v&amp;agrave; ngay sau đ&amp;oacute; những d&amp;ograve;ng th&amp;ecirc;m v&amp;agrave;o đ&amp;atilde; được loại bỏ. Kết quả l&amp;agrave; bằng c&amp;aacute;ch n&amp;acirc;ng độ ph&amp;acirc;n giải h&amp;igrave;nh ảnh 3D lập thể, bạn sẽ c&amp;oacute; h&amp;igrave;nh ảnh hiển thị 3D giống nhau tr&amp;ecirc;n m&amp;agrave;n 3D chủ động v&amp;agrave; thụ động. Mặc d&amp;ugrave; vậy bạn vẫn cảm nhận được một ch&amp;uacute;t độ ph&amp;acirc;n giải thấp hơn tr&amp;ecirc;n giải ph&amp;aacute;p 3D thụ động.&lt;br /&gt;\r\n	Bằng c&amp;aacute;ch dựng h&amp;igrave;nh với độ ph&amp;acirc;n giải Full HD tr&amp;ecirc;n 3D thụ động cho ph&amp;eacute;p bạn tiết kiệm được sreenshots 3D hay c&amp;aacute;c đoạn video 3D từ c&amp;aacute;c tr&amp;ograve; chơi c&amp;oacute; độ ph&amp;acirc;n giải Full HD, tuy nhi&amp;ecirc;n việc l&amp;agrave;m n&amp;agrave;y kh&amp;ocirc;ng &amp;aacute;p dụng cho tất cả c&amp;aacute;c giải ph&amp;aacute;p 3D.&lt;br /&gt;\r\n	N&amp;oacute; cũng cho ph&amp;eacute;p bạn c&amp;oacute; được h&amp;igrave;nh ảnh tốt hơn m&amp;agrave; kh&amp;ocirc;ng phải sử dụng đến giải ph&amp;aacute;p chống răng cưa như việc giảm độ ph&amp;acirc;n giải theo chiều dọc c&amp;oacute; thể dẫn đến c&amp;aacute;c cạnh lởm chởm hơn.&lt;br /&gt;\r\n	Sử dụng c&amp;aacute;c th&amp;ocirc;ng tin h&amp;igrave;nh ảnh th&amp;ecirc;m v&amp;agrave;o trong việc dựng h&amp;igrave;nh c&amp;oacute; thể gi&amp;uacute;p xử l&amp;yacute; c&amp;aacute;c h&amp;igrave;nh ảnh 3D lập thể c&amp;oacute; độ ph&amp;acirc;n giải cao, v&amp;igrave; vậy sẽ nh&amp;igrave;n tốt hơn, mặc d&amp;ugrave; những h&amp;agrave;ng dọc th&amp;ecirc;m từ mỗi khu&amp;ocirc;n h&amp;igrave;nh vẫn c&amp;ograve;n được bỏ đi.&lt;br /&gt;\r\n	Điều đ&amp;oacute; kh&amp;ocirc;ng gi&amp;uacute;p bạn nh&amp;igrave;n r&amp;otilde; c&amp;aacute;c chi tiết nhỏ hoặc những h&amp;agrave;ng chữ b&amp;eacute; x&amp;iacute;u khi hiển thị tr&amp;ecirc;n hệ thống 3D thụ động, nhưng n&amp;oacute; vẫn l&amp;agrave;m tăng chất lượng h&amp;igrave;nh ảnh một c&amp;aacute;ch tổng thể m&amp;agrave; kh&amp;ocirc;ng l&amp;agrave;m mất m&amp;aacute;t qu&amp;aacute; lớn.&lt;br /&gt;\r\n	Nhưng điều cuối c&amp;ugrave;ng l&amp;agrave; bạn mong muốn c&amp;oacute; được số lượng fps như nhau tr&amp;ecirc;n c&amp;ugrave;ng một hệ thống hiện thị 3D chủ động hay 3D thụ động cho giải ph&amp;aacute;p tr&amp;ograve; chơi 3D lập thể.&lt;br /&gt;\r\n	Tr&amp;ecirc;n thực tế với thụ động, bạn c&amp;oacute; thể nhận được fps một ch&amp;uacute;t &amp;iacute;t do phải xử l&amp;yacute; th&amp;ecirc;m những h&amp;igrave;nh ảnh trước khi ch&amp;uacute;ng được hiển thị tr&amp;ecirc;n m&amp;agrave;n h&amp;igrave;nh, nhưng sự kh&amp;aacute;c biệt l&amp;agrave; rất nhỏ, v&amp;igrave; vậy bạn sẽ kh&amp;oacute; nhận thấy n&amp;oacute;.&lt;br /&gt;\r\n	Xem ra cuộc chiến giữa 2 c&amp;ocirc;ng nghệ c&amp;ograve;n diễn ra d&amp;agrave;i d&amp;agrave;i.&lt;/p&gt;', 'game-cuoc-chien-3d-thu-dong-va-3d-chu-dong-1', '', '', '', '', '', '', 0, 0, 'upload/chotroi3d.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 18:33:42', 'admin', '2012-10-10 18:33:42');
INSERT INTO `media` VALUES (77, 'default1349865459', '', 'media_news', '[6],', 'admin', 'Bạn biết bao nhiêu loại công nghệ 3D?', '', '', 'vn', '&lt;p&gt;\r\n	&lt;span style=&quot;text-align: justify; &quot;&gt;Hiện nay, kh&amp;ocirc;ng ai d&amp;aacute;m phủ nhận những tiềm năng của 3D trong tương lai, v&amp;agrave; cũng giống như những c&amp;ocirc;ng nghệ tiềm năng kh&amp;aacute;c, c&amp;oacute; rất nhiều thứ để ch&amp;uacute;ng ta l&amp;agrave;m với 3D.&lt;/span&gt;&lt;/p&gt;', '&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;a href=&quot;https://lh3.googleusercontent.com/-sOe_JSOKRV4/T9dxN6NMndI/AAAAAAAABac/IzYNCrBi4Y0/s640/3-d-image.jpg&quot; rel=&quot;nofollow&quot;&gt;&lt;img alt=&quot;&quot; border=&quot;0&quot; id=&quot;hs_imageresizer_container_3&quot; src=&quot;https://lh3.googleusercontent.com/-sOe_JSOKRV4/T9dxN6NMndI/AAAAAAAABac/IzYNCrBi4Y0/s640/3-d-image.jpg&quot; /&gt;&lt;/a&gt;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Hiện nay, kh&amp;ocirc;ng ai d&amp;aacute;m phủ nhận những tiềm năng của 3D trong tương lai, v&amp;agrave; cũng giống như những c&amp;ocirc;ng nghệ tiềm năng kh&amp;aacute;c, c&amp;oacute; rất nhiều thứ để ch&amp;uacute;ng ta l&amp;agrave;m với 3D.&amp;nbsp;&lt;br /&gt;\r\n	Một c&amp;acirc;u hỏi được đặt ra, đ&amp;oacute; l&amp;agrave; c&amp;oacute; tất cả bao nhi&amp;ecirc;u loại c&amp;ocirc;ng nghệ 3D m&amp;agrave; bạn từng biết?&lt;br /&gt;\r\n	Chỉ c&amp;oacute; 3D chủ động v&amp;agrave; 3D thụ động th&amp;ocirc;i ư?&lt;br /&gt;\r\n	Chưa đủ.&lt;br /&gt;\r\n	Theo một b&amp;aacute;o c&amp;aacute;o vừa được h&amp;atilde;ng Jon Peddie Research (JPR) c&amp;ocirc;ng bố, hiện nay tr&amp;ecirc;n thế giới c&amp;oacute; khoảng 7 c&amp;ocirc;ng nghệ giải m&amp;atilde; 3D cơ bản, v&amp;agrave; ch&amp;uacute;ng tạo ra tới 13 nền tảng kh&amp;aacute;c nhau để phục vụ cho nhu cầu v&amp;ocirc; tận của lo&amp;agrave;i người.&lt;br /&gt;\r\n	Về l&amp;yacute; thuyết, 13 nền tảng 3D tr&amp;ecirc;n c&amp;oacute; thể được ứng dụng cho khoảng 91 lĩnh vực kh&amp;aacute;c nhau. Tuy nhi&amp;ecirc;n, do một v&amp;agrave;i nền tảng kh&amp;ocirc;ng tương th&amp;iacute;ch với c&amp;aacute;c c&amp;ocirc;ng nghệ giải m&amp;atilde; c&amp;ograve;n lại n&amp;ecirc;n trong thực tế - JPR cho rằng chỉ c&amp;oacute; khoảng 29 lĩnh vực l&amp;agrave; mang t&amp;iacute;nh thực tế cao.&amp;nbsp;&lt;br /&gt;\r\n	C&amp;aacute;c lĩnh vực th&amp;ocirc;ng dụng nhất c&amp;oacute; sự xuất hiện của 3D bao gồm điện ảnh (tất nhi&amp;ecirc;n), c&amp;ocirc;ng nghệ di động, PC, y tế.... V&amp;agrave; thật đ&amp;aacute;ng ngạc nhi&amp;ecirc;n: c&amp;ocirc;ng nghệ di động ch&amp;iacute;nh l&amp;agrave; lĩnh vực m&amp;agrave; 3D sẽ phục vụ nhiều nhất trong tương lai, dự kiến chiếm khoảng 97,1% thị phần v&amp;agrave;o năm 2017.&lt;br /&gt;\r\n	&amp;quot;3D chỉ l&amp;agrave; th&amp;agrave;nh phần bổ sung, kh&amp;ocirc;ng phải l&amp;agrave; một ng&amp;agrave;nh c&amp;ocirc;ng nghiệp. Giống như m&amp;agrave;u sắc, &amp;acirc;m thanh hay cảm biến chuyển động, n&amp;oacute; chỉ gi&amp;uacute;p tăng cường&amp;nbsp;nội dung&amp;nbsp;v&amp;agrave; l&amp;agrave;m phong ph&amp;uacute; th&amp;ecirc;m c&amp;aacute;c t&amp;iacute;nh năng. Tuy nhi&amp;ecirc;n, nếu nguồn nội dung c&amp;oacute; chất lượng kh&amp;ocirc;ng tốt, 3D cũng sẽ kh&amp;ocirc;ng l&amp;agrave;m được g&amp;igrave;. C&amp;aacute;c ng&amp;agrave;nh c&amp;ocirc;ng nghiệp đang bước sang một giai đoạn mới, v&amp;agrave; đ&amp;acirc;y l&amp;agrave; thời điểm để họ bắt đầu kh&amp;aacute;m ph&amp;aacute; c&amp;ocirc;ng nghệ 3D&amp;quot;, JPR cho biết.&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div&gt;\r\n	&lt;br /&gt;\r\n	&lt;a href=&quot;https://lh3.googleusercontent.com/-VCdr4tvi1_8/T9dxNBVPefI/AAAAAAAABaQ/_sSXddrz2mw/s726/Untitled-1.jpg&quot; rel=&quot;nofollow&quot;&gt;&lt;img alt=&quot;&quot; border=&quot;0&quot; id=&quot;hs_imageresizer_container_1&quot; src=&quot;https://lh3.googleusercontent.com/-VCdr4tvi1_8/T9dxNBVPefI/AAAAAAAABaQ/_sSXddrz2mw/s726/Untitled-1.jpg&quot; /&gt;&lt;/a&gt;&lt;br /&gt;\r\n	1: hầu như c&amp;aacute;c thiết bị 3D c&amp;ocirc;ng nghệ thị sai trong y tế đều ở dạng cầm tay&lt;/div&gt;\r\n&lt;div&gt;\r\n	2: thường c&amp;oacute; ở trong c&amp;aacute;c tạp ch&amp;iacute; hoặc c&amp;aacute;c mẩu quảng c&amp;aacute;o&lt;/div&gt;\r\n&lt;div&gt;\r\n	3: thường xuất hiện tr&amp;ecirc;n laptop&lt;/div&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;br /&gt;\r\n	&lt;a href=&quot;https://lh4.googleusercontent.com/-20kOLGL6cMM/T9dxMuQmZ6I/AAAAAAAABaM/J2kA2whz-LQ/s550/20120611-press-310.jpg&quot; rel=&quot;nofollow&quot;&gt;&lt;img alt=&quot;&quot; border=&quot;0&quot; id=&quot;hs_imageresizer_container_2&quot; src=&quot;https://lh4.googleusercontent.com/-20kOLGL6cMM/T9dxMuQmZ6I/AAAAAAAABaM/J2kA2whz-LQ/s550/20120611-press-310.jpg&quot; /&gt;&lt;/a&gt;&lt;/div&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;', 'ban-biet-bao-nhieu-loai-cong-nghe-3d-1', '', '', '', '', '', '', 0, 0, 'upload/images.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 18:37:39', 'admin', '2012-10-10 18:37:39');
INSERT INTO `media` VALUES (78, 'default1349866391', '', 'media_news', '[6],', 'admin', 'Làm sao sắm được máy chiếu HD ưng ý?', '', '', 'vn', '&lt;p&gt;\r\n	Hiện nay phong tr&amp;agrave;o chơi m&amp;aacute;y chiếu xem phim kh&amp;ocirc;ng c&amp;ograve;n xa lạ trong giới HDvietnam, tuy nhi&amp;ecirc;n trước khi mua c&amp;aacute;c b&amp;aacute;c đ&amp;atilde; bao giờ tự hỏi n&amp;ecirc;n mua loại g&amp;igrave;, hiệu g&amp;igrave;, ở đ&amp;acirc;u v&amp;agrave; nhiều c&amp;acirc;u hỏi h&amp;oacute;c b&amp;uacute;a kh&amp;aacute;c nữa.&amp;nbsp;&lt;/p&gt;', '&lt;p&gt;\r\n	Hiện nay phong tr&amp;agrave;o chơi m&amp;aacute;y chiếu xem phim kh&amp;ocirc;ng c&amp;ograve;n xa lạ trong giới HDvietnam, tuy nhi&amp;ecirc;n trước khi mua c&amp;aacute;c b&amp;aacute;c đ&amp;atilde; bao giờ tự hỏi n&amp;ecirc;n mua loại g&amp;igrave;, hiệu g&amp;igrave;, ở đ&amp;acirc;u v&amp;agrave; nhiều c&amp;acirc;u hỏi h&amp;oacute;c b&amp;uacute;a kh&amp;aacute;c nữa.&amp;nbsp;&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;a href=&quot;http://www.hdvietnam.com/diendan/members/291015-albums25978-picture60739.jpg&quot;&gt;&lt;img alt=&quot;&quot; border=&quot;0&quot; id=&quot;hs_imageresizer_container_3&quot; src=&quot;http://www.hdvietnam.com/diendan/members/291015-albums25978-picture60739.jpg&quot; /&gt;&lt;/a&gt;&lt;br /&gt;\r\n	Epson 8350 một thời được rất nhiều tay chơi m&amp;aacute;y chiếu lựa chọn&lt;/div&gt;\r\n&lt;p&gt;\r\n	B&amp;agrave;i viết n&amp;agrave;y em đ&amp;uacute;c kết từ kinh nghiệm bản th&amp;acirc;n (&lt;a href=&quot;http://www.hdvietnam.com/diendan/192-karaoke-thiet-bi-phong-thu/267363-lam-nao-sam-duoc-bo-karaoke-gia-dinh-ung-y.html&quot; target=&quot;_blank&quot;&gt;tương tự như b&amp;agrave;i về karaoke&lt;/a&gt;), c&amp;oacute; tham khảo nhiều nguồn tin tr&amp;ecirc;n internet. Với mong muốn gi&amp;uacute;p những người mới chơi m&amp;aacute;y chiếu c&amp;oacute; thể tự tin quyết định lựa chọn cho m&amp;igrave;nh một m&amp;aacute;y chiếu ph&amp;ugrave; hợp để l&amp;agrave;m sao mỗi tối khi &amp;aacute;nh đ&amp;egrave;n ph&amp;ograve;ng tắt đi, m&amp;agrave;n chiếu hạ xuống, những thước phim HD được bật l&amp;ecirc;n, vợ c&amp;aacute;c b&amp;aacute;c c&amp;oacute; thể cười rung rốn v&amp;agrave; vỗ c&amp;aacute;i đ&amp;eacute;t v&amp;agrave;o đ&amp;ugrave;i c&amp;aacute;c b&amp;aacute;c su&amp;yacute;t xoa tự h&amp;agrave;o về sự đầu tư đ&amp;uacute;ng đắn.&lt;br /&gt;\r\n	V&amp;igrave; những &amp;yacute; kiến mang t&amp;iacute;nh chủ quan n&amp;ecirc;n kh&amp;ocirc;ng tr&amp;aacute;nh khỏi thiếu s&amp;oacute;t. Rất mong c&amp;aacute;c b&amp;aacute;c g&amp;oacute;p &amp;yacute; để ho&amp;agrave;n thiện b&amp;agrave;i viết.&amp;nbsp;&lt;br /&gt;\r\n	N&amp;agrave;o b&amp;acirc;y giờ ch&amp;uacute;ng ta sẽ v&amp;agrave;o chủ để ch&amp;iacute;nh th&amp;ocirc;i.&lt;br /&gt;\r\n	C&amp;oacute; rất nhiều ti&amp;ecirc;u ch&amp;iacute; để lựa chọn v&amp;igrave; vậy em tạm thời ph&amp;acirc;n chia c&amp;aacute;c ti&amp;ecirc;u ch&amp;iacute; để cho c&amp;aacute;c b&amp;aacute;c dễ h&amp;igrave;nh dung.&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;Gi&amp;aacute; tiền đầu tư&lt;/strong&gt;&lt;br /&gt;\r\n	Trước khi nghĩ đến việc mua m&amp;aacute;y chiếu, c&amp;aacute;c b&amp;aacute;c trả lời xem m&amp;igrave;nh c&amp;oacute; thể r&amp;uacute;t k&amp;eacute;t ra bao nhi&amp;ecirc;u tiền để trang bị cho m&amp;aacute;y chiếu (bao gồm cả d&amp;acirc;y, m&amp;agrave;n chiếu&amp;hellip;). V&amp;igrave; c&amp;aacute;i n&amp;agrave;y kh&amp;aacute; quan trọng để đi đến c&amp;aacute;c ti&amp;ecirc;u ch&amp;iacute; kế tiếp. Theo kinh nghiệm của em th&amp;igrave; để xem phim bằng m&amp;aacute;y chiếu cho n&amp;oacute; &amp;ldquo;ra tấm ra m&amp;oacute;n&amp;rdquo; n&amp;ecirc;n đầu tư &amp;iacute;t nhất khoảng 20 triệu đến v&amp;ocirc; c&amp;ugrave;ng (bao gồm cả phụ kiện). Nếu &amp;iacute;t hơn th&amp;igrave; e rằng kh&amp;oacute; c&amp;oacute; thể xem &amp;ldquo;sướng&amp;rdquo; được. Những con m&amp;aacute;y chiếu Full HD của Optoma như HD20 (đ&amp;atilde; bỏ mẫu), HD23 cũng chỉ khoảng 25-27 triệu, Epson 8350 khoảng 27-30 triệu, Mitsubishi HC4000 cũng chỉ khoảng 27-30 triệu. Đ&amp;acirc;y l&amp;agrave; những mẫu m&amp;aacute;y Full HD được nhiều người lựa chọn.&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n	&lt;strong&gt;Độ ph&amp;acirc;n giải&amp;nbsp;&lt;/strong&gt;&lt;br /&gt;\r\n	C&amp;aacute;i n&amp;agrave;y n&amp;oacute; cũng li&amp;ecirc;n quan đến vấn đề tr&amp;ecirc;n. Để xem phim HD, tốt nhất n&amp;ecirc;n đầu tư Full HD. Một số mẫu m&amp;aacute;y chiếu c&amp;oacute; giả rẻ (giao động 10-15 triệu) chỉ hỗ trợ HD Ready. Thậm ch&amp;iacute; một số lời quảng c&amp;aacute;o n&amp;oacute;i l&amp;agrave; support Full HD g&amp;acirc;y hiểu lầm cho người ti&amp;ecirc;u d&amp;ugrave;ng. C&amp;aacute;i quan trọng l&amp;agrave; c&amp;aacute;c b&amp;aacute;c xem độ ph&amp;acirc;n giải thực (native resolution) của n&amp;oacute; l&amp;agrave; bao nhi&amp;ecirc;u. Nhiều b&amp;aacute;c hỏi rằng vậy với số tiền &amp;iacute;t đ&amp;oacute; th&amp;igrave; kh&amp;ocirc;ng chơi được m&amp;aacute;y chiếu &amp;agrave;? Chơi th&amp;igrave; chơi được, nhưng chẳng may c&amp;oacute; dịp gh&amp;eacute; thăm nh&amp;agrave; anh bạn c&amp;oacute; con Full HD, xem một v&amp;agrave;i cảnh phim Brulay, c&amp;aacute;c b&amp;aacute;c sẽ bắt đầu cảm thấy ch&amp;aacute;n c&amp;aacute;i m&amp;aacute;y chiếu nh&amp;agrave; m&amp;igrave;nh thế kh&amp;ocirc;ng biết. Theo em th&amp;igrave; đ&amp;atilde; chơi m&amp;aacute;y chiếu th&amp;igrave; chơi Full HD, c&amp;ograve;n nếu tiền m&amp;igrave;nh chưa c&amp;oacute; đủ th&amp;igrave; chờ đợi t&amp;iacute;ch c&amp;oacute;p th&amp;ecirc;m hoặc mua lại của b&amp;aacute;c n&amp;agrave;o thanh l&amp;yacute; cho rẻ.&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://www.getprice.com.au/images/uploadimg/BimgOptoma-HD23.jpg&quot; style=&quot;width: 700px; height: 414px; &quot; /&gt;&lt;br /&gt;\r\n	Optoma HD23 (tiền th&amp;acirc;n l&amp;agrave; HD20) được nhiều người lựa chọn v&amp;igrave; gi&amp;aacute; rẻ&lt;/div&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;Độ s&amp;aacute;ng&amp;nbsp;&lt;/strong&gt;&lt;br /&gt;\r\n	C&amp;aacute;i n&amp;agrave;y kh&amp;aacute; quan trọng, n&amp;oacute; quan trọng đến mức m&amp;agrave; nhiều người đi mua m&amp;aacute;y chiếu hỏi ngay c&amp;acirc;u đầu ti&amp;ecirc;n &amp;ldquo;độ s&amp;aacute;ng bao nhi&amp;ecirc;u?&amp;rdquo;. Độ s&amp;aacute;ng được thể hiện bằng ANSI lumens. Th&amp;ocirc;ng số c&amp;agrave;ng cao, m&amp;aacute;y chiếu c&amp;agrave;ng s&amp;aacute;ng. C&amp;aacute;c b&amp;aacute;c xem c&amp;aacute;i ph&amp;ograve;ng nh&amp;agrave; m&amp;igrave;nh n&amp;oacute; c&amp;oacute; tối đen như đ&amp;ecirc;m 30 hay kh&amp;ocirc;ng? Nếu c&amp;oacute; th&amp;igrave; chỉ cần 1500 lumens &amp;ndash; 2000 lumens l&amp;agrave; đủ. S&amp;aacute;ng qu&amp;aacute; chỉ l&amp;agrave;m mỏi cặp cửa sổ t&amp;acirc;m hồn của c&amp;aacute;c b&amp;aacute;c. Nếu ph&amp;ograve;ng bị lọt s&amp;aacute;ng th&amp;igrave; mới cần độ s&amp;aacute;ng cao hơn. Phần lớn m&amp;aacute;y chiếu home HD c&amp;oacute; độ s&amp;aacute;ng giao động 1500 lumens đến 2500 lumens. C&amp;oacute; một v&amp;agrave;i model rẻ tiền c&amp;oacute; độ s&amp;aacute;ng 3000-4000 lumens thường d&amp;ugrave;ng cho tr&amp;igrave;nh chiếu ph&amp;ograve;ng họp nhiều hơn hoặc l&amp;agrave; gi&amp;aacute; tr&amp;ecirc;n trời nếu full HD.&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n	&lt;strong&gt;Độ tương phản (contrast ratio)&lt;/strong&gt;&lt;br /&gt;\r\n	Theo em c&amp;aacute;i n&amp;agrave;y mới l&amp;agrave; quan trọng nhất. Độ tương phản cao sẽ cho h&amp;igrave;nh ảnh trong trẻo, s&amp;acirc;u. N&amp;oacute; cũng thường ảnh hưởng đến m&amp;agrave;u đen hoặc trắng khi tr&amp;igrave;nh chiếu. Tương phản tốt l&amp;agrave; những cảnh tối ta vẫn ph&amp;acirc;n biệt được c&amp;aacute;c chi tiết, kh&amp;ocirc;ng bị bệt. C&amp;aacute;c m&amp;aacute;y chiếu c&amp;oacute; độ tương phản k&amp;eacute;m, khi tăng tương phản l&amp;ecirc;n sẽ dẫn đến mất chi tiết ở v&amp;ugrave;ng tối hoặc s&amp;aacute;ng. Con Epson 8350 c&amp;oacute; contrast ratio l&amp;ecirc;n đến 50000:1, trong khi đ&amp;oacute; Optoma HD20 chỉ c&amp;oacute; 4000:1. Tất nhi&amp;ecirc;n tr&amp;ecirc;n thực tế khi tr&amp;igrave;nh chiếu kh&amp;ocirc;ng phải 8350 c&amp;oacute; độ tương phản cao hơn HD20 gấp 10 lần.&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div&gt;\r\n	&lt;p style=&quot;text-align: center; &quot;&gt;\r\n		&lt;a href=&quot;http://www.hdvietnam.com/diendan/members/291015-albums25978-picture60743.jpg&quot;&gt;&lt;img alt=&quot;&quot; id=&quot;hs_imageresizer_container_2&quot; src=&quot;http://www.hdvietnam.com/diendan/members/291015-albums25978-picture60743.jpg&quot; style=&quot;border-width: 0px; border-style: solid; width: 700px; height: 397px; &quot; /&gt;&lt;/a&gt;&lt;/p&gt;\r\n	&lt;div style=&quot;text-align: center; &quot;&gt;\r\n		Misubishi HC4000 gần đ&amp;acirc;y được nhiều người lựa chọn&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;C&amp;ocirc;ng nghệ&lt;/strong&gt;&lt;br /&gt;\r\n	Hiện nay c&amp;oacute; 2 c&amp;ocirc;ng nghệ phổ biến đ&amp;oacute; l&amp;agrave; LCD v&amp;agrave; DLP. Mỗi anh c&amp;oacute; c&amp;aacute;i lợi thế ri&amp;ecirc;ng. C&amp;ocirc;ng nghệ DLP cho h&amp;igrave;nh ảnh mượt, cảm gi&amp;aacute;c hơi soft, trong khi đ&amp;oacute; LCD cảm gi&amp;aacute;c sắc n&amp;eacute;t hơn. Về m&amp;agrave;u sắc th&amp;igrave; LCD c&amp;oacute; vẻ cho h&amp;igrave;nh ảnh đẹp hơn ch&amp;uacute;t. Nhược điểm của c&amp;ocirc;ng nghệ LCD l&amp;agrave; m&amp;aacute;y to, nặng hơn DLP. Ngo&amp;agrave;i ra LCD c&amp;oacute; hiện tượng &amp;ldquo;viền t&amp;iacute;m&amp;rdquo;. Khi đứng gần m&amp;agrave;n h&amp;igrave;nh, những d&amp;ograve;ng chữ hay cảnh c&amp;oacute; tương phản lớn th&amp;igrave; sẽ ph&amp;aacute;t hiện rất r&amp;otilde;. Tuy nhi&amp;ecirc;n c&amp;aacute;c b&amp;aacute;c y&amp;ecirc;n t&amp;acirc;m, ngồi xa khoảng 3m th&amp;igrave; kh&amp;ocirc;ng thấy c&amp;aacute;i g&amp;igrave; đ&amp;acirc;u. DLP th&amp;igrave; lại c&amp;oacute; cảm gi&amp;aacute;c &amp;ldquo;cầu vồng&amp;rdquo;. Khi xem cảnh c&amp;oacute; độ ch&amp;ecirc;nh s&amp;aacute;ng lớn, lướt mắt từ tr&amp;aacute;i qua phải (đọc phụ đề) thỉnh thoảng c&amp;oacute; cảm gi&amp;aacute;c c&amp;oacute; &amp;aacute;nh s&amp;aacute;ng l&amp;oacute;e l&amp;ecirc;n như 7 sắc cầu vồng.&amp;nbsp;&lt;br /&gt;\r\n	C&amp;ograve;n rất nhiều yếu tố nữa như độ bền của b&amp;oacute;ng, độ ồn của m&amp;aacute;y, độ nặng nhẹ của m&amp;aacute;y, thiết kế của m&amp;aacute;y, danh tiếng nh&amp;agrave; sản suất&amp;hellip;để cho ch&amp;uacute;ng ta c&amp;acirc;n nhắc khi mua. Ri&amp;ecirc;ng em th&amp;igrave; những c&amp;aacute;i n&amp;agrave;y em cũng kh&amp;ocirc;ng để &amp;yacute; kỹ v&amp;igrave; mấy c&amp;aacute;i quan trọng ph&amp;iacute;a tr&amp;ecirc;n thường t&amp;aacute;c động đến việc mua h&amp;agrave;ng của em. Trong c&amp;aacute;i box những d&amp;agrave;n mẫu, c&amp;aacute;c b&amp;aacute;c v&amp;agrave;o đ&amp;oacute; tham khảo rồi l&amp;agrave;m quen xem nh&amp;agrave; b&amp;aacute;c n&amp;agrave;o c&amp;oacute; con m&amp;aacute;y c&amp;aacute;c b&amp;aacute;c &amp;yacute; định mua, c&amp;aacute;c b&amp;aacute;c đến tham khảo, xem thử. Từ đ&amp;oacute; sẽ dẫn đến việc đưa ra quyết định s&amp;aacute;ng suốt nhất.&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n	&lt;strong&gt;Vậy mua ở đ&amp;acirc;u?&lt;/strong&gt;&lt;br /&gt;\r\n	Việc tham khảo, chọn được m&amp;aacute;y rồi đến khi mua ở đ&amp;acirc;u cũng đau đầu. Đừng bao giờ tin 100% lời của sales n&amp;oacute;i. Th&amp;ocirc;ng thường sales sẽ&amp;nbsp;&lt;acronym title=&quot;Google Page Ranking&quot;&gt;PR&lt;/acronym&gt;&amp;nbsp;mạnh cho sản phẩm n&amp;agrave;o m&amp;agrave; c&amp;oacute; hoa hồng cao. C&amp;aacute;c b&amp;aacute;c h&amp;atilde;y hỏi những người đ&amp;atilde; từng chơi m&amp;aacute;y chiếu xem mua ở đ&amp;acirc;u, thường c&amp;aacute;ch n&amp;agrave;y sẽ tiết kiệm cho c&amp;aacute;c b&amp;aacute;c kha kh&amp;aacute; học ph&amp;iacute;. N&amp;ecirc;n mua ở những cửa h&amp;agrave;ng uy t&amp;iacute;n, c&amp;oacute; chế độ bảo h&amp;agrave;nh r&amp;otilde; r&amp;agrave;ng.&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n	&lt;strong&gt;D&amp;ugrave;ng m&amp;agrave;n chiếu g&amp;igrave;?&lt;/strong&gt;&lt;br /&gt;\r\n	Kinh nghiệm của em l&amp;agrave; kh&amp;ocirc;ng n&amp;ecirc;n đầu tư qu&amp;aacute; đắt tiền v&amp;agrave;o c&amp;aacute;i m&amp;agrave;n chiếu. Nếu c&amp;aacute;c b&amp;aacute;c c&amp;oacute; bức tường phẳng phiu c&amp;oacute; thể DIY một chiếc fix frame bằng gỗ, thạch cao, k&amp;iacute;nh&amp;hellip;sau đ&amp;oacute; d&amp;ugrave;ng sơn trắng phun (kh&amp;ocirc;ng n&amp;ecirc;n lăn). Hiện nay m&amp;agrave;n chiếu c&amp;oacute; v&amp;ocirc; v&amp;agrave;n c&amp;aacute;c thương hiệu, hầu hết đến từ TQ. Những m&amp;agrave;n n&amp;agrave;y về chất lượng cũng tạm được, tuy nhi&amp;ecirc;n c&amp;oacute; hiện tượng bị v&amp;otilde;ng sau một thời gian sử dụng. Khi xem cho ta cảm gi&amp;aacute;c rất kh&amp;oacute; chịu. C&amp;oacute; n&amp;ecirc;n mua m&amp;agrave;n bạc kh&amp;ocirc;ng? Nếu xem 2D th&amp;igrave; kh&amp;ocirc;ng n&amp;ecirc;n v&amp;igrave; m&amp;agrave;n bạc sẽ c&amp;oacute; hiện tượng hot spot vả lại v&amp;igrave; gain cao n&amp;ecirc;n rất kh&amp;ocirc;ng tốt cho mắt.&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;a href=&quot;http://www.hdvietnam.com/diendan/members/291015-albums25978-picture60741.jpg&quot;&gt;&lt;img alt=&quot;&quot; border=&quot;0&quot; id=&quot;hs_imageresizer_container_1&quot; src=&quot;http://www.hdvietnam.com/diendan/members/291015-albums25978-picture60741.jpg&quot; /&gt;&lt;/a&gt;&lt;br /&gt;\r\n	&lt;em&gt;Epson 3010 - M&amp;aacute;y chiếu 3D gi&amp;aacute; mềm&lt;/em&gt;&lt;/div&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n	&lt;strong&gt;Tinh chỉnh&lt;/strong&gt;&lt;br /&gt;\r\n	Th&amp;ocirc;ng thường c&amp;aacute;c nh&amp;agrave; sản suất c&amp;oacute; c&amp;aacute;c chế độ mặc định. Một v&amp;agrave;i model cho h&amp;igrave;nh ảnh kh&amp;aacute; tốt (chế độ Cinema của Epson 8350 hầu như kh&amp;ocirc;ng phải chỉnh lại đ&amp;atilde; đẹp rồi). Đ&amp;ocirc;i khi c&amp;aacute;c b&amp;aacute;c phải c&amp;acirc;n chỉnh lại c&amp;aacute;c th&amp;ocirc;ng số như t&amp;ocirc;ng m&amp;agrave;u (n&amp;oacute;ng, lạnh&amp;hellip;), độ b&amp;atilde;o h&amp;ograve;a m&amp;agrave;u, độ tương phản, độ n&amp;eacute;t, độ s&amp;aacute;ng... Chi tiết hơn th&amp;igrave; chỉnh c&amp;aacute;c k&amp;ecirc;nh m&amp;agrave;u RBG. C&amp;oacute; thể tham khảo th&amp;ocirc;ng số của một số người đ&amp;atilde; d&amp;ugrave;ng. B&amp;aacute;c davidhand đ&amp;atilde; c&amp;oacute; b&amp;agrave;i chi tiết về vụ n&amp;agrave;y cho Optoma HD20, c&amp;aacute;c b&amp;aacute;c c&amp;oacute; thể tham khảo th&amp;ecirc;m.&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n	&lt;p&gt;\r\n		&amp;nbsp;&lt;/p&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n	&lt;strong&gt;N&amp;ecirc;n mua TV hay m&amp;aacute;y chiếu?&lt;/strong&gt;&lt;br /&gt;\r\n	C&amp;acirc;u hỏi n&amp;agrave;y c&amp;aacute;c b&amp;aacute;c c&amp;oacute; thể dễ d&amp;agrave;ng t&amp;igrave;m thấy tr&amp;ecirc;n box n&amp;agrave;y. Mỗi c&amp;aacute;i n&amp;oacute; c&amp;oacute; lợi thế ri&amp;ecirc;ng. TV cho c&amp;aacute;c b&amp;aacute;c sự đa năng (c&amp;oacute; thể xem phim, xem TV&amp;hellip;). M&amp;aacute;y chiếu cho ta cảm gi&amp;aacute;c rất l&amp;agrave; &amp;ldquo;xi n&amp;ecirc;&amp;rdquo;. Nếu c&amp;aacute;c b&amp;aacute;c c&amp;oacute; ph&amp;ograve;ng ri&amp;ecirc;ng để setup home cinema th&amp;igrave; m&amp;aacute;y chiếu l&amp;agrave; lựa chọn h&amp;agrave;ng đầu.&amp;nbsp;&lt;br /&gt;\r\n	Tr&amp;ecirc;n đ&amp;acirc;y l&amp;agrave; những kinh nghiệm &amp;iacute;t ỏi của em. Việc sắm một chiếc m&amp;aacute;y chiếu cũng kh&amp;ocirc;ng hề đơn giản. Mỗi một lần n&amp;acirc;ng cấp l&amp;agrave; một lần trải nghiệm những điều th&amp;uacute; vị. Hi vọng c&amp;aacute;c b&amp;aacute;c sẽ sắm cho m&amp;igrave;nh được một chiếc m&amp;aacute;y chiếu ưng &amp;yacute;.&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;Danh s&amp;aacute;ch 10 m&amp;aacute;y chiếu Full HD phổ biến nhất (theo xếp hạng của trang projectcentral). Gi&amp;aacute; v&amp;agrave; ch&amp;iacute;nh s&amp;aacute;ch bảo h&amp;agrave;nh theo thị trường Mỹ.&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;1. Epson PowerLite Home Cinema 5010&amp;nbsp;&lt;/strong&gt;&lt;br /&gt;\r\n	Brightness: 2400 Lumens&lt;br /&gt;\r\n	Contrast: 200000:1&lt;br /&gt;\r\n	Technology: 0.7&amp;quot; 3 LCD&lt;br /&gt;\r\n	Lamp Life: 4000 Hours&lt;br /&gt;\r\n	Audible Noise: 32 dB&lt;br /&gt;\r\n	Warranty: 2 Years&lt;br /&gt;\r\n	Gi&amp;aacute;: $2,599&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;2. Optoma HD33&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Brightness: 1800 Lumens&lt;br /&gt;\r\n	Contrast: 4000:1&lt;br /&gt;\r\n	Technology: 0.7&amp;quot; DLP&lt;br /&gt;\r\n	Lamp Life: 3000 Hours&lt;br /&gt;\r\n	Audible Noise: 30 dB&lt;br /&gt;\r\n	Warranty: 1 Year&amp;nbsp;&lt;br /&gt;\r\n	Gi&amp;aacute;: $1,407&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;3. Panasonic PT-AE7000&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Brightness: 2000 Lumens&lt;br /&gt;\r\n	Contrast: 300000:1&lt;br /&gt;\r\n	Technology: 0.7&amp;quot; 3 LCD&lt;br /&gt;\r\n	Lamp Life: 4000 Hours&lt;br /&gt;\r\n	Audible Noise: n/a&lt;br /&gt;\r\n	Warranty: 1 Year&lt;br /&gt;\r\n	Gi&amp;aacute;: $2,579&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;4. Acer H6500&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Brightness: 2100 Lumens&lt;br /&gt;\r\n	Contrast: 10000:1&lt;br /&gt;\r\n	Technology: 0.7&amp;quot; DLP&lt;br /&gt;\r\n	Lamp Life: 3500 Hours&lt;br /&gt;\r\n	Audible Noise: 35 dB&lt;br /&gt;\r\n	Warranty: 1 Year&amp;nbsp;&lt;br /&gt;\r\n	Gi&amp;aacute;: $899&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;5. Epson PowerLite Home Cinema 3010&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Brightness: 2200 Lumens&lt;br /&gt;\r\n	Contrast: 40000:1&lt;br /&gt;\r\n	Technology: 0.6&amp;quot; 3 LCD&lt;br /&gt;\r\n	Lamp Life: 4000 Hours&lt;br /&gt;\r\n	Audible Noise: 32 dB&lt;br /&gt;\r\n	Warranty: 2 Years&amp;nbsp;&lt;br /&gt;\r\n	Gi&amp;aacute;: $1,532&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;6. Epson Home Cinema 8350&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Brightness: 2000 Lumens&lt;br /&gt;\r\n	Contrast: 50000:1&lt;br /&gt;\r\n	Technology: 3 LCD&lt;br /&gt;\r\n	Lamp Life: n/a&lt;br /&gt;\r\n	Audible Noise: 28 dB&lt;br /&gt;\r\n	Warranty: 2 Years&amp;nbsp;&lt;br /&gt;\r\n	Gi&amp;aacute;: $1,232&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;7. Panasonic PT-AR100U&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Brightness: 2800 Lumens&lt;br /&gt;\r\n	Contrast: 50000:1&lt;br /&gt;\r\n	Technology: 3 LCD&lt;br /&gt;\r\n	Lamp Life: n/a&lt;br /&gt;\r\n	Audible Noise: n/a&lt;br /&gt;\r\n	Warranty: 1 Year&amp;nbsp;&lt;br /&gt;\r\n	Gi&amp;aacute;: $1,199&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;8. BenQ W7000&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Brightness: 2000 Lumens&lt;br /&gt;\r\n	Contrast: 50000:1&lt;br /&gt;\r\n	Technology: DLP&lt;br /&gt;\r\n	Lamp Life: 2000 Hours&lt;br /&gt;\r\n	Audible Noise: 33 dB&lt;br /&gt;\r\n	Warranty: 1 Year&lt;br /&gt;\r\n	Gi&amp;aacute;: $2,299&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;9. JVC DLA-X30&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Brightness: 1300 Lumens&lt;br /&gt;\r\n	Contrast: 50000:1&lt;br /&gt;\r\n	Technology: 0.7&amp;quot; D-ILA&lt;br /&gt;\r\n	Lamp Life: 3000 Hours&lt;br /&gt;\r\n	Audible Noise: 20 dB&lt;br /&gt;\r\n	Warranty: 2 Years&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;10. Sony VPL-VW95ES&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Brightness: 1000 Lumens&lt;br /&gt;\r\n	Contrast: 150000:1&lt;br /&gt;\r\n	Technology: 0.6&amp;quot; SXRD&lt;br /&gt;\r\n	Lamp Life: n/a&lt;br /&gt;\r\n	Audible Noise: 20 dB&lt;br /&gt;\r\n	Warranty: 3 Years&lt;/p&gt;', 'lam-sao-sam-duoc-may-chieu-hd-ung-y-1', '', '', '', '', '', '', 0, 0, 'upload/PCP324.otfeat.optima_hd23-380-75.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 18:53:11', 'admin', '2012-10-10 18:53:11');
INSERT INTO `media` VALUES (79, 'default1349866571', '', 'media_news', '[6],', 'admin', 'Sholay được chuyển thể sang 3D sau 37 năm', '', '', 'vn', '&lt;p&gt;\r\n	Sholay &amp;ndash; bộ phim được sản xuất v&amp;agrave;o năm 1975 của Ấn Độ đ&amp;atilde; được chuyển thể sang phi&amp;ecirc;n bản 3D dự kiến sẽ ph&amp;aacute;t h&amp;agrave;nh v&amp;agrave;o th&amp;aacute;ng 8 tới. Đạo diễn Ram Gopal Verma đ&amp;atilde; từng cố gắng l&amp;agrave;m lại bộ phim n&amp;agrave;y nhưng kh&amp;ocirc;ng th&amp;agrave;nh c&amp;ocirc;ng.&lt;/p&gt;', '&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Sholay &amp;ndash; bộ phim được sản xuất v&amp;agrave;o năm 1975 của Ấn Độ đ&amp;atilde; được chuyển thể sang phi&amp;ecirc;n bản 3D dự kiến sẽ ph&amp;aacute;t h&amp;agrave;nh v&amp;agrave;o th&amp;aacute;ng 8 tới. Đạo diễn Ram Gopal Verma đ&amp;atilde; từng cố gắng l&amp;agrave;m lại bộ phim n&amp;agrave;y nhưng kh&amp;ocirc;ng th&amp;agrave;nh c&amp;ocirc;ng.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;http://i1080.photobucket.com/albums/j332/Hien_Yen/Sholay-picture.jpg&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n	Việc chuyển đổi sang phi&amp;ecirc;n bản 3D kh&amp;ocirc;ng c&amp;oacute; nghĩa l&amp;agrave; c&amp;aacute;i g&amp;igrave; đ&amp;oacute; to t&amp;aacute;t, nhưng bộ phim c&amp;oacute; thời lượng 3h18&amp;rsquo; dự kiến sẽ trở th&amp;agrave;nh bom tấn thực sự ở Ấn Độ.&lt;br /&gt;\r\n	&amp;ldquo;Ch&amp;uacute;ng t&amp;ocirc;i đang l&amp;agrave;m việc cật lực để chuyển thể v&amp;agrave; c&amp;ocirc;ng việc sắp ho&amp;agrave;n th&amp;agrave;nh. Dự kiến sẽ c&amp;ocirc;ng chiếu v&amp;agrave;o 15/8/2012. Mặc d&amp;ugrave; bộ phim cũ, nhưng mọi người sẽ vấn th&amp;iacute;ch th&amp;uacute; đến rạp xem phim với c&amp;ocirc;ng nghệ 3D&amp;rdquo;&amp;nbsp;Shan Uttam Singh, ch&amp;aacute;u trai của Ramesh Sippy- một trong những người đồng sở hữu phim gốc khẳng định.&amp;nbsp;&lt;br /&gt;\r\n	Hơn 150 kỹ thuật tại Maya Digital Studio đ&amp;atilde; l&amp;agrave;m việc chuyển thể hơn một năm nay. Frank Foster v&amp;agrave; Hollywood DI studio cũng tham gia v&amp;agrave;o dự &amp;aacute;n n&amp;agrave;y. C&amp;ocirc;ng việc l&amp;agrave; phải dựng lại tr&amp;ecirc;n 5.000 bức ảnh v&amp;agrave; 2.850.120 khung h&amp;igrave;nh ri&amp;ecirc;ng lẻ để tạo l&amp;ecirc;n phi&amp;ecirc;n bản 3D.&lt;/p&gt;', 'sholay-duoc-chuyen-the-sang-3d-sau-37-nam-1', '', '', '', '', '', '', 0, 0, 'upload/Sholay-picture.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 18:56:11', 'admin', '2012-10-10 18:56:11');
INSERT INTO `media` VALUES (80, 'default1349867084', '', 'media_news', '[6],', 'admin', 'Tương lai của TV 3D ra sao?', '', '', 'vn', '&lt;p&gt;\r\n	&lt;span style=&quot;text-align: justify; &quot;&gt;Điều g&amp;igrave; l&amp;agrave;m cho những gia đ&amp;igrave;nh ở Mỹ đua nhau mua Tivi 3D? C&amp;oacute; phải l&amp;agrave; chức năng 3D tr&amp;ecirc;n Tivi? Thực tế kh&amp;ocirc;ng phải như vậy.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Điều g&amp;igrave; l&amp;agrave;m cho những gia đ&amp;igrave;nh ở Mỹ đua nhau mua Tivi 3D? C&amp;oacute; phải l&amp;agrave; chức năng 3D tr&amp;ecirc;n Tivi? Thực tế kh&amp;ocirc;ng phải như vậy. Doanh số b&amp;aacute;n ra của Tivi 3D cao l&amp;agrave; do c&amp;oacute; kết nối internet t&amp;iacute;ch hợp trong Tivi 3D mang lại. Liệu điều n&amp;agrave;y c&amp;oacute; ho&amp;agrave;n to&amp;agrave;n đ&amp;uacute;ng?&lt;/p&gt;\r\n&lt;div style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; height=&quot;auto&quot; src=&quot;images/autosize-800x800/upload/TV 3D.jpg&quot; width=&quot;auto&quot; /&gt;&lt;/div&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Theo Hiệp hội điện tử ti&amp;ecirc;u d&amp;ugrave;ng của Mỹ, c&amp;oacute; &amp;iacute;t hơn &amp;frac14; kh&amp;aacute;ch h&amp;agrave;ng tiềm năng chọn mua TV3D. Vậy TV 3D đang tr&amp;ecirc;n bờ vực rồi sao? C&amp;acirc;u trả lời l&amp;agrave; KH&amp;Ocirc;NG&lt;br /&gt;\r\n	Những người ủng hộ c&amp;ocirc;ng nghệ nghệ n&amp;agrave;y cũng phải thừa nhận l&amp;agrave; n&amp;oacute; kh&amp;ocirc;ng c&amp;ograve;n ph&amp;aacute;t triển rầm rộ như đầu năm 2010 khi m&amp;agrave;&amp;nbsp;&lt;a href=&quot;http://www.exphim.com/2012/05/hanh-trinh-xuong-ia-nguc-cua-ao-dien.html&quot; rel=&quot;&quot; target=&quot;&quot;&gt;bộ phim 3D bom tấm Avatar&lt;/a&gt;&amp;nbsp;c&amp;ocirc;ng chiếu.&lt;br /&gt;\r\n	Tuy nhi&amp;ecirc;n những ph&amp;acirc;n t&amp;iacute;ch của c&amp;aacute;c nh&amp;agrave; chuy&amp;ecirc;n m&amp;ocirc;n chỉ ra người ti&amp;ecirc;u d&amp;ugrave;ng vẫn mong muốn TV vừa th&amp;ocirc;ng minh nhưng cũng phải c&amp;oacute; t&amp;iacute;nh năng 3D. &amp;ldquo;Sự quan t&amp;acirc;m của kh&amp;aacute;ch h&amp;agrave;ng vẫn c&amp;ograve;n th&amp;igrave; vẫn c&amp;oacute; thể lạc quan&amp;rdquo;.&lt;br /&gt;\r\n	Nhưng theo Samsung th&amp;igrave; người ti&amp;ecirc;u d&amp;ugrave;ng quan t&amp;acirc;m t&amp;iacute;nh năng &amp;ldquo;smart TV&amp;rdquo; hơn l&amp;agrave; 3D. Vậy ph&amp;aacute;t biểu đ&amp;oacute; c&amp;oacute; nghĩa l&amp;agrave; g&amp;igrave;? Chẳng qua l&amp;agrave; quan điểm c&amp;aacute; nh&amp;acirc;n của Samsung th&amp;ocirc;i v&amp;igrave; hiện Samsung đang c&amp;oacute; rất nhiều model cũ sử dụng c&amp;ocirc;ng nghệ chủ động (k&amp;iacute;nh phải sử dụng pin) &amp;iacute;t được người d&amp;ugrave;ng th&amp;iacute;ch th&amp;uacute;. &amp;ldquo;Họ đang cố gắng để cải thiện t&amp;igrave;nh h&amp;igrave;nh&amp;rdquo; Doherty chuy&amp;ecirc;n gia ph&amp;acirc;n t&amp;iacute;ch chia sẻ.&lt;br /&gt;\r\n	Theo Rod Riegel &amp;ndash; người ph&amp;aacute;t ng&amp;ocirc;n của 3Net (Chương tr&amp;igrave;nh truyền h&amp;igrave;nh 3D 24/24 hợp t&amp;aacute;c bởi Sony, Discovery, IMAX) chia sẻ rằng doanh số TV3D của Mỹ đạt 15,3 triệu v&amp;agrave;o cuối năm nay. Cả Rod Riegel v&amp;agrave; Doherty đều dự đo&amp;aacute;n cuối năm 2012 con số đ&amp;oacute; đạt 30 triệu chiếc. Chiếm 1/3 doanh số TV c&amp;aacute;c loại.&lt;br /&gt;\r\n	Về nội dung c&amp;aacute;c chương tr&amp;igrave;nh 3D c&amp;oacute; kh&amp;aacute; nhiều k&amp;ecirc;nh truyền h&amp;igrave;nh nhảy v&amp;agrave;o cung cấp nội dung như 3Net, Disney&amp;rsquo;s ESPN 3D... Cũng theo nhiều chuy&amp;ecirc;n gia th&amp;igrave; thị trường TV3D tại Trung Quốc sẽ đạt con số 22 triệu TV3D.&lt;br /&gt;\r\n	Vậy th&amp;igrave; TV3D vẫn được ch&amp;agrave;o đ&amp;oacute;n đấy chứ!&lt;/p&gt;', 'tuong-lai-cua-tv-3d-ra-sao-1', '', '', '', '', '', '', 0, 0, 'upload/TV 3D.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 19:04:44', 'admin', '2012-10-10 19:04:44');
INSERT INTO `media` VALUES (81, 'default1349867492', '', 'media_news', '[6],', 'admin', 'Công ty TNHH HTN tuyển kỹ thuật viên', '', '', 'vn', '', '&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;M&amp;ocirc; tả c&amp;ocirc;ng việc:&lt;/strong&gt;&lt;/div&gt;\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Phụ tr&amp;aacute;ch về kỹ thuật, chịu tr&amp;aacute;ch nhiệm ch&amp;iacute;nh trong việc đi lắp đặt m&amp;aacute;y t&amp;iacute;nh, thiết bị &amp;acirc;m thanh, m&amp;aacute;y chiếu,..&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Tư vấn kỹ thuật cho kh&amp;aacute;ch h&amp;agrave;ng khi cần thiết&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Chuẩn bị c&amp;aacute;c thiết bị cần thiết trước khi đi lắp m&amp;aacute;y&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Phụ tr&amp;aacute;ch cập nhật website &lt;a href=&quot;http://www.htn.vn&quot;&gt;www.htn.vn&lt;/a&gt; bao gồm:&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;ul style=&quot;margin-left: 40px; &quot;&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Trả lời y&amp;ecirc;u cầu của kh&amp;aacute;ch h&amp;agrave;ng gửi v&amp;agrave;o website / email c&amp;ocirc;ng ty&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Cập nhật website bao gồm: h&amp;igrave;nh ảnh, b&amp;agrave;i viết, tổng hợp th&amp;ocirc;ng tin từ c&amp;aacute;c website kh&amp;aacute;c để cập nhật v&amp;agrave;o website.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Phụ tr&amp;aacute;ch post b&amp;agrave;i tr&amp;ecirc;n c&amp;aacute;c diễn đ&amp;agrave;n c&amp;ocirc;ng ty đang tham gia&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Chịu tr&amp;aacute;ch nhiệm quản l&amp;yacute; văn ph&amp;ograve;ng khi cấp tr&amp;ecirc;n đi c&amp;ocirc;ng t&amp;aacute;c v&amp;agrave; c&amp;aacute;c c&amp;ocirc;ng việc kh&amp;aacute;c theo y&amp;ecirc;u cầu (khi cần thiết).&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		L&amp;agrave;m việc ch&amp;iacute;nh tại Ph&amp;uacute; Nhuận.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;Lương&lt;/strong&gt;: Lương cơ bản 3.000.000 &amp;ndash; 4.000.000 VNĐ + phụ cấp + thưởng theo chế độ c&amp;ocirc;ng ty + c&amp;ocirc;ng t&amp;aacute;c ph&amp;iacute;&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;Y&amp;ecirc;u cầu&lt;/strong&gt;&lt;/div&gt;\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Tốt nghiệp Cao đẳng, ưu ti&amp;ecirc;n ng&amp;agrave;nh b&amp;aacute;ch khoa bao gồm tin học, điện &amp;ndash; điện tử, kỹ sư&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Nam, tuổi từ 22 - 35&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Nhiệt t&amp;igrave;nh, tinh thần l&amp;agrave;m việc t&amp;iacute;ch cực&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Nhanh nhẹn, chịu học hỏi, sẵn s&amp;agrave;ng đi c&amp;ocirc;ng t&amp;aacute;c tỉnh&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;Cơ hội&lt;/strong&gt;&lt;/div&gt;\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Được đ&amp;agrave;o tạo về kỹ thuật lắp m&amp;aacute;y t&amp;iacute;nh, hệ thống chiếu phim, &amp;acirc;m thanh h&amp;igrave;nh ảnh cao cấp, c&amp;ocirc;ng nghệ lắp đặt ph&amp;ograve;ng ch&amp;iacute;&amp;ecirc;u phim gia đ&amp;igrave;nh&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		L&amp;agrave;m việc trong m&amp;ocirc;i trường năng động, linh hoạt, chuy&amp;ecirc;n nghiệp&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	Cơ hội tiếp cận c&amp;aacute;c sản phẩm, thiết bị, c&amp;ocirc;ng nghệ h&amp;igrave;nh ảnh &amp;acirc;m thanh mới nhất.&lt;/p&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;Li&amp;ecirc;n hệ:&lt;/strong&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Ms.Huy&amp;ecirc;n : 0978.181.018&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	C&amp;ocirc;ng ty TNHH HTN&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	Webiste:&lt;a href=&quot;http://www.htn.vn&quot;&gt;http://www.htn.vn&lt;/a&gt;&lt;/div&gt;\r\n&lt;div style=&quot;text-align: justify; &quot;&gt;\r\n	110 Hoa C&amp;uacute;c, Phường 1, Quận Ph&amp;uacute; Nhuận&lt;/div&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;', 'cong-ty-tnhh-htn-tuyen-ky-thuat-vien-1', '', '', '', '', '', '', 0, 0, 'upload/TuyenDung.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 19:11:32', 'admin', '2012-10-10 19:11:32');
INSERT INTO `media` VALUES (82, 'default1349867720', '', 'media_news', '[6],', 'admin', 'Thư mời tham quan Phòng chiếu phim 3D demo tại Hội chợ Vietbuild HCM', '', '', 'vn', '&lt;p&gt;\r\n	C&amp;ocirc;ng ty Ho&amp;agrave;ng Tuấn Nguy&amp;ecirc;n tr&amp;acirc;n trọng k&amp;iacute;nh mời qu&amp;yacute; kh&amp;aacute;ch h&amp;agrave;ng đến tham quan v&amp;agrave; tận hưởng cảm gi&amp;aacute;c v&amp;ocirc; c&amp;ugrave;ng th&amp;uacute; vị c&amp;ugrave;ng ch&amp;uacute;ng t&amp;ocirc;i với PH&amp;Ograve;NG CHIẾU PHIM 3D DEMO CHO GIA Đ&amp;Igrave;NH tại triển l&amp;atilde;m Vietbuild HCM 2012&lt;/p&gt;', '&lt;p&gt;\r\n	C&amp;ocirc;ng ty Ho&amp;agrave;ng Tuấn Nguy&amp;ecirc;n tr&amp;acirc;n trọng k&amp;iacute;nh mời qu&amp;yacute; kh&amp;aacute;ch h&amp;agrave;ng đến tham quan v&amp;agrave; tận hưởng cảm gi&amp;aacute;c v&amp;ocirc; c&amp;ugrave;ng th&amp;uacute; vị c&amp;ugrave;ng ch&amp;uacute;ng t&amp;ocirc;i với PH&amp;Ograve;NG CHIẾU PHIM 3D DEMO CHO GIA Đ&amp;Igrave;NH tại triển l&amp;atilde;m Vietbuild HCM 2012&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;images/autosize-800x800/upload/image001.jpg&quot; style=&quot;height: 394px; width: 700px; &quot; /&gt;&lt;/p&gt;', '', '', '', '', '', '', '', 0, 0, 'upload/image001.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 19:15:20', 'admin', '2012-10-10 19:15:20');
INSERT INTO `media` VALUES (83, 'default1349868254', '', 'media_news', '[6],', 'admin', 'Hình ảnh công ty Hoàng Tuấn Nguyên tại hội chợ Vietbuild HCM 2012', '', '', 'vn', '&lt;p&gt;\r\n	Vừa qua, C&amp;ocirc;ng ty Ho&amp;agrave;ng Tuấn Nguy&amp;ecirc;n đ&amp;atilde; tham gia Hội chợ triển l&amp;atilde;m Vietbuild 2012 diễn ra từ ng&amp;agrave;y 13/09 &amp;ndash; 17/09/2012 tại Th&amp;agrave;nh phố HCM.&lt;/p&gt;', '&lt;p&gt;\r\n	Vừa qua, C&amp;ocirc;ng ty Ho&amp;agrave;ng Tuấn Nguy&amp;ecirc;n đ&amp;atilde; tham gia Hội chợ triển l&amp;atilde;m Vietbuild 2012 diễn ra từ ng&amp;agrave;y 13/09 &amp;ndash; 17/09/2012 tại Th&amp;agrave;nh phố HCM. Đ&amp;acirc;y l&amp;agrave; hội chợ về ng&amp;agrave;nh x&amp;acirc;y dựng &amp;amp; trang tr&amp;iacute; nội ngoại thất h&amp;agrave;ng năm lớn nhất tại Th&amp;agrave;nh Phố Hồ Ch&amp;iacute; Minh v&amp;agrave; c&amp;aacute;c th&amp;agrave;nh phố lớn kh&amp;aacute;c tr&amp;ecirc;n cả nước.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Tham gia hội chợ lần n&amp;agrave;y, c&amp;ocirc;ng ty Ho&amp;agrave;ng Tuấn Nguy&amp;ecirc;n đ&amp;atilde; x&amp;acirc;y dựng một&amp;nbsp;ph&amp;ograve;ng chiếu phim 3D demo d&amp;agrave;nh cho gia đ&amp;igrave;nh&amp;nbsp;với gi&amp;aacute; trị trọn g&amp;oacute;i chỉ c&amp;oacute;&amp;nbsp;70,000,000 VNĐ&amp;nbsp;cho to&amp;agrave;n bộ hệ thống m&amp;aacute;y m&amp;oacute;c thiết bị. Với m&amp;ocirc; h&amp;igrave;nh n&amp;agrave;y, c&amp;ocirc;ng ty HTN đ&amp;atilde; thu h&amp;uacute;t được&amp;nbsp;rất nhiều kh&amp;aacute;ch h&amp;agrave;ng đến&amp;nbsp;tham quan v&amp;agrave; t&amp;igrave;m hiểu th&amp;ocirc;ng tin sản phẩm. Ngo&amp;agrave;i hệ thống chiếu phim 3D, ch&amp;uacute;ng t&amp;ocirc;i c&amp;ograve;n mang đến một c&amp;ocirc;ng nghệ tr&amp;igrave;nh chiếu độc đ&amp;aacute;o mới: c&amp;ocirc;ng nghệ&amp;nbsp;Holoscreen&amp;nbsp;&amp;ndash; hiển thị h&amp;igrave;nh ảnh tr&amp;ecirc;n bề mặt k&amp;iacute;nh, đ&amp;atilde; thực sự g&amp;acirc;y ấn tượng v&amp;ocirc; c&amp;ugrave;ng mạnh mẽ cho kh&amp;aacute;ch đến tham quan.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Kh&amp;aacute;ch h&amp;agrave;ng đến với ph&amp;ograve;ng chiếu phim 3D demo của Ho&amp;agrave;ng Tuấn Nguy&amp;ecirc;n tại Vietbuild kh&amp;ocirc;ng chỉ được tận hưởng cảm gi&amp;aacute;c v&amp;ocirc; c&amp;ugrave;ng th&amp;uacute; vị v&amp;agrave; ho&amp;agrave;nh tr&amp;aacute;ng của thế giới phim ảnh m&amp;agrave; c&amp;ograve;n được đội ngũ chuy&amp;ecirc;n vi&amp;ecirc;n kỹ thuật của ch&amp;uacute;ng t&amp;ocirc;i tư vấn tận t&amp;igrave;nh về những giải ph&amp;aacute;p 3D tối ưu nhất.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Qua 05 ng&amp;agrave;y tham gia tại hội chợ, c&amp;ocirc;ng ty HTN rất vui v&amp;agrave; tự h&amp;agrave;o khi nhận được sự quan t&amp;acirc;m đ&amp;ocirc;ng đảo v&amp;agrave; nhiều đơn đặt h&amp;agrave;ng của kh&amp;aacute;ch tham quan khi m&amp;ocirc; h&amp;igrave;nh ph&amp;ograve;ng chiếu phim 3D cho gia đ&amp;igrave;nh tưởng chừng như xa xỉ v&amp;agrave; k&amp;eacute;n thị trường trong thời buổi kinh tế như hiện nay nay đ&amp;atilde; nằm trong tầm tay với chi ph&amp;iacute; v&amp;agrave; chất lượng hợp l&amp;yacute;.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Điều n&amp;agrave;y l&amp;agrave; động lực rất lớn cho ch&amp;uacute;ng t&amp;ocirc;i trong việc ng&amp;agrave;y c&amp;agrave;ng ph&amp;aacute;t triển hơn nữa loại h&amp;igrave;nh n&amp;agrave;y cũng như kh&amp;ocirc;ng ngừng cập nhật những c&amp;ocirc;ng nghệ ti&amp;ecirc;n tiến nhất để đ&amp;aacute;p ứng nhu cầu giải tr&amp;iacute; ng&amp;agrave;y c&amp;agrave;ng cao của người d&amp;acirc;n nhưng với gi&amp;aacute; trị sản phẩm v&amp;ocirc; c&amp;ugrave;ng kinh tế.&lt;/p&gt;\r\n&lt;p&gt;\r\n	C&amp;ocirc;ng ty Ho&amp;agrave;ng Tuấn Nguy&amp;ecirc;n xin gửi lời cảm ơn ch&amp;acirc;n th&amp;agrave;nh đến tất cả qu&amp;yacute; kh&amp;aacute;ch h&amp;agrave;ng đ&amp;atilde; đến tham quan v&amp;agrave; ủng hộ sản phẩm của c&amp;ocirc;ng ty ch&amp;uacute;ng t&amp;ocirc;i tại hội chợ Vietbuild 2012. Ho&amp;agrave;ng Tuấn Nguy&amp;ecirc;n lu&amp;ocirc;n cam kết mang đến cho kh&amp;aacute;ch h&amp;agrave;ng sản phẩm chất lượng tốt nhất với chi ph&amp;iacute; hợp l&amp;iacute; nhất.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Trong tương lai gần, Ho&amp;agrave;ng Tuấn Nguy&amp;ecirc;n sẽ giới thiệu m&amp;ocirc; h&amp;igrave;nh n&amp;agrave;y đến c&amp;aacute;c tỉnh th&amp;agrave;nh kh&amp;aacute;c trong cả nước, ch&amp;uacute;ng t&amp;ocirc;i rất mong tiếp tục nhận được sự quan t&amp;acirc;m v&amp;agrave; đ&amp;oacute;n nhận của tất cả qu&amp;yacute; kh&amp;aacute;ch h&amp;agrave;ng cho sản phẩm ph&amp;ograve;ng chiếu phim 3D cũng như c&amp;aacute;c sản phẩm kh&amp;aacute;c của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/p&gt;\r\n&lt;p&gt;\r\n	Một số h&amp;igrave;nh ảnh ti&amp;ecirc;u biểu&amp;nbsp;của C&amp;ocirc;ng ty Ho&amp;agrave;ng Tuấn Nguy&amp;ecirc;n tại hội chợ Vietbuild 2012:&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;images/autosize-800x800/upload/DSC_0063_zps1f002ef2.jpg&quot; style=&quot;height: 393px; width: 700px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;images/autosize-800x800/upload/t7-1.jpg&quot; style=&quot;height: 938px; width: 700px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;images/autosize-800x800/upload/photo2_zps381cce01.jpg&quot; style=&quot;width: 700px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;images/autosize-800x800/upload/photo3_zps3ca9ef25.jpg&quot; style=&quot;height: 938px; width: 700px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;images/autosize-800x800/upload/photo4_zps6ea8f246.jpg&quot; style=&quot;height: 938px; width: 700px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;images/autosize-800x800/upload/photo5_zps9baf679e.jpg&quot; style=&quot;height: 1014px; width: 700px; &quot; /&gt;&lt;/p&gt;', 'hinh-anh-cong-ty-hoang-tuan-nguyen-tai-hoi-cho-vietbuild-hcm-2012', '', '', '', '', '', '', 0, 0, 'upload/DSC_0063_zps1f002ef2.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-10 19:27:43', 'admin', '2012-10-10 19:27:43');
INSERT INTO `media` VALUES (85, 'default1349869356', '', 'media_news', '[6]', 'admin', 'HOLOSCREEN', '', '', 'vn', '&lt;p&gt;\r\n	C&amp;ocirc;ng cụ hữu hiệu cho truyền th&amp;ocirc;ng, quảng c&amp;aacute;o v&amp;agrave; tổ chức sự kiện!!!&lt;/p&gt;', '&lt;p&gt;\r\n	C&amp;ocirc;ng cụ hữu hiệu cho truyền th&amp;ocirc;ng, quảng c&amp;aacute;o v&amp;agrave; tổ chức sự kiện!!!&lt;/p&gt;\r\n&lt;p&gt;\r\n	C&amp;oacute; một thời gian khi c&amp;ocirc;ng nghệ tr&amp;igrave;nh chiếu chỉ d&amp;agrave;nh cho c&amp;aacute;c ph&amp;ograve;ng họp hoặc nh&amp;agrave; h&amp;aacute;t. Điều đ&amp;oacute; đ&amp;atilde; kh&amp;ocirc;ng c&amp;ograve;n nữa khi giờ đ&amp;acirc;y bạn đ&amp;atilde; c&amp;oacute; thể đưa h&amp;igrave;nh ảnh l&amp;ecirc;n bất k&amp;igrave; bề mặt k&amp;iacute;nh n&amp;agrave;o trong cửa h&amp;agrave;ng, ph&amp;ograve;ng trưng b&amp;agrave;y, khu triển l&amp;atilde;m,&amp;hellip; biến n&amp;oacute; trở th&amp;agrave;nh điểm thu h&amp;uacute;t v&amp;agrave; t&amp;aacute;c động mạnh mẽ nhất đến kh&amp;aacute;ch h&amp;agrave;ng với c&amp;ocirc;ng nghệ hiển thị m&amp;agrave;n h&amp;igrave;nh của HOLOSCREEN &amp;ndash; m&amp;agrave;n chiếu trong suốt sử dụng tr&amp;ecirc;n bề mặt k&amp;iacute;nh.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;images/autosize-800x800/upload/Holo.jpg&quot; style=&quot;height: 378px; width: 700px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;Ứng dụng:&lt;/strong&gt;&lt;/p&gt;\r\n&lt;ul&gt;\r\n	&lt;li&gt;\r\n		Quảng c&amp;aacute;o kỹ thuật số cho shop, showroom, c&amp;aacute;c khu vực c&amp;ocirc;ng cộng kh&amp;aacute;c như trung t&amp;acirc;m thương mại, s&amp;acirc;n bay, hội chợ, bảo t&amp;agrave;ng, triển l&amp;atilde;m hoặc c&amp;aacute;c tổ chức sự kiện...&lt;/li&gt;\r\n	&lt;li&gt;\r\n		Khu vực tiếp t&amp;acirc;n, ph&amp;ograve;ng họp c&amp;ocirc;ng ty.&lt;/li&gt;\r\n	&lt;li&gt;\r\n		C&amp;aacute;c khu vực chức năng, ph&amp;ograve;ng điều h&amp;agrave;nh.&amp;nbsp;&lt;/li&gt;\r\n	&lt;li&gt;\r\n		Phục vụ cho c&amp;aacute;c chương tr&amp;igrave;nh đ&amp;agrave;o tạo, giảng dạy v&amp;agrave; học tập.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;images/autosize-800x800/upload/4088611839_80e40203e7_z.jpg&quot; style=&quot;height: 524px; width: 700px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	Đặc biệt, với Holoscreen, ch&amp;uacute;ng t&amp;ocirc;i c&amp;ograve;n mang đến cho bạn c&amp;ocirc;ng nghệ cảm ứng trực tiếp tr&amp;ecirc;n m&amp;agrave;n h&amp;igrave;nh v&amp;ocirc; c&amp;ugrave;ng ấn tượng, lần đầu ti&amp;ecirc;n c&amp;oacute; mặt tại Việt Nam.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; height=&quot;auto&quot; src=&quot;images/autosize-800x800/upload/holo-screen.jpg&quot; width=&quot;auto&quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;Tại sao d&amp;ugrave;ng HOLOSCREEN:&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;Chi ph&amp;iacute; tối ưu&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Tiết kiệm tối đa chi ph&amp;iacute; quảng c&amp;aacute;o so với c&amp;aacute;c h&amp;igrave;nh thức quảng c&amp;aacute;o ngo&amp;agrave;i trời, truyền h&amp;igrave;nh,&amp;hellip; m&amp;agrave; hiệu quả mang lại tương đương.&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Gi&amp;aacute; cả phải chăng hơn so với m&amp;agrave;n h&amp;igrave;nh kh&amp;aacute;c (LCD, LED &amp;hellip;).&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;Hiệu ứng ho&amp;agrave;n hảo&lt;/strong&gt;&lt;/p&gt;\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Tăng khả năng hiển thị sản phẩm, tạo ra sự tương t&amp;aacute;c mạnh mẽ của sản phẩm với kh&amp;aacute;ch bộ h&amp;agrave;nh hay người đi đường.&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Cho ph&amp;eacute;p đưa th&amp;ocirc;ng điệp quảng c&amp;aacute;o của bạn tới trực tiếp kh&amp;aacute;ch h&amp;agrave;ng với h&amp;igrave;nh ảnh v&amp;ocirc; c&amp;ugrave;ng sống động v&amp;agrave; độ sắc n&amp;eacute;t hơn bao giờ hết tr&amp;ecirc;n c&amp;aacute;c bề mặt k&amp;iacute;nh.&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		Được thiết kế đặc biệt để sử dụng trong điều kiện &amp;aacute;nh s&amp;aacute;ng m&amp;ocirc;i trường xung quanh cao, kh&amp;aacute;ng tia UV v&amp;agrave; thời tiết, với sự t&amp;aacute;c động tối đa tới người xem từ mọi g&amp;oacute;c độ.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong&gt;Tiện &amp;iacute;ch kh&amp;ocirc;ng ngờ&lt;/strong&gt;&lt;/p&gt;\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		&amp;nbsp; Giải ph&amp;aacute;p l&amp;yacute; tưởng để giảm bớt kh&amp;ocirc;ng gian tổng thể, tận dụng tối đa vị tr&amp;iacute; ngay tại shop, showroom, đặc biệt rất ph&amp;ugrave; hợp với window display cho m&amp;ocirc; h&amp;igrave;nh b&amp;aacute;n lẻ retail&amp;hellip;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		&amp;nbsp; Dễ d&amp;agrave;ng thay đổi nội dung h&amp;igrave;nh ảnh, tr&amp;igrave;nh chiếu được cả h&amp;igrave;nh tĩnh v&amp;agrave; động linh hoạt.&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		&amp;nbsp; L&amp;yacute; tưởng khi t&amp;iacute;ch hợp với những h&amp;igrave;nh ảnh đồ họa kh&amp;aacute;c tr&amp;ecirc;n bề mặt k&amp;iacute;nh&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		&amp;nbsp; K&amp;iacute;ch thước t&amp;ugrave;y chỉnh linh hoạt theo y&amp;ecirc;u cầu.&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		&amp;nbsp; Lắp đặt đơn giản v&amp;agrave; nhanh ch&amp;oacute;ng.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;images/autosize-800x800/upload/holo4.jpg&quot; style=&quot;height: 483px; width: 700px; &quot; /&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img alt=&quot;&quot; src=&quot;images/autosize-800x800/upload/holo5.jpg&quot; style=&quot;height: 479px; width: 700px; &quot; /&gt;&lt;/p&gt;', 'holoscreen-0', '', '', '', '', '', '', 0, 0, 'upload/Holo.jpg', 0, '', '', '', 0, 0, 'active', '', '2012-10-11 15:36:55', 'admin', '2012-10-11 15:36:55');
