INSERT INTO `media` VALUES (97, 'default13521790831', '', 'media_news', '[77]', 'admin', 'TUYỂN THIẾT KẾ ĐỒ HỌA WEB - WEB DESIGNER', '', '', 'vn', '&lt;p&gt;\r\n	Cơ hội l&amp;agrave;m việc với một m&amp;ocirc;i trường chuy&amp;ecirc;n nghiệp v&amp;agrave; đầy s&amp;aacute;ng tạo, ch&amp;uacute;ng t&amp;ocirc;i kh&amp;ocirc;ng cần biết bạn học từ m&amp;ocirc;i trường n&amp;agrave;o ra, hay bạn đạt đến học vị n&amp;agrave;o, ch&amp;uacute;ng t&amp;ocirc;i chỉ cần quan t&amp;acirc;m đến năng khiếu, t&amp;iacute;nh thẩm mỹ, niềm đam m&amp;ecirc; v&amp;agrave; nhiệt huyết với c&amp;ocirc;ng việc của c&amp;aacute;c bạn. Nếu bạn chưa c&amp;oacute; kinh nghiệm, ch&amp;uacute;ng t&amp;ocirc;i sẽ đ&amp;agrave;o tạo.&lt;/p&gt;', '&lt;p&gt;\r\n	&lt;a href=&quot;http://elifepartners.com&quot;&gt;Elife Partners&lt;/a&gt; l&amp;agrave; chuỗi c&amp;ocirc;ng ty li&amp;ecirc;n kết kỹ thuật đa quốc gia chuy&amp;ecirc;n về lĩnh vực hạ tầng ứng dụng internet, ch&amp;uacute;ng t&amp;ocirc;i l&amp;agrave; sự kết hợp về kỹ thuật của những kỹ sư c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin giỏi ở nhiều quốc gia, trong đ&amp;oacute; c&amp;oacute; Brazil, Anh, Mỹ, Singapore.&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;a href=&quot;http://elifepartners.com&quot;&gt;Elife Partners&lt;/a&gt; l&amp;agrave; sự &amp;nbsp;kết hợp tuyệt vời giữa tư duy to&amp;agrave;n cầu v&amp;agrave; kinh nghiệm bản địa, ở Việt Nam, đội ngũ Elife bao gồm:&lt;/p&gt;\r\n&lt;p&gt;\r\n	+ Du học sinh Việt Nam c&amp;oacute; kiến thức &amp;amp; kinh nghiệm l&amp;agrave;m việc tại c&amp;aacute;c nước Ch&amp;acirc;u &amp;Acirc;u&lt;/p&gt;\r\n&lt;p&gt;\r\n	+ Những nh&amp;agrave; quản l&amp;yacute;, điều h&amp;agrave;nh c&amp;oacute; kinh nghiệm về thị trường internet Việt Nam ngay từ giai đoạn thai ngh&amp;eacute;n.&lt;/p&gt;\r\n&lt;p&gt;\r\n	+ Những kỹ sư c&amp;ocirc;ng nghệ th&amp;ocirc;ng tin y&amp;ecirc;u nghề&lt;/p&gt;\r\n&lt;p&gt;\r\n	+ Những chuy&amp;ecirc;n vi&amp;ecirc;n thiết kế đầy s&amp;aacute;ng tạo&lt;/p&gt;\r\n&lt;p&gt;\r\n	Năm 2012, Elife Partners được th&amp;agrave;nh lập tại Việt Nam với sứ mệnh x&amp;acirc;y dựng một thương hiệu uy t&amp;iacute;nh h&amp;agrave;ng đầu về ứng dụng internet tại Việt Nam.&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center;&quot;&gt;\r\n	&lt;strong style=&quot;font-size: 18px;&quot;&gt;&lt;span style=&quot;font-family: tahoma, geneva, sans-serif;&quot;&gt;H&amp;Atilde;Y THAM GIA ĐỘI NGŨ S&amp;Aacute;NG TẠO C&amp;Ugrave;NG GIA Đ&amp;Igrave;NH ELIFE PARTNERS&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	Cơ hội l&amp;agrave;m việc với một m&amp;ocirc;i trường chuy&amp;ecirc;n nghiệp v&amp;agrave; đầy s&amp;aacute;ng tạo, ch&amp;uacute;ng t&amp;ocirc;i kh&amp;ocirc;ng cần biết bạn học từ m&amp;ocirc;i trường n&amp;agrave;o ra, hay bạn đạt đến học vị n&amp;agrave;o, ch&amp;uacute;ng t&amp;ocirc;i chỉ cần quan t&amp;acirc;m đến năng khiếu, t&amp;iacute;nh thẩm mỹ, niềm đam m&amp;ecirc; v&amp;agrave; nhiệt huyết với c&amp;ocirc;ng việc của c&amp;aacute;c bạn. Nếu bạn chưa c&amp;oacute; kinh nghiệm, ch&amp;uacute;ng t&amp;ocirc;i sẽ đ&amp;agrave;o tạo.&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-family: tahoma, geneva, sans-serif;&quot;&gt;&lt;strong&gt;Vị tr&amp;iacute; tuyển dụng:&lt;/strong&gt;&amp;nbsp;Nh&amp;acirc;n vi&amp;ecirc;n Design, thiết kế đồ họa web&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-family: tahoma, geneva, sans-serif;&quot;&gt;&lt;strong&gt;Số lượng:&lt;/strong&gt;&amp;nbsp;02 người&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-family: tahoma, geneva, sans-serif;&quot;&gt;&lt;strong&gt;Thời gian l&amp;agrave;m việc:&lt;/strong&gt;&amp;nbsp;Giờ h&amp;agrave;nh ch&amp;iacute;nh.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-family: tahoma, geneva, sans-serif;&quot;&gt;&lt;strong&gt;Địa điểm l&amp;agrave;m việc:&lt;/strong&gt;&amp;nbsp;L&amp;agrave;m việc online, Họp triển khai c&amp;ocirc;ng việc tại Q1 hoặc Quận T&amp;acirc;n Ph&amp;uacute;, TP.Hồ Ch&amp;iacute; M