INSERT INTO `media` VALUES (99, 'default13521812751', '', 'media_information', '[67]', 'admin', 'Elife Smartweb', '', '', 'vn', '', '&lt;h1 style=&quot;text-align: justify; &quot;&gt;\r\n	TIẾM KIỆM TỐI ĐA CHI PH&amp;Iacute; - NHƯNG KH&amp;Ocirc;NG RẬP KHU&amp;Ocirc;N GIAO DIỆN WEB&lt;/h1&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;span style=&quot;font-family: tahoma, geneva, sans-serif; &quot;&gt;Elife SmartWeb l&amp;agrave; bộ sản phẩm c&amp;ocirc;ng nghệ thiết kế web của Elife Partners bao gồm từ quy tr&amp;igrave;nh, c&amp;ocirc;ng nghệ, ti&amp;ecirc;u chuẩn thiết kế, đến sản phẩm quản trị nội dung &amp;amp; đo lường hiệu quả hoạt động website.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;span style=&quot;font-family: tahoma, geneva, sans-serif; &quot;&gt;Elife SmartWeb quan t&amp;acirc;m đến một website ho&amp;agrave;n hảo, từ thẩm mỹ thiết kế đến bố cục tr&amp;igrave;nh b&amp;agrave;y nội dung, h&amp;igrave;nh ảnh. Đem lại một website thật sự chuy&amp;ecirc;n nghiệp &amp;amp; đẳng cấp.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;strong style=&quot;font-family: tahoma, geneva, sans-serif; &quot;&gt;Ưu điểm Elife SmartWeb:&lt;/strong&gt;&lt;/p&gt;\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		&lt;span style=&quot;font-family:tahoma,geneva,sans-serif;&quot;&gt;Quy tr&amp;igrave;nh thiết kế chuy&amp;ecirc;n nghiệp, theo ti&amp;ecirc;u chuẩn nước ngo&amp;agrave;i&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		&lt;span style=&quot;font-family:tahoma,geneva,sans-serif;&quot;&gt;Chi ph&amp;iacute; x&amp;acirc;y dựng thấp&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		&lt;span style=&quot;font-family:tahoma,geneva,sans-serif;&quot;&gt;Thời gian x&amp;acirc;y dựng web nhanh ch&amp;oacute;ng&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		&lt;span style=&quot;font-family:tahoma,geneva,sans-serif;&quot;&gt;C&amp;ocirc;ng nghệ lu&amp;ocirc;n cập nhật, đem lại một website chuy&amp;ecirc;n nghiệp&lt;/span&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		&lt;span style=&quot;font-family:tahoma,geneva,sans-serif;&quot;&gt;Elife Partners chịu tr&amp;aacute;ch nhiệm mọi sự cố xảy ra cho website từ m&amp;atilde; nguồn đến m&amp;aacute;y chủ, cơ sở dữ liệu.&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;strong&gt;1.&lt;/strong&gt;&amp;nbsp;&lt;strong style=&quot;font-family: tahoma, geneva, sans-serif; &quot;&gt;TI&amp;Ecirc;U CHUẨN THIẾT KẾ ELIFE SMARTWEB&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;span style=&quot;font-family:tahoma,geneva,sans-serif;&quot;&gt;V&amp;igrave; Elife SmartWeb l&amp;agrave; một bộ quy tr&amp;igrave;nh c&amp;ocirc;ng nghệ thiết kế được &amp;aacute;p dụng theo ti&amp;ecirc;u chuẩn nhất định nhằm đảm bảo cho chất lượng sản phẩm của Elife Partners, đội ngũ thiết kế vi&amp;ecirc;n ch&amp;uacute;ng t&amp;ocirc;i kh&amp;ocirc;ng ho&amp;agrave;n to&amp;agrave;n l&amp;agrave;m việc 100% theo y&amp;ecirc;u cầu kh&amp;aacute;ch h&amp;agrave;ng, m&amp;agrave; c&amp;ugrave;ng kh&amp;aacute;ch h&amp;agrave;ng thảo luận v&amp;agrave; t&amp;igrave;m ra giải ph&amp;aacute;p tốt nhất tr&amp;ecirc;n cơ sở những ti&amp;ecirc;u chuẩn sau:&lt;/span&gt;&lt;/p&gt;\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align: justify; &quot;&gt;\r\n		&lt;span style=&quot;font-family:tahoma,geneva,sans-serif;&quot;&gt;&lt;strong&gt;Hạn chế hoặc kh&amp;ocirc;ng sử dụng Flash&lt;/strong&gt;&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p style=&quot;text-align: justify; &quot;&gt;\r\n	&lt;span style=&quot;font-family:tahoma,geneva,sans-serif;&quot;&gt;V&amp;igrave; một số thiết bị duyệt web như Ipad v&amp;agrave; Smart Phone kh&amp;ocirc;ng 