INSERT INTO `product_description` VALUES ('vn', 33, '', 'Darkest.Hour.2011', '&lt;p&gt;\r\n	&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma, Geneva, sans-serif; font-size: 11px; text-align: justify; &quot;&gt;Một nh&amp;oacute;m bạn 5 người bị mắc kẹt ở Moscow, chiến đấu để tồn tại trong cuộc truy qu&amp;eacute;t của người ngo&amp;agrave;i h&amp;agrave;nh tinh. C&amp;aacute;c bạn trẻ n&amp;agrave;y phải l&amp;agrave;m c&amp;aacute;ch n&amp;agrave;o để sống s&amp;oacute;t khi những con qu&amp;aacute;i vật n&amp;agrave;y tấn c&amp;ocirc;ng th&amp;agrave;nh phố v&amp;agrave; hủy diệt tất cả mọi sinh vật sống chỉ trong v&amp;ograve;ng v&amp;agrave;i gi&amp;acirc;y...&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;iframe allowfullscreen=&quot;&quot; frameborder=&quot;0&quot; height=&quot;315&quot; src=&quot;http://www.youtube.com/embed/wR9_oRx8h-Q&quot; width=&quot;560&quot;&gt;&lt;/iframe&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;background-color:#fff;&quot;&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: Tahoma, Geneva, sans-serif; font-size: 11px; text-align: justify; &quot;&gt;Kh&amp;ocirc;ng những vậy, ngo&amp;agrave;i khả năng h&amp;uacute;t năng lượng từ tr&amp;aacute;i đất, ch&amp;uacute;ng lại c&amp;ograve;n v&amp;ocirc; h&amp;igrave;nh&lt;/span&gt;&lt;br /&gt;\r\n	&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-align: center; &quot;&gt;\r\n	&lt;img src=&quot;http://img.vuaphim.net/info/12-11/gio-den-toi---the-darkest-hour-edit-1976422368-1.png&quot; style=&quot;border: 0px; font-family: Tahoma, Geneva, sans-serif; font-size: 11px; text-align: center; background-color: rgb(15, 15, 15); color: rgb(85, 85, 85); &quot; /&gt;&lt;br style=&quot;font-family: Tahoma, Geneva, sans-serif; font-size: 11px; text-align: center; background-color: rgb(15, 15, 15); color: rgb(85, 85, 85); &quot; /&gt;\r\n	&lt;br style=&quot;font-family: Tahoma, Geneva, sans-serif; font-size: 11px; text-align: center; background-color: rgb(15, 15, 15); color: rgb(85, 85, 85); &quot; /&gt;\r\n	&lt;img src=&quot;http://img.vuaphim.net/info/12-11/gio-den-toi---the-darkest-hour-edit-1976422368-2.png&quot; style=&quot;border: 0px; font-family: Tahoma, Geneva, sans-serif; font-size: 11px; text-align: center; background-color: rgb(15, 15, 15); color: rgb(85, 85, 85); &quot; /&gt;&lt;br style=&quot;font-family: Tahoma, Geneva, sans-serif; font-size: 11px; text-align: center; background-color: rgb(15, 15, 15); color: rgb(85, 85, 85); &quot; /&gt;\r\n	&lt;br style=&quot;font-family: Tahoma, Geneva, sans-serif; font-size: 11px; text-align: center; background-color: rgb(15, 15, 15); color: rgb(85, 85, 85); &quot; /&gt;\r\n	&lt;img src=&quot;http://img.vuaphim.net/info/12-11/gio-den-toi---the-darkest-hour-edit-1976422368-3.png&quot; style=&quot;border: 0px; font-family: Tahoma, Geneva, sans-serif; font-size: 11px; text-align: center; background-color: rgb(15, 15, 15); color: rgb(85, 85, 85); &quot; /&gt;&lt;/p&gt;', '', '', '');
